<body>
    <div class="main-container">
        <!-- Control Panel Section -->
        <div class="control-panel">
            <div class="parameter-container">
                <label for="slider-alpha">parameter αlabel>
                <input type="range" id="slider-alpha" min="0.1" max="5.0" step="0.005" value="1.825">
                <div class="parameter-value" id="display-alpha">1.825div>
            </div>
            <div class="parameter-container">
                <label for="slider-beta">parameter βlabel>
                <input type="range" id="slider-beta" min="0.1" max="5.0" step="0.005" value="1.945">
                <div class="parameter-value" id="display-beta">1.945div>
            </div>
        </div>

        <!-- Visualization Section -->
        <div class="visualization-section">
            <div class="plot-title">
                <span class="blue-text">density functionspan>
                <span> and </span>
                <span class="orange-text">distribution functionspan>
            </div>
            <div id="plot-container"></div>
        </div>

        <!-- Statistics Display Section -->
        <div class="statistics-section">
            <p>Mean: <span id="mean-value" class="stat-value">3.550span></p>
            <p>Variance: <span id="variance-value" class="stat-value">6.904span></p>
            <div id="mgf-formula">$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - 1.945 t)^{1.825}} $$div>
        </div>
    </div>

    <script>
        // Lanczos approximation for Gamma function
        function gamma(z) {
            // Coefficients for Lanczos approximation
            const g = 7;
            const p = [
                0.99999999999980993,
                676.5203681218851,
                -1259.1392167224028,
                771.32342877765313,
                -176.61502916214059,
                12.507343278686905,
                -0.13857109526572012,
                9.9843695780195716e-6,
                1.5056327351493116e-7
            ];

            if (z < 0.5) {
                return Math.PI / (Math.sin(Math.PI * z) * gamma(1 - z));
            }

            z -= 1;
            let x = p[0];
            for (let i = 1; i < p.length; i++) {
                x += p[i] / (z + i);
            }

            const sqrt2pi = Math.sqrt(2 * Math.PI);
            const t = z + g + 0.5;
            return sqrt2pi * Math.pow(t, z + 0.5) * Math.exp(-t) * x;
        }

        // Gamma PDF function
        function gammaPDF(x, alpha, beta) {
            if (x <= 0) return 0;
            return (Math.pow(x, alpha - 1) * Math.exp(-x / beta)) / (Math.pow(beta, alpha) * gamma(alpha));
        }

        // Numerical integration for CDF using trapezoidal rule
        function calculateCDF(xValues, pdfValues) {
            const cdfValues = new Array(xValues.length);
            cdfValues[0] = 0;

            for (let i = 1; i < xValues.length; i++) {
                const dx = xValues[i] - xValues[i - 1];
                const avgPDF = (pdfValues[i] + pdfValues[i - 1]) / 2;
                cdfValues[i] = cdfValues[i - 1] + avgPDF * dx;
            }

            return cdfValues;
        }

        // Initialize variables
        let currentAlpha = 1.825;
        let currentBeta = 1.945;

        // Get DOM elements
        const sliderAlpha = document.getElementById('slider-alpha');
        const sliderBeta = document.getElementById('slider-beta');
        const displayAlpha = document.getElementById('display-alpha');
        const displayBeta = document.getElementById('display-beta');
        const meanValue = document.getElementById('mean-value');
        const varianceValue = document.getElementById('variance-value');
        const mgfFormula = document.getElementById('mgf-formula');

        // Generate initial data and plot
        function updateVisualization() {
            // Get current values
            currentAlpha = parseFloat(sliderAlpha.value);
            currentBeta = parseFloat(sliderBeta.value);

            // Update displays
            displayAlpha.textContent = currentAlpha.toFixed(3);
            displayBeta.textContent = currentBeta.toFixed(3);

            // Calculate statistics
            const mean = currentAlpha * currentBeta;
            const variance = currentAlpha * currentBeta * currentBeta;

            // Update statistics display
            meanValue.textContent = mean.toFixed(3);
            varianceValue.textContent = variance.toFixed(3);

            // Update MGF formula
            mgfFormula.innerHTML = `$$ (1 - \\beta t)^{-\\alpha} = \\frac{1}{(1 - ${currentBeta.toFixed(3)} t)^{${currentAlpha.toFixed(3)}}} $$`;

            // Generate x values
            const xValues = [];
            const pdfValues = [];
            const step = 0.1;
            for (let x = 0; x <= 25; x += step) {
                xValues.push(x);
                pdfValues.push(gammaPDF(x, currentAlpha, currentBeta));
            }

            // Calculate CDF
            const cdfValues = calculateCDF(xValues, pdfValues);

            // Calculate mean line height
            const meanLineHeight = gammaPDF(mean, currentAlpha, currentBeta);

            // Create plot data
            const data = [
                {
                    type: 'scatter',
                    mode: 'lines',
                    x: xValues,
                    y: pdfValues,
                    line: { color: 'blue', width: 2.5 },
                    name: 'PDF'
                },
                {
                    type: 'scatter',
                    mode: 'lines',
                    x: xValues,
                    y: cdfValues,
                    line: { color: 'orange', width: 2.5 },
                    name: 'CDF'
                }
            ];

            // Create layout
            const layout = {
                xaxis: {
                    range: [0, 25],
                    title: ''
                },
                yaxis: {
                    range: [0, 1.1],
                    title: ''
                },
                showlegend: false,
                margin: { l: 50, r: 50, t: 20, b: 40 },
                shapes: [
                    {
                        type: 'line',
                        x0: mean,
                        x1: mean,
                        y0: 0,
                        y1: meanLineHeight,
                        line: {
                            color: 'red',
                            width: 3
                        }
                    }
                ]
            };

            // Update plot
            Plotly.newPlot('plot-container', data, layout, { responsive: true });

            // Re-render MathJax
            MathJax.typesetPromise([mgfFormula]).then(() => {
                console.log('MathJax rendered');
            }).catch((err) => console.log(err));
        }

        // Add event listeners
        sliderAlpha.addEventListener('input', updateVisualization);
        sliderBeta.addEventListener('input', updateVisualization);

        // Initial render
        updateVisualization();
    </script>
</body>
