<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Interactive Ternary Plot</title>
    <style>
        body {
            margin: 0;
            padding: 20px;
            background-color: #f0f0f0;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            font-family: Arial, sans-serif;
        }

        #canvas-container {
            border: 2px solid #333;
            background-color: white;
            box-shadow: 0 4px 8px rgba(0,0,0,0.1);
        }
    </style>
</head>
<body>
    <div id="canvas-container">
        <canvas id="visualization-canvas"></canvas>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script>
        let locator;
        let isDragging = false;
        const margin = 60;
        const canvasSize = 650;
        const plotSize = canvasSize - 2 * margin;

        // Azeotrope data
        const azeotropes = {
            A1: { x: 0.655, y: 0 },
            A2: { x: 0, y: 0.795 },
            A3: { x: 0.605, y: 0.345 },
            A4: { x: 0.26, y: 0.335 }
        };

        // Distillation boundary path (for locator constraint)
        const boundaryPath = [
            azeotropes.A2,
            azeotropes.A4,
            azeotropes.A1
        ];

        function setup() {
            const canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');

            // Initialize locator
            locator = {
                x: 0.2,
                y: 0.22
            };
        }

        function draw() {
            background(255);

            // Draw the triangular coordinate system
            drawCoordinateSystem();

            // Draw azeotropes
            drawAzeotropes();

            // Calculate and draw distillation line
            drawDistillationLine();

            // Draw locator
            drawLocator();
        }

        function dataToPixel(dataX, dataY) {
            // Map data coordinates (0-1) to pixel coordinates
            const pixelX = margin + dataX * plotSize;
            const pixelY = canvasSize - margin - dataY * plotSize;
            return { x: pixelX, y: pixelY };
        }

        function pixelToData(pixelX, pixelY) {
            const dataX = (pixelX - margin) / plotSize;
            const dataY = (canvasSize - margin - pixelY) / plotSize;
            return { x: dataX, y: dataY };
        }

        function drawCoordinateSystem() {
            stroke(0);
            strokeWeight(2);

            // X axis (bottom)
            line(margin, height - margin, width - margin, height - margin);

            // Y axis (left)
            line(margin, margin, margin, height - margin);

            // Hypotenuse
            line(margin, height - margin, width - margin, margin);

            // Draw ticks and labels
            textAlign(CENTER, CENTER);
            textSize(12);

            // X axis ticks and labels
            for (let i = 0; i <= 10; i++) {
                const tickX = margin + (i / 10) * plotSize;
                const tickY = height - margin;

                stroke(0);
                strokeWeight(1);
                line(tickX, tickY, tickX, tickY + 5);

                noStroke();
                fill(0);
                text((i / 10).toFixed(1), tickX, tickY + 20);
            }

            // Y axis ticks and labels
            for (let i = 0; i <= 10; i++) {
                const tickX = margin;
                const tickY = height - margin - (i / 10) * plotSize;

                stroke(0);
                strokeWeight(1);
                line(tickX - 5, tickY, tickX, tickY);

                noStroke();
                fill(0);
                text((i / 10).toFixed(1), tickX - 20, tickY);
            }

            // Component labels
            textSize(16);
            fill(255, 0, 0);

            // Methanol (bottom-left)
            text("methanol", margin + 30, height - margin + 30);

            // Chloroformbottom-right)
            text("chloroform", width - margin - 30, height - margin + 30);

            // Acetone (top-left)
            text("acetone", margin - 30, margin + 30);
        }

        function drawAzeotropes() {
            textSize(14);

            // Draw each azeotrope
            for (let key in azeotropes) {
                const az = azeotropes[key];
                const pixel = dataToPixel(az.x, az.y);

                // Draw point
                fill(153, 0, 0);
                noStroke();
                circle(pixel.x, pixel.y, 12);

                // Draw label
                fill(0);
                text(key, pixel.x, pixel.y - 15);
            }
        }

        function drawLocator() {
            const pixel = dataToPixel(locator.x, locator.y);

            // Draw locator circle
            stroke(0);
            strokeWeight(2);
            fill(255);
            circle(pixel.x, pixel.y, 16);
        }

        function getVector(x, y) {
            const A3 = {x: 0.605, y: 0.345}; // Unstable node (repeller)
            const M = {x: 0, y: 0};         // Stable node (attractor)

            // Vector from repeller to current point
            let v_repel_x = x - A3.x;
            let v_repel_y = y - A3.y;
            // Vector from current point to attractor
            let v_attract_x = M.x - x;
            let v_attract_y = M.y - y;

            // Normalize influence by distance squared, add epsilon to avoid division by zero
            let dist_repel_sq = v_repel_x * v_repel_x + v_repel_y * v_repel_y + 0.01;
            let dist_attract_sq = v_attract_x * v_attract_x + v_attract_y * v_attract_y + 0.01;

            // Weighted sum of influences
            let dx = (v_repel_x / dist_repel_sq) * 0.05 + (v_attract_x / dist_attract_sq) * 0.03;
            let dy = (v_repel_y / dist_repel_sq) * 0.05 + (v_attract_y / dist_attract_sq) * 0.03;

            // Add a small rotational field to shape the curves
            dx += -0.1 * y;
            dy += 0.1 * x;

            return { dx, dy };
        }

        function drawDistillationLine() {
            const points = [];
            const stepSize = 0.005;
            const maxSteps = 250;

            // Forward integration
            let currentX = locator.x;
            let currentY = locator.y;

            for (let i = 0; i < maxSteps; i++) {
                points.push({ x: currentX, y: currentY });

                const vector = getVector(currentX, currentY);
                currentX += vector.dx * stepSize;
                currentY += vector.dy * stepSize;

                // Check if point is outside triangle
                if (currentX < 0 || currentY < 0 || currentX + currentY > 1) {
                    break;
                }
            }

            // Backward integration
            currentX = locator.x;
            currentY = locator.y;

            for (let i = 0; i < maxSteps; i++) {
                // Insert at beginning for correct order
                points.unshift({ x: currentX, y: currentY });

                const vector = getVector(currentX, currentY);
                currentX -= vector.dx * stepSize;
                currentY -= vector.dy * stepSize;

                // Check if point is outside triangle
                if (currentX < 0 || currentY < 0 || currentX + currentY > 1) {
                    break;
                }
            }

            // Draw the distillation line
            if (points.length > 1) {
                stroke(0, 0, 255);
                strokeWeight(2);
                noFill();

                beginShape();
                for (let i = 0; i < points.length; i++) {
                    const pixel = dataToPixel(points[i].x, points[i].y);
                    curveVertex(pixel.x, pixel.y);
                }
                endShape();
            }
        }

        function mousePressed() {
            const locatorPixel = dataToPixel(locator.x, locator.y);
            const distance = dist(mouseX, mouseY, locatorPixel.x, locatorPixel.y);

            if (distance < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (!isDragging) return;

            const data = pixelToData(mouseX, mouseY);

            // Find closest point on boundary path
            let closestPoint = { x: locator.x, y: locator.y };
            let minDistance = Infinity;

            // Check each segment of the boundary path
            for (let i = 0; i < boundaryPath.length - 1; i++) {
                const p1 = boundaryPath[i];
                const p2 = boundaryPath[i + 1];

                // Project point onto line segment
                const projected = projectPointToSegment(data, p1, p2);
                const distance = dist(data.x, data.y, projected.x, projected.y);

                if (distance < minDistance) {
                    minDistance = distance;
                    closestPoint = projected;
                }
            }

            locator.x = closestPoint.x;
            locator.y = closestPoint.y;
        }

        function mouseReleased() {
            isDragging = false;
        }

        function projectPointToSegment(p, p1, p2) {
            // Vector from p1 to p2
            const v = { x: p2.x - p1.x, y: p2.y - p1.y };

            // Vector from p1 to p
            const w = { x: p.x - p1.x, y: p.y - p1.y };

            // Calculate projection parameter
            const c1 = dotProduct(w, v);
            const c2 = dotProduct(v, v);
            let t = c1 / c2;

            // Clamp to segment
            t = constrain(t, 0, 1);

            // Return projected point
            return {
                x: p1.x + t * v.x,
                y: p1.y + t * v.y
            };
        }

        function dotProduct(a, b) {
            return a.x * b.x + a.y * b.y;
        }
    </script>
</body>
</html>
