        </div>
        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Scene setup
        let scene, camera, renderer, controls;
        let paraboloidSolid, paraboloidWireframe;
        let axes = [];

        const range = 2.0;
        const slices = 50;
        const stacks = 50;

        // Get slider values
        function getSliderValues() {
            return {
                a: parseFloat(document.getElementById('slider-a').value),
                b: parseFloat(document.getElementById('slider-b').value),
                c: parseFloat(document.getElementById('slider-c').value)
            };
        }

        // Initialize the scene
        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x000000);

            // Camera
            camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x404040);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
            directionalLight.position.set(5, 10, 5);
            scene.add(directionalLight);

            // Add coordinate axes
            addCoordinateAxes();

            // Create initial paraboloid
            createParaboloid();

            // Start animation
            animate();
        }

        // Add coordinate axes
        function addCoordinateAxes() {
            const axisLength = 5;
            const axisMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });

            // X-axis
            const xGeometry = new THREE.BufferGeometry();
            const xPositions = new Float32Array([-axisLength, 0, 0, axisLength, 0, 0]);
            xGeometry.setAttribute('position', new THREE.BufferAttribute(xPositions, 3));
            const xAxis = new THREE.Line(xGeometry, axisMaterial);
            scene.add(xAxis);
            axes.push(xAxis);

            // Y-axis
            const yGeometry = new THREE.BufferGeometry();
            const yPositions = new Float32Array([0, -axisLength, 0, 0, axisLength, 0]);
            yGeometry.setAttribute('position', new THREE.BufferAttribute(yPositions, 3));
            const yAxis = new THREE.Line(yGeometry, axisMaterial);
            scene.add(yAxis);
            axes.push(yAxis);

            // Z-axis
            const zGeometry = new THREE.BufferGeometry();
            const zPositions = new Float32Array([0, 0, -axisLength, 0, 0, axisLength]);
            zGeometry.setAttribute('position', new THREE.BufferAttribute(zPositions, 3));
            const zAxis = new THREE.Line(zGeometry, axisMaterial);
            scene.add(zAxis);
            axes.push(zAxis);
        }

        // Create elliptic paraboloid
        function createParaboloid() {
            const { a, b, c } = getSliderValues();

            // Remove existing paraboloid
            if (paraboloidSolid) scene.remove(paraboloidSolid);
            if (paraboloidWireframe) scene.remove(paraboloidWireframe);

            // Parametric function
            const parametricFunction = function(u, v, target) {
                const phi = u * 2 * Math.PI;
                const r = v * range;

                const x = a * r * Math.cos(phi);
                const y = b * r * Math.sin(phi);
                const z = c * r * r;

                target.set(x, y, z);
            };

            // Create geometry
            const geometry = new THREE.ParametricGeometry(parametricFunction, slices, stacks);

            // Add vertex colors based on height
            const colors = [];
            const positions = geometry.attributes.position.array;
            const maxZ = c * range * range;

            for (let i = 0; i < positions.length; i += 3) {
                const z = positions[i + 2];
                const normalizedZ = Math.min(z / maxZ, 1);

                // Interpolate between yellow (#FFD700) and orange-red (#FF4500)
                const r = 1.0;
                const g = 0.84 - (0.84 - 0.27) * normalizedZ;
                const b = 0.0;

                colors.push(r, g, b);
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            // Create solid mesh
            const solidMaterial = new THREE.MeshPhongMaterial({
                side: THREE.DoubleSide,
                vertexColors: true,
                shading: THREE.SmoothShading
            });
            paraboloidSolid = new THREE.Mesh(geometry, solidMaterial);
            scene.add(paraboloidSolid);

            // Create wireframe mesh
            const wireframeMaterial = new THREE.MeshBasicMaterial({
                color: 0xcccccc,
                wireframe: true,
                transparent: true,
                opacity: 0.3
            });
            paraboloidWireframe = new THREE.Mesh(geometry, wireframeMaterial);
            scene.add(paraboloidWireframe);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        window.addEventListener('resize', function() {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        });

        // Synchronize sliders and inputs
        function syncControls() {
            // a slider and input
            const sliderA = document.getElementById('slider-a');
            const inputA = document.getElementById('input-a');

            sliderA.addEventListener('input', function() {
                inputA.value = this.value;
                createParaboloid();
            });

            inputA.addEventListener('input', function() {
                sliderA.value = this.value;
                createParaboloid();
            });

            // b slider and input
            const sliderB = document.getElementById('slider-b');
            const inputB = document.getElementById('input-b');

            sliderB.addEventListener('input', function() {
                inputB.value = this.value;
                createParaboloid();
            });

            inputB.addEventListener('input', function() {
                sliderB.value = this.value;
                createParaboloid();
            });

            // c slider and input
            const sliderC = document.getElementById('slider-c');
            const inputC = document.getElementById('input-c');

            sliderC.addEventListener('input', function() {
                inputC.value = this.value;
                createParaboloid();
            });

            inputC.addEventListener('input', function() {
                sliderC.value = this.value;
                createParaboloid();
            });
        }

        // Initialize everything
        init();
        syncControls();

        // Handle canvas container resize
        const resizeObserver = new ResizeObserver(() => {
            const container = document.getElementById('canvas-container');
            const width = container.clientWidth;
            const height = container.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        });

        resizeObserver.observe(document.getElementById('canvas-container'));
    </script>
</body>
</html>
