            <div class="legend-item">
                <span class="legend-arrow legend-j2">J₂ →</span>
                <span>Second angular momentum</span>
            </div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // State management
        const state = {
            j1: 1.5,
            m1: 0.5,
            j2: 1.0,
            m2: 0.0,
            j: 2.5,
            isAnimating: false,
            phi_z: 0,
            phi_j: 0
        };

        // Three.js variables
        let scene, camera, renderer, controls;
        let vectors = {};
        let cones = {};

        // Initialize the application
        function init() {
            setupScene();
            setupEventListeners();
            updateSelectors();
            updateVisualization();
            animate();
        }

        // Setup three.js scene
        function setupScene() {
            const container = document.getElementById('canvas-container');
            const canvas = document.getElementById('main-canvas');

            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            camera = new THREE.PerspectiveCamera(
                75,
                container.clientWidth / container.clientHeight,
                0.1,
                1000
            );
            camera.position.set(5, 5, 5);
            camera.up.set(0, 0, 1);

            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Add axes helper
            const axesHelper = new THREE.AxesHelper(3);
            scene.add(axesHelper);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // Setup event listeners
        function setupEventListeners() {
            // j1 selector
            document.getElementById('j1-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.j1 = parseFloat(e.target.dataset.value);
                    updateSelectors();
                    resetAnimation();
                    updateVisualization();
                }
            });

            // j2 selector
            document.getElementById('j2-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.j2 = parseFloat(e.target.dataset.value);
                    updateSelectors();
                    resetAnimation();
                    updateVisualization();
                }
            });

            // m1 selector
            document.getElementById('m1-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.m1 = parseFloat(e.target.dataset.value);
                    updateButtonStates('m1-selector');
                    resetAnimation();
                    updateVisualization();
                }
            });

            // m2 selector
            document.getElementById('m2-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.m2 = parseFloat(e.target.dataset.value);
                    updateButtonStates('m2-selector');
                    resetAnimation();
                    updateVisualization();
                }
            });

            // j selector
            document.getElementById('j-selector').addEventListener('click', (e) => {
                if (e.target.tagName === 'BUTTON') {
                    state.j = parseFloat(e.target.dataset.value);
                    updateButtonStates('j-selector');
                    resetAnimation();
                    updateVisualization();
                }
            });

            // Animation controls
            document.getElementById('btn-play-pause').addEventListener('click', toggleAnimation);
            document.getElementById('btn-reset').addEventListener('click', resetAnimation);
        }

        // Update selector buttons
        function updateSelectors() {
            updateMSelector('m1-selector', state.j1, state.m1);
            updateMSelector('m2-selector', state.j2, state.m2);
            updateJSelector();
            updateButtonStates('j1-selector');
            updateButtonStates('j2-selector');
        }

        function updateMSelector(selectorId, j, selectedValue) {
            const container = document.getElementById(selectorId);
            container.innerHTML = '';

            const mValues = [];
            for (let m = -j; m <= j; m += 1) {
                mValues.push(m);
            }

            mValues.forEach(m => {
                const button = document.createElement('button');
                button.dataset.value = m;
                button.textContent = m === 0 ? '0' : m.toString();
                container.appendChild(button);
            });

            // Select default value if needed
            if (!mValues.includes(selectedValue)) {
                selectedValue = mValues.find(m => m >= 0) || mValues[0];
                if (selectorId === 'm1-selector') state.m1 = selectedValue;
                if (selectorId === 'm2-selector') state.m2 = selectedValue;
            }

            updateButtonStates(selectorId);
        }

        function updateJSelector() {
            const container = document.getElementById('j-selector');
            container.innerHTML = '';

            const jMin = Math.abs(state.j1 - state.j2);
            const jMax = state.j1 + state.j2;
            const jValues = [];

            for (let j = jMin; j <= jMax; j += 1) {
                jValues.push(j);
            }

            jValues.forEach(j => {
                const button = document.createElement('button');
                button.dataset.value = j;
                button.textContent = j === 0.5 ? '1/2' : j === 1.5 ? '3/2' : j.toString();
                container.appendChild(button);
            });

            // Select default value if needed
            if (!jValues.includes(state.j)) {
                state.j = jMax;
            }

            updateButtonStates('j-selector');
        }

        function updateButtonStates(selectorId) {
            const container = document.getElementById(selectorId);
            const buttons = container.querySelectorAll('button');

            buttons.forEach(button => {
                const value = parseFloat(button.dataset.value);
                let isSelected = false;

                if (selectorId === 'j1-selector') isSelected = (value === state.j1);
                else if (selectorId === 'j2-selector') isSelected = (value === state.j2);
                else if (selectorId === 'm1-selector') isSelected = (value === state.m1);
                else if (selectorId === 'm2-selector') isSelected = (value === state.m2);
                else if (selectorId === 'j-selector') isSelected = (value === state.j);

                button.classList.toggle('selected', isSelected);
            });
        }

        // Calculate angular momentum magnitude
        function calculateL(j) {
            return Math.sqrt(j * (j + 1));
        }

        // Update visualization
        function updateVisualization() {
            clearVisualization();

            const L1 = calculateL(state.j1);
            const L2 = calculateL(state.j2);
            const LJ = calculateL(state.j);
            const m = state.m1 + state.m2;

            const isCoupled = state.j >= state.j1 && state.j >= state.j2;

            if (isCoupled) {
                createCoupledModel(L1, L2, LJ, m);
            } else {
                createUncoupledModel(L1, L2, m);
            }
        }

        function clearVisualization() {
            // Remove vectors
            Object.values(vectors).forEach(vector => {
                scene.remove(vector);
            });
            vectors = {};

            // Remove cones
            Object.values(cones).forEach(cone => {
                scene.remove(cone);
            });
            cones = {};
        }

        function createCoupledModel(L1, L2, LJ, m) {
            // Create J vector (black)
            const thetaJ = Math.acos(m / LJ);
            const jDirection = new THREE.Vector3(
                Math.sin(thetaJ) * Math.cos(state.phi_z),
                Math.sin(thetaJ) * Math.sin(state.phi_z),
                Math.cos(thetaJ)
            );
            vectors.J = createArrowHelper(jDirection, LJ, 0x000000);
            scene.add(vectors.J);

            // Create J cone (around Z-axis)
            cones.J = createCone(Math.PI/2 - thetaJ, LJ, 0x000000, 0.1);
            scene.add(cones.J);

            // Calculate J1 and J2 directions using law of cosines
            const cos_alpha = (LJ * LJ + L1 * L1 - L2 * L2) / (2 * LJ * L1);
            const cos_beta = (LJ * LJ + L2 * L2 - L1 * L1) / (2 * LJ * L2);
            const alpha = Math.acos(Math.max(-1, Math.min(1, cos_alpha)));
            const beta = Math.acos(Math.max(-1, Math.min(1, cos_beta)));

            // Create J1 and J2 in local frame
            const j1Local = new THREE.Vector3(L1 * Math.sin(alpha), 0, L1 * Math.cos(alpha));
            const j2Local = new THREE.Vector3(-L2 * Math.sin(beta), 0, L2 * Math.cos(beta));

            // Transform to world frame
            const quaternion = new THREE.Quaternion();
            quaternion.setFromUnitVectors(new THREE.Vector3(0, 0, 1), jDirection);

            const j1World = j1Local.clone().applyQuaternion(quaternion);
            const j2World = j2Local.clone().applyQuaternion(quaternion);

            // Add rotation around J axis
            const jRotation = new THREE.Quaternion();
            jRotation.setFromAxisAngle(jDirection, state.phi_j);

            j1World.applyQuaternion(jRotation);
            j2World.applyQuaternion(jRotation);

            vectors.J1 = createArrowHelper(j1World, L1, 0xff0000);
            vectors.J2 = createArrowHelper(j2World, L2, 0x0000ff);
            scene.add(vectors.J1);
            scene.add(vectors.J2);

            // Create cones for J1 and J2 around J axis
            cones.J1 = createCone(alpha, L1, 0xff0000, 0.1);
            cones.J2 = createCone(beta, L2, 0x0000ff, 0.1);

            // Orient cones along J direction
            cones.J1.quaternion.copy(quaternion);
            cones.J2.quaternion.copy(quaternion);
            scene.add(cones.J1);
            scene.add(cones.J2);
        }

        function createUncoupledModel(L1, L2, m) {
            // J1 vector (red)
            const theta1 = Math.acos(state.m1 / L1);
            const j1Direction = new THREE.Vector3(
                Math.sin(theta1) * Math.cos(state.phi_z),
                Math.sin(theta1) * Math.sin(state.phi_z),
                Math.cos(theta1)
            );
            vectors.J1 = createArrowHelper(j1Direction, L1, 0xff0000);
            scene.add(vectors.J1);

            // J2 vector (blue)
            const theta2 = Math.acos(state.m2 / L2);
            const j2Direction = new THREE.Vector3(
                Math.sin(theta2) * Math.cos(state.phi_z),
                Math.sin(theta2) * Math.sin(state.phi_z),
                Math.cos(theta2)
            );
            vectors.J2 = createArrowHelper(j2Direction, L2, 0x0000ff);
            scene.add(vectors.J2);

            // Create cones around Z-axis
            cones.J1 = createCone(Math.PI/2 - theta1, L1, 0xff0000, 0.1);
            cones.J2 = createCone(Math.PI/2 - theta2, L2, 0x0000ff, 0.1);
            scene.add(cones.J1);
            scene.add(cones.J2);
        }

        function createArrowHelper(direction, length, color) {
            const arrow = new THREE.ArrowHelper(
                direction.clone().normalize(),
                new THREE.Vector3(0, 0, 0),
                length,
                color,
                length * 0.3,
                length * 0.2
            );
            return arrow;
        }

        function createCone(angle, height, color, opacity) {
            const radius = height * Math.tan(angle);
            const geometry = new THREE.ConeGeometry(radius, height, 32);
            const material = new THREE.MeshBasicMaterial({
                color: color,
                transparent: true,
                opacity: opacity,
                side: THREE.DoubleSide
            });
            const cone = new THREE.Mesh(geometry, material);
            cone.position.y = height / 2;
            return cone;
        }

        // Animation functions
        function toggleAnimation() {
            state.isAnimating = !state.isAnimating;
            const button = document.getElementById('btn-play-pause');
            button.textContent = state.isAnimating ? '❚❚' : '▶';
        }

        function resetAnimation() {
            state.isAnimating = false;
            state.phi_z = 0;
            state.phi_j = 0;
            document.getElementById('btn-play-pause').textContent = '▶';
            updateVisualization();
        }

        function animate() {
            requestAnimationFrame(animate);

            if (state.isAnimating) {
                state.phi_z += 0.02;
                state.phi_j += 0.03;
                updateVisualization();
            }

            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize when page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
