        let grid = [];
        let recursiveHistory = [];
        let iterativeHistory = [];
        let currentPattern = 'scattered';

        // Initialize grid based on pattern
        function initGrid(pattern) {
            grid = [];
            for (let i = 0; i < GRID_SIZE; i++) {
                grid[i] = [];
                for (let j = 0; j < GRID_SIZE; j++) {
                    grid[i][j] = EMPTY;
                }
            }

            // Use a fixed seed for consistent scattered pattern
            let seed = 42;
            function random() {
                seed = (seed * 9301 + 49297) % 233280;
                return seed / 233280;
            }

            switch (pattern) {
                case 'none':
                    break;
                case 'box':
                    for (let i = 0; i < GRID_SIZE; i++) {
                        grid[0][i] = OBSTACLE;
                        grid[GRID_SIZE - 1][i] = OBSTACLE;
                        grid[i][0] = OBSTACLE;
                        grid[i][GRID_SIZE - 1] = OBSTACLE;
                    }
                    break;
                case 'scattered':
                    for (let i = 0; i < GRID_SIZE; i++) {
                        for (let j = 0; j < GRID_SIZE; j++) {
                            if (i === START_X && j === START_Y) continue;
                            if (random() < 0.25) {
                                grid[i][j] = OBSTACLE;
                            }
                        }
                    }
                    break;
            }
        }

        // Recursive flood fill (DFS)
        function floodFillRecursive(x, y, visited) {
            if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) return;
            if (grid[x][y] === OBSTACLE || visited[x][y]) return;

            visited[x][y] = true;
            recursiveHistory.push({x, y});

            // Fixed order: North, West, South, East
            floodFillRecursive(x, y - 1, visited);
            floodFillRecursive(x - 1, y, visited);
            floodFillRecursive(x, y + 1, visited);
            floodFillRecursive(x + 1, y, visited);
        }

        // Iterative flood fill (BFS)
        function floodFillIterative() {
            const queue = [{x: START_X, y: START_Y}];
            const visited = [];

            for (let i = 0; i < GRID_SIZE; i++) {
                visited[i] = new Array(GRID_SIZE).fill(false);
            }

            while (queue.length > 0) {
                const {x, y} = queue.shift();

                if (x < 0 || x >= GRID_SIZE || y < 0 || y >= GRID_SIZE) continue;
                if (grid[x][y] === OBSTACLE || visited[x][y]) continue;

                visited[x][y] = true;
                iterativeHistory.push({x, y});

                // Add neighbors to queue
                queue.push({x, y - 1}, {x - 1, y}, {x, y + 1}, {x + 1, y});
            }
        }

        // Reset and recalculate for new pattern
        function resetSimulation(pattern) {
            currentPattern = pattern;
            initGrid(pattern);

            recursiveHistory = [];
            iterativeHistory = [];

            // Run recursive algorithm
            const visitedRec = [];
            for (let i = 0; i < GRID_SIZE; i++) {
                visitedRec[i] = new Array(GRID_SIZE).fill(false);
            }
            floodFillRecursive(START_X, START_Y, visitedRec);

            // Run iterative algorithm
            floodFillIterative();

            // Update slider
            const maxSteps = Math.max(recursiveHistory.length, iterativeHistory.length);
            const slider = document.getElementById('steps-slider');
            slider.max = maxSteps;
            slider.value = 0;
            document.getElementById('steps-value').textContent = '+ 0';

            // Update canvas displays
            recursiveSketch.drawGrid(0);
            iterativeSketch.drawGrid(0);
        }

        // P5.js sketch for recursive visualization
        const recursiveSketch = new p5(p => {
            p.setup = function() {
                const canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                canvas.parent('canvas-recursive');
            };

            p.drawGrid = function(steps) {
                p.background(255);
                p.stroke(0);
                p.strokeWeight(1);

                // Draw grid
                for (let i = 0; i <= GRID_SIZE; i++) {
                    p.line(i * CELL_SIZE, 0, i * CELL_SIZE, CANVAS_SIZE);
                    p.line(0, i * CELL_SIZE, CANVAS_SIZE, i * CELL_SIZE);
                }

                // Draw obstacles
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        if (grid[i][j] === OBSTACLE) {
                            p.fill(0);
                            p.noStroke();
                            p.rect(i * CELL_SIZE + 1, j * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                        }
                    }
                }

                // Draw filled cells
                p.fill(0, 0, 255);
                p.noStroke();
                for (let i = 0; i < steps && i < recursiveHistory.length; i++) {
                    const {x, y} = recursiveHistory[i];
                    p.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                }
            };
        });

        // P5.js sketch for iterative visualization
        const iterativeSketch = new p5(p => {
            p.setup = function() {
                const canvas = p.createCanvas(CANVAS_SIZE, CANVAS_SIZE);
                canvas.parent('canvas-iterative');
            };

            p.drawGrid = function(steps) {
                p.background(255);
                p.stroke(0);
                p.strokeWeight(1);

                // Draw grid
                for (let i = 0; i <= GRID_SIZE; i++) {
                    p.line(i * CELL_SIZE, 0, i * CELL_SIZE, CANVAS_SIZE);
                    p.line(0, i * CELL_SIZE, CANVAS_SIZE, i * CELL_SIZE);
                }

                // Draw obstacles
                for (let i = 0; i < GRID_SIZE; i++) {
                    for (let j = 0; j < GRID_SIZE; j++) {
                        if (grid[i][j] === OBSTACLE) {
                            p.fill(0);
                            p.noStroke();
                            p.rect(i * CELL_SIZE + 1, j * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                        }
                    }
                }

                // Draw filled cells
                p.fill(0, 0, 255);
                p.noStroke();
                for (let i = 0; i < steps && i < iterativeHistory.length; i++) {
                    const {x, y} = iterativeHistory[i];
                    p.rect(x * CELL_SIZE + 1, y * CELL_SIZE + 1, CELL_SIZE - 1, CELL_SIZE - 1);
                }
            };
        });

        // Event listeners
        document.addEventListener('DOMContentLoaded', function() {
            const slider = document.getElementById('steps-slider');
            const stepsValue = document.getElementById('steps-value');
            const buttons = document.querySelectorAll('button');

            // Initialize with scattered pattern
            resetSimulation('scattered');
            slider.value = 61;
            stepsValue.textContent = '+ 61';
            recursiveSketch.drawGrid(61);
            iterativeSketch.drawGrid(61);

            // Slider event
            slider.addEventListener('input', function() {
                const value = parseInt(this.value);
                stepsValue.textContent = '+ ' + value;
                recursiveSketch.drawGrid(value);
                iterativeSketch.drawGrid(value);
            });

            // Button events
            document.getElementById('btn-none').addEventListener('click', function() {
                buttons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                resetSimulation('none');
            });

            document.getElementById('btn-box').addEventListener('click', function() {
                buttons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                resetSimulation('box');
            });

            document.getElementById('btn-scattered').addEventListener('click', function() {
                buttons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                resetSimulation('scattered');
            });
        });
    </script>
</body>
