      const y0 = 10; // initial height (m)
      const vy0 = 0; // initial vertical velocity (m/s)
      const x0 = 0; // initial horizontal position (m)

      // Arrays to store trajectory points
      const pathPoints = [];
      const strobePoints = [];

      // Simulation variables
      let currentTime = 0;
      let currentX = x0;
      let currentY = y0;
      let currentVy = vy0;

      // Bounce-by-bounce simulation
      while (currentTime < totalTime) {
        // Calculate velocity just before hitting ground and time of flight
        const vyImpact = Math.sqrt(2 * g * currentY + currentVy * currentVy);
        const tFlight = (vyImpact + currentVy) / g;

        // Check if this bounce would exceed total time
        if (currentTime + tFlight > totalTime) {
          tFlight = totalTime - currentTime;
        }

        // Generate continuous path points for this arc
        const steps = 100;
        for (let i = 0; i <= steps; i++) {
          const t = (i / steps) * tFlight;
          const x = currentX + vx * t;
          const y = currentY + currentVy * t - 0.5 * g * t * t;
          if (y >= 0) { // Only add points above ground
            pathPoints.push({x: x, y: y});
          }
        }

        // Generate stroboscopic points for this arc
        let lastStrobeTime = currentTime - (currentTime % dt);
        if (lastStrobeTime < currentTime) {
          lastStrobeTime += dt;
        }

        for (let strobeTime = lastStrobeTime; strobeTime <= currentTime + tFlight && strobeTime <= totalTime; strobeTime += dt) {
          const t = strobeTime - currentTime;
          const x = currentX + vx * t;
          const y = currentY + currentVy * t - 0.5 * g * t * t;
          if (y >= 0) {
            strobePoints.push({x: x, y: y});
          }
        }

        // Update state for next bounce
        currentTime += tFlight;
        currentX += vx * tFlight;
        if (currentTime < totalTime) { // Only bounce if we haven't reached total time
          const vyNew = Math.sqrt(k) * vyImpact;
          currentY = 0;
          currentVy = vyNew;
        }
      }

      // Clear canvas with black background
      background(0, 0, 0);

      // Calculate scaling factors
      const padding = 40;
      const maxX = Math.max(...pathPoints.map(p => p.x), 1);
      const maxY = y0;

      const scaleX = (width - 2 * padding) / maxX;
      const scaleY = (height - 2 * padding) / maxY;
      const scale = Math.min(scaleX, scaleY);

      // Save current transformation state
      push();

      // Transform to Cartesian coordinates
      translate(padding, height - padding);
      scale(1, -1);

      // Draw axes
      stroke(100, 100, 150);
      strokeWeight(2);

      // X-axis
      line(0, 0, maxX * scale, 0);
      // Y-axis
      line(0, 0, 0, maxY * scale);

      // Draw tick marks on axes
      strokeWeight(1);
      const tickSize = 5;

      // X-axis ticks
      for (let x = 0; x <= maxX; x += Math.ceil(maxX / 10)) {
        line(x * scale, -tickSize, x * scale, tickSize);
      }

      // Y-axis ticks
      for (let y = 0; y <= maxY; y += Math.ceil(maxY / 10)) {
        line(-tickSize, y * scale, tickSize, y * scale);
      }

      // Restore transformation for text
      pop();

      // Draw axis labels
      push();
      translate(padding, height - padding);
      fill(255);
      noStroke();
      textAlign(CENTER, TOP);
      for (let x = 0; x <= maxX; x += Math.ceil(maxX / 10)) {
        text(x.toFixed(0), x * scale, 10);
      }

      textAlign(RIGHT, CENTER);
      for (let y = 0; y <= maxY; y += Math.ceil(maxY / 10)) {
        if (y > 0) {
          text(y.toFixed(0), -10, -y * scale);
        }
      }
      pop();

      // Transform back to Cartesian coordinates for drawing
      push();
      translate(padding, height - padding);
      scale(1, -1);

      // Draw trajectory path (red line)
      stroke(255, 0, 0);
      strokeWeight(2);
      noFill();
      beginShape();
      for (const point of pathPoints) {
        vertex(point.x * scale, point.y * scale);
      }
      endShape();

      // Draw stroboscopic points (yellow dots)
      noStroke();
      fill(255, 255, 0);
      for (const point of strobePoints) {
        ellipse(point.x * scale, point.y * scale, 8, 8);
      }

      // Restore transformation state
      pop();
    }
  </script>
</body>
</html>
