<body>
    <header>
        <h1>Angle Bisectors on the Circumcircleh1>
        <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>
    </header>

    <section id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </section>

    <script>
        let canvas;
        let centerX, centerY;
        let radius = 200;

        // Points on circumcircle
        let points = {
            A: { angle: 210 * PI / 180, x: 0, y: 0 },
            B: { angle: 330 * PI / 180, x: 0, y: 0 },
            C: { angle: 90 * PI / 180, x: 0, y: 0 }
        };

        // Calculated points
        let pointsPrime = {
            A: { angle: 0, x: 0, y: 0 },
            B: { angle: 0, x: 0, y: 0 },
            C: { angle: 0, x: 0, y: 0 }
        };

        let draggedPoint = null;
        let tolerance = 15;

        function setup() {
            canvas = createCanvas(500, 500);
            canvas.parent('p5-canvas');
            centerX = width / 2;
            centerY = height / 2;

            // Initialize point positions
            updatePointPositions();
            updateDerivedPoints();
        }

        function draw() {
            background(240);

            // Draw circumcircle
            noFill();
            strokeWeight(4);
            stroke(106, 90, 205); // SlateBlue
            circle(centerX, centerY, radius * 2);

            // Draw triangle ABC
            strokeWeight(4);
            stroke(106, 90, 205); // SlateBlue
            line(points.A.x, points.A.y, points.B.x, points.B.y);
            line(points.B.x, points.B.y, points.C.x, points.C.y);
            line(points.C.x, points.C.y, points.A.x, points.A.y);

            // Draw extended bisectors
            strokeWeight(4);
            stroke(255, 165, 0); // Orange
            line(points.A.x, points.A.y, pointsPrime.A.x, pointsPrime.A.y);
            line(points.B.x, points.B.y, pointsPrime.B.x, pointsPrime.B.y);
            line(points.C.x, points.C.y, pointsPrime.C.x, pointsPrime.C.y);

            // Draw triangle A'B'C'
            line(pointsPrime.A.x, pointsPrime.A.y, pointsPrime.B.x, pointsPrime.B.y);
            line(pointsPrime.B.x, pointsPrime.B.y, pointsPrime.C.x, pointsPrime.C.y);
            line(pointsPrime.C.x, pointsPrime.C.y, pointsPrime.A.x, pointsPrime.A.y);

            // Draw points A, B, C
            drawPoint(points.A.x, points.A.y);
            drawPoint(points.B.x, points.B.y);
            drawPoint(points.C.x, points.C.y);

            // Draw points A', B', C'
            fill(255, 165, 0); // Orange
            noStroke();
            circle(pointsPrime.A.x, pointsPrime.A.y, 12);
            circle(pointsPrime.B.x, pointsPrime.B.y, 12);
            circle(pointsPrime.C.x, pointsPrime.C.y, 12);
            stroke(0);
            strokeWeight(1);
            noFill();
            circle(pointsPrime.A.x, pointsPrime.A.y, 12);
            circle(pointsPrime.B.x, pointsPrime.B.y, 12);
            circle(pointsPrime.C.x, pointsPrime.C.y, 12);

            // Draw labels
            drawLabels();
        }

        function drawPoint(x, y) {
            // Outer circle
            fill(255, 165, 0); // Orange
            noStroke();
            circle(x, y, 12);

            // Outer stroke
            stroke(0);
            strokeWeight(1);
            noFill();
            circle(x, y, 12);

            // Crosshair
            stroke(0);
            strokeWeight(1);
            line(x - 6, y, x + 6, y);
            line(x, y - 6, x, y + 6);
        }

        function drawLabels() {
            fill(0);
            noStroke();
            textSize(16);
            textAlign(CENTER, CENTER);

            // Calculate radial offset for labels
            let labelOffset = 25;

            // Label A
            let angleA = atan2(points.A.y - centerY, points.A.x - centerX);
            let labelAX = points.A.x + cos(angleA) * labelOffset;
            let labelAY = points.A.y + sin(angleA) * labelOffset;
            text("A", labelAX, labelAY);

            // Label B
            let angleB = atan2(points.B.y - centerY, points.B.x - centerX);
            let labelBX = points.B.x + cos(angleB) * labelOffset;
            let labelBY = points.B.y + sin(angleB) * labelOffset;
            text("B", labelBX, labelBY);

            // Label C
            let angleC = atan2(points.C.y - centerY, points.C.x - centerX);
            let labelCX = points.C.x + cos(angleC) * labelOffset;
            let labelCY = points.C.y + sin(angleC) * labelOffset;
            text("C", labelCX, labelCY);

            // Label A'
            let angleAPrime = atan2(pointsPrime.A.y - centerY, pointsPrime.A.x - centerX);
            let labelAPrimeX = pointsPrime.A.x + cos(angleAPrime) * labelOffset;
            let labelAPrimeY = pointsPrime.A.y + sin(angleAPrime) * labelOffset;
            text("A'", labelAPrimeX, labelAPrimeY);

            // Label B'
            let angleBPrime = atan2(pointsPrime.B.y - centerY, pointsPrime.B.x - centerX);
            let labelBPrimeX = pointsPrime.B.x + cos(angleBPrime) * labelOffset;
            let labelBPrimeY = pointsPrime.B.y + sin(angleBPrime) * labelOffset;
            text("B'", labelBPrimeX, labelBPrimeY);

            // Label C'
            let angleCPrime = atan2(pointsPrime.C.y - centerY, pointsPrime.C.x - centerX);
            let labelCPrimeX = pointsPrime.C.x + cos(angleCPrime) * labelOffset;
            let labelCPrimeY = pointsPrime.C.y + sin(angleCPrime) * labelOffset;
            text("C'", labelCPrimeX, labelCPrimeY);
        }

        function updatePointPositions() {
            // Update A
            points.A.x = centerX + radius * cos(points.A.angle);
            points.A.y = centerY + radius * sin(points.A.angle);

            // Update B
            points.B.x = centerX + radius * cos(points.B.angle);
            points.B.y = centerY + radius * sin(points.B.angle);

            // Update C
            points.C.x = centerX + radius * cos(points.C.angle);
            points.C.y = centerY + radius * sin(points.C.angle);
        }

        function updateDerivedPoints() {
            // A' is midpoint of arc BC not containing A
            pointsPrime.A.angle = atan2(
                sin(points.B.angle) + sin(points.C.angle),
                cos(points.B.angle) + cos(points.C.angle)
            );
            pointsPrime.A.x = centerX + radius * cos(pointsPrime.A.angle);
            pointsPrime.A.y = centerY + radius * sin(pointsPrime.A.angle);

            // B' is midpoint of arc AC not containing B
            pointsPrime.B.angle = atan2(
                sin(points.A.angle) + sin(points.C.angle),
                cos(points.A.angle) + cos(points.C.angle)
            );
            pointsPrime.B.x = centerX + radius * cos(pointsPrime.B.angle);
            pointsPrime.B.y = centerY + radius * sin(pointsPrime.B.angle);

            // C' is midpoint of arc AB not containing C
            pointsPrime.C.angle = atan2(
                sin(points.A.angle) + sin(points.B.angle),
                cos(points.A.angle) + cos(points.B.angle)
            );
            pointsPrime.C.x = centerX + radius * cos(pointsPrime.C.angle);
            pointsPrime.C.y = centerY + radius * sin(pointsPrime.C.angle);
        }

        function mousePressed() {
            // Check if clicking near any point
            let mouseDistanceA = dist(mouseX, mouseY, points.A.x, points.A.y);
            let mouseDistanceB = dist(mouseX, mouseY, points.B.x, points.B.y);
            let mouseDistanceC = dist(mouseX, mouseY, points.C.x, points.C.y);

            if (mouseDistanceA < tolerance) {
                draggedPoint = 'A';
            } else if (mouseDistanceB < tolerance) {
                draggedPoint = 'B';
            } else if (mouseDistanceC < tolerance) {
                draggedPoint = 'C';
            }
        }

        function mouseDragged() {
            if (draggedPoint) {
                // Calculate angle from center to mouse
                let newAngle = atan2(mouseY - centerY, mouseX - centerX);

                // Update the dragged point's angle
                points[draggedPoint].angle = newAngle;

                // Update positions
                updatePointPositions();
                updateDerivedPoints();
            }
        }

        function mouseReleased() {
            draggedPoint = null;
        }
    </script>
