    <script>
        // State object
        const state = {
            t: 0.4,
            showPosition: false,
            showVelocity: false,
            showAcceleration: true,
            coordinateSystem: 'rectangular',
            showCircle: false
        };

        // Get DOM elements
        const sliderTime = document.getElementById('slider-time');
        const btnResetTime = document.getElementById('btn-reset-time');
        const togglePosition = document.querySelectorAll('input[name="position"]');
        const toggleVelocity = document.querySelectorAll('input[name="velocity"]');
        const toggleAcceleration = document.querySelectorAll('input[name="acceleration"]');
        const selectCoords = document.getElementById('select-coords');
        const toggleCircle = document.querySelectorAll('input[name="circle"]');

        // Event listeners
        sliderTime.addEventListener('input', (e) => {
            state.t = parseFloat(e.target.value);
        });

        btnResetTime.addEventListener('click', () => {
            sliderTime.value = 0.4;
            state.t = 0.4;
        });

        togglePosition.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showPosition = e.target.value === 'on';
            });
        });

        toggleVelocity.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showVelocity = e.target.value === 'on';
            });
        });

        toggleAcceleration.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showAcceleration = e.target.value === 'on';
            });
        });

        selectCoords.addEventListener('change', (e) => {
            state.coordinateSystem = e.target.value;
        });

        toggleCircle.forEach(radio => {
            radio.addEventListener('change', (e) => {
                state.showCircle = e.target.value === 'on';
            });
        });

        // p5.js sketch
        let sketch = function(p) {
            const SCALE = 100;
            const ORIGIN_X = 400;
            const ORIGIN_Y = 300;

            p.setup = function() {
                let canvas = p.createCanvas(800, 600);
                canvas.parent('canvas-container');
                p.textAlign(p.CENTER, p.CENTER);
            };

            p.draw = function() {
                p.background(255);
                p.translate(ORIGIN_X, ORIGIN_Y);
                p.scale(SCALE, -SCALE);

                // Draw coordinate system
                drawCoordinateSystem();

                // Draw lemniscate curve
                drawLemniscate();

                // Calculate position, velocity, acceleration
                const pos = calculatePosition(state.t);
                const vel = calculateVelocity(state.t);
                const acc = calculateAcceleration(state.t);

                // Draw current point
                p.stroke(0);
                p.strokeWeight(0.02);
                p.fill(0);
                p.ellipse(pos.x, pos.y, 0.05, 0.05);

                // Draw vectors
                if (state.showPosition) {
                    drawVector(p.createVector(0, 0), pos, p.color(0, 0, 0), 2, 'r');
                }

                if (state.showVelocity) {
                    drawVector(pos, p5.Vector.add(pos, vel), p.color(180, 0, 0), 2, 'v');
                }

                if (state.showAcceleration) {
                    drawVector(pos, p5.Vector.add(pos, acc), p.color(150, 0, 150), 2, 'a');
                }

                // Draw basis vectors
                drawBasisVectors(pos, vel, acc);

                // Draw instantaneous circle
                if (state.showCircle) {
                    drawOsculatingCircle(pos, vel, acc);
                }
            };

            function drawCoordinateSystem() {
                p.stroke(0);
                p.strokeWeight(0.01);

                // Draw axes
                p.line(-3, 0, 3, 0);
                p.line(0, -3, 0, 3);

                // Draw ticks and labels
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(0.08);

                for (let i = -2; i <= 2; i++) {
                    if (i !== 0) {
                        // X-axis ticks
                        p.line(i, -0.05, i, 0.05);
                        p.text(i.toString(), i, -0.1);

                        // Y-axis ticks
                        p.strokeWeight(0.005);
                        p.line(-0.05, i, 0.05, i);
                        p.text(i.toString(), 0.1, i);
                        p.strokeWeight(0.01);
                    }
                }

                // Axis labels
                p.textAlign(p.LEFT, p.TOP);
                p.textSize(0.1);
                p.text('x(t)', 2.8, 0.05);
                p.text('y(t)', 0.05, 2.8);
            }

            function drawLemniscate() {
                p.stroke(0, 100, 200);
                p.strokeWeight(0.015);
                p.noFill();

                p.beginShape();
                for (let t = 0; t <= 2 * Math.PI; t += 0.02) {
                    const pos = calculatePosition(t);
                    p.vertex(pos.x, pos.y);
                }
                p.endShape();
            }

            function calculatePosition(t) {
                return p.createVector(Math.cos(t), Math.sin(2 * t));
            }

            function calculateVelocity(t) {
                return p.createVector(-Math.sin(t), 2 * Math.cos(2 * t));
            }

            function calculateAcceleration(t) {
                return p.createVector(-Math.cos(t), -4 * Math.sin(2 * t));
            }

            function drawVector(start, end, col, weight, label) {
                p.stroke(col);
                p.strokeWeight(weight);
                p.line(start.x, start.y, end.x, end.y);

                // Draw arrowhead
                const dir = p5.Vector.sub(end, start);
                dir.normalize();
                dir.mult(0.08);

                const arrowLeft = p.createVector(dir.y, -dir.x);
                arrowLeft.mult(0.03);

                const arrowRight = p5.Vector.mult(arrowLeft, -1);

                p.line(end.x, end.y, end.x - dir.x + arrowLeft.x, end.y - dir.y + arrowLeft.y);
                p.line(end.x, end.y, end.x - dir.x + arrowRight.x, end.y - dir.y + arrowRight.y);

                // Draw label
                p.noStroke();
                p.fill(col);
                p.textSize(0.12);
                p.textAlign(p.CENTER, p.CENTER);
                p.text(label, end.x, end.y + 0.15);
            }

            function drawBasisVectors(pos, vel, acc) {
                p.stroke(0);
                p.strokeWeight(0.01);
                p.setLineDash([0.02, 0.02]);

                const basisLength = 0.5;

                switch (state.coordinateSystem) {
                    case 'rectangular':
                        drawVector(pos, p5.Vector.add(pos, p.createVector(basisLength, 0)), p.color(0, 0, 0), 1, 'î');
                        drawVector(pos, p5.Vector.add(pos, p.createVector(0, basisLength)), p.color(0, 0, 0), 1, 'ĵ');
                        break;

                    case 'normal-tangential':
                        const et = p5.Vector.div(vel, vel.mag());
                        const en = p.createVector(-et.y, et.x);
                        drawVector(pos, p5.Vector.add(pos, p5.Vector.mult(et, basisLength)), p.color(0, 0, 0), 1, 'êₜ');
                        drawVector(pos, p5.Vector.add(pos, p5.Vector.mult(en, basisLength)), p.color(0, 0, 0), 1, 'êₙ');
                        break;

                    case 'polar':
                        const er = pos.copy().normalize();
                        const etheta = p.createVector(-er.y, er.x);
                        drawVector(pos, p5.Vector.add(pos, p5.Vector.mult(er, basisLength)), p.color(0, 0, 0), 1, 'êᵣ');
                        drawVector(pos, p5.Vector.add(pos, p5.Vector.mult(etheta, basisLength)), p.color(0, 0, 0), 1, 'êₛ');
                        break;
                }

                p.setLineDash([]);
            }

            function drawOsculatingCircle(pos, vel, acc) {
                const vMag = vel.mag();
                const crossProduct = vel.x * acc.y - vel.y * acc.x;
                const radius = Math.pow(vMag, 3) / Math.abs(crossProduct);

                // Calculate normal vector
                const et = p5.Vector.div(vel, vel.mag());
                const en = p.createVector(-et.y, et.x);
                if (crossProduct < 0) en.mult(-1);

                // Calculate center
                const center = p5.Vector.add(pos, p5.Vector.mult(en, radius));

                // Draw circle
                p.stroke(100);
                p.strokeWeight(0.005);
                p.setLineDash([0.01, 0.02]);
                p.noFill();
                p.ellipse(center.x, center.y, 2 * radius, 2 * radius);
                p.setLineDash([]);
            }
        };

        // Initialize p5.js
        new p5(sketch);
    </script>
</body>
