    <script>
        // State variables
        let amplitude = 0.3;
        let stiffness = 1.0;
        let mass = 1.0;
        let time = 0;
        let isPlaying = false;
        let angularFrequency;
        let positionHistory = [];

        // Plot canvas variables
        let plotXMin = 0;
        let plotXMax = 25;
        let plotYMin = -1;
        let plotYMax = 1;

        // P5.js sketches
        let plotSketch;
        let animationSketch;

        // Initialize canvases
        function initializeCanvases() {
            // Plot canvas sketch
            plotSketch = function(p) {
                p.setup = function() {
                    let canvas = p.createCanvas(
                        document.getElementById('plot-canvas-container').offsetWidth,
                        document.getElementById('plot-canvas-container').offsetHeight
                    );
                    canvas.parent('plot-canvas-container');
                };

                p.draw = function() {
                    drawPlot(p);
                };

                p.windowResized = function() {
                    p.resizeCanvas(
                        document.getElementById('plot-canvas-container').offsetWidth,
                        document.getElementById('plot-canvas-container').offsetHeight
                    );
                };
            };

            // Animation canvas sketch
            animationSketch = function(p) {
                p.setup = function() {
                    let canvas = p.createCanvas(
                        document.getElementById('animation-canvas-container').offsetWidth,
                        document.getElementById('animation-canvas-container').offsetHeight
                    );
                    canvas.parent('animation-canvas-container');
                };

                p.draw = function() {
                    drawAnimation(p);
                };

                p.windowResized = function() {
                    p.resizeCanvas(
                        document.getElementById('animation-canvas-container').offsetWidth,
                        document.getElementById('animation-canvas-container').offsetHeight
                    );
                };
            };

            // Create p5.js instances
            new p5(plotSketch);
            new p5(animationSketch);
        }

        // Draw plot function
        function drawPlot(p) {
            p.background(255);

            // Get canvas dimensions
            let w = p.width;
            let h = p.height;
            let margin = 40;

            // Calculate scale factors
            let xScale = (w - 2 * margin) / (plotXMax - plotXMin);
            let yScale = (h - 2 * margin) / (plotYMax - plotYMin);

            // Draw grid
            p.stroke(230);
            p.strokeWeight(1);

            // Vertical grid lines
            for (let xTime = Math.ceil(plotXMin); xTime <= plotXMax; xTime++) {
                let xPos = margin + (xTime - plotXMin) * xScale;
                p.line(xPos, margin, xPos, h - margin);
            }

            // Horizontal grid lines
            for (let y = plotYMin; y <= plotYMax; y += 0.5) {
                let yPos = h - margin - (y - plotYMin) * yScale;
                p.line(margin, yPos, w - margin, yPos);
            }

            // Draw axes
            p.stroke(0);
            p.strokeWeight(2);

            // X-axis
            let xAxisY = h - margin - (0 - plotYMin) * yScale;
            p.line(margin, xAxisY, w - margin, xAxisY);

            // Y-axis
            let yAxisX = margin + (0 - plotXMin) * xScale;
            p.line(yAxisX, margin, yAxisX, h - margin);

            // Draw labels
            p.fill(0);
            p.noStroke();
            p.textAlign('center');
            p.textSize(12);

            // X-axis labels
            for (let xTime = Math.ceil(plotXMin); xTime <= plotXMax; xTime += 5) {
                let xPos = margin + (xTime - plotXMin) * xScale;
                p.text(xTime.toString(), xPos, xAxisY + 20);
            }

            // Y-axis labels
            p.textAlign('right');
            for (let y = plotYMin; y <= plotYMax; y += 0.5) {
                if (Math.abs(y) < 0.001) y = 0;
                let yPos = h - margin - (y - plotYMin) * yScale;
                p.text(y.toFixed(1), yAxisX - 10, yPos + 4);
            }

            // Draw position history
            if (positionHistory.length > 1) {
                p.stroke('#6363B2');
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();

                for (let point of positionHistory) {
                    let xPos = margin + (point.time - plotXMin) * xScale;
                    let yPos = h - margin - (point.position - plotYMin) * yScale;
                    p.vertex(xPos, yPos);
                }

                p.endShape();
            }
        }

        // Draw animation function
        function drawAnimation(p) {
            p.background(255);

            let w = p.width;
            let h = p.height;
            let margin = 40;

            // Calculate current position
            let position = amplitude * Math.sin(angularFrequency * time);

            // Mapping: canvas Y from 1.2 to -1.2 (inverted)
            let yScale = (h - 2 * margin) / 2.4;
            let centerX = w / 2;
            let centerY = h / 2;

            // Draw vertical axis
            p.stroke(0);
            p.strokeWeight(2);
            p.line(centerX, margin, centerX, h - margin);

            // Draw axis labels
            p.fill(0);
            p.noStroke();
            p.textAlign('right');
            p.textSize(12);

            p.text('1', centerX - 10, margin + (1.2 - 1) * yScale + 4);
            p.text('0', centerX - 10, centerY + 4);
            p.text('-1', centerX - 10, margin + (1.2 + 1) * yScale + 4);

            // Draw anchor point
            let anchorY = margin + 20;
            p.stroke(0);
            p.strokeWeight(3);
            p.line(centerX - 15, anchorY, centerX + 15, anchorY);

            // Calculate mass position
            let massY = centerY - position * yScale;

            // Draw spring
            drawSpring(p, centerX, anchorY, massY - 20);

            // Draw mass
            p.fill('#6363B2');
            p.noStroke();
            let massWidth = 60;
            let massHeight = 30;
            p.rect(centerX - massWidth/2, massY - massHeight/2, massWidth, massHeight, 5);
        }

        // Draw spring function
        function drawSpring(p, x, y1, y2) {
            p.stroke(0);
            p.strokeWeight(2);
            p.noFill();

            let numCoils = 10;
            let amplitude = 15;
            let springLength = y2 - y1;
            let coilSpacing = springLength / numCoils;

            p.beginShape();
            for (let i = 0; i <= numCoils * 4; i++) {
                let t = i / (numCoils * 4);
                let y = y1 + springLength * t;

                let offset;
                if (i % 4 === 0 || i % 4 === 2) {
                    offset = 0;
                } else if (i % 4 === 1) {
                    offset = amplitude;
                } else {
                    offset = -amplitude;
                }

                // Compress/expand based on position
                let compressionFactor = springLength / (numCoils * 4 * 10);
                offset *= compressionFactor;

                p.vertex(x + offset, y);
            }
            p.endShape();
        }

        // Update angular frequency
        function updateAngularFrequency() {
            angularFrequency = Math.sqrt(stiffness / mass);
        }

        // Update position for current time
        function updatePosition() {
            let position = amplitude * Math.sin(angularFrequency * time);
            positionHistory.push({ time: time, position: position });

            // Limit history length to prevent memory issues
            if (positionHistory.length > 1000) {
                positionHistory = positionHistory.slice(-500);
            }

            // Extend plot x-axis if needed
            if (time > plotXMax * 0.9) {
                plotXMax += 25;
            }
        }

        // Animation loop
        let lastTime = 0;
        function animate(currentTime) {
            if (isPlaying) {
                let deltaTime = (currentTime - lastTime) / 1000; // Convert to seconds
                lastTime = currentTime;

                // Update time
                time += Math.min(deltaTime * 2, 0.05); // Cap delta time for stability

                updatePosition();

                // Redraw canvases
                if (plotSketch && typeof plotSketch._renderer !== 'undefined') {
                    plotSketch._renderer._pInst.redraw();
                }
                if (animationSketch && typeof animationSketch._renderer !== 'undefined') {
                    animationSketch._renderer._pInst.redraw();
                }
            } else {
                lastTime = currentTime;
            }

            requestAnimationFrame(animate);
        }

        // Event listeners
        document.getElementById('slider-amplitude').addEventListener('input', function(e) {
            amplitude = parseFloat(e.target.value);
            document.getElementById('amplitude-value').textContent = amplitude.toFixed(3);
            resetSimulation();
        });

        document.getElementById('slider-stiffness').addEventListener('input', function(e) {
            stiffness = parseFloat(e.target.value);
            document.getElementById('stiffness-value').textContent = stiffness.toFixed(2);
            updateAngularFrequency();
            resetSimulation();
        });

        document.getElementById('slider-mass').addEventListener('input', function(e) {
            mass = parseFloat(e.target.value);
            document.getElementById('mass-value').textContent = mass.toFixed(2);
            updateAngularFrequency();
            resetSimulation();
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '||' : '▶';
        });

        document.getElementById('btn-step-back').addEventListener('click', function() {
            if (!isPlaying) {
                time = Math.max(0, time - 0.1);
                updatePosition();

                // Redraw
                if (plotSketch && typeof plotSketch._renderer !== 'undefined') {
                    plotSketch._renderer._pInst.redraw();
                }
                if (animationSketch && typeof animationSketch._renderer !== 'undefined') {
                    animationSketch._renderer._pInst.redraw();
                }
            }
        });

        document.getElementById('btn-step-forward').addEventListener('click', function() {
            if (!isPlaying) {
                time += 0.1;
                updatePosition();

                // Redraw
                if (plotSketch && typeof plotSketch._renderer !== 'undefined') {
                    plotSketch._renderer._pInst.redraw();
                }
                if (animationSketch && typeof animationSketch._renderer !== 'undefined') {
                    animationSketch._renderer._pInst.redraw();
                }
            }
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            // Reset sliders
            document.getElementById('slider-amplitude').value = 0.3;
            document.getElementById('slider-stiffness').value = 1.0;
            document.getElementById('slider-mass').value = 1.0;

            // Update displays
            document.getElementById('amplitude-value').textContent = '0.3';
            document.getElementById('stiffness-value').textContent = '1.0';
            document.getElementById('mass-value').textContent = '1.0';

            // Reset state
            amplitude = 0.3;
            stiffness = 1.0;
            mass = 1.0;
            time = 0;
            isPlaying = false;
            plotXMax = 25;
            document.getElementById('btn-play-pause').textContent = '▶';

            updateAngularFrequency();
            positionHistory = [];

            // Redraw
            if (plotSketch && typeof plotSketch._renderer !== 'undefined') {
                plotSketch._renderer._pInst.redraw();
            }
            if (animationSketch && typeof animationSketch._renderer !== 'undefined') {
                animationSketch._renderer._pInst.redraw();
            }
        });

        // Reset simulation function
        function resetSimulation() {
            time = 0;
            isPlaying = false;
            plotXMax = 25;
            document.getElementById('btn-play-pause').textContent = '▶';
            positionHistory = [];

            // Redraw
            if (plotSketch && typeof plotSketch._renderer !== 'undefined') {
                plotSketch._renderer._pInst.redraw();
            }
            if (animationSketch && typeof animationSketch._renderer !== 'undefined') {
                animationSketch._renderer._pInst.redraw();
            }
        }

        // Initialize everything when page loads
        window.addEventListener('load', function() {
            updateAngularFrequency();
            initializeCanvases();

            // Start animation loop
            requestAnimationFrame(animate);
        });
    </script>
</body>
</html>
