            </div>

            <!-- Data Generation Sliders -->
            <div class="control-section">
                <div class="slider-container">
                    <label for="slider-n">\(n\)</label>
                    <input type="range" id="slider-n" min="2" max="20" step="1" value="6">
                    <div class="slider-value" id="label-n">6</div>
                </div>
                <div class="slider-container">
                    <label for="slider-tmin">\(T_{min}\) (°C)</label>
                    <input type="range" id="slider-tmin" min="0" max="5999" step="1" value="25">
                    <div class="slider-value" id="label-tmin">25.</div>
                </div>
                <div class="slider-container">
                    <label for="slider-tmax">\(T_{max}\) (°C)</label>
                    <input type="range" id="slider-tmax" min="1" max="1200" step="1" value="100">
                    <div class="slider-value" id="label-tmax">100.</div>
