                <label>data generation model parameters</label>
                <div class="slider-container">
                    <label for="slider-tref">\(T_{ref}\) (°C)</label>
                    <input type="range" id="slider-tref" min="0" max="6000" step="1" value="50">
                    <div class="slider-value" id="label-tref">50.</div>
                </div>
                <div class="slider-container">
                    <label for="slider-param" id="label-param-name">\(a_{EP}\)</label>
                    <input type="range" id="slider-param" min="1" max="6000" step="1" value="4000">
                    <div class="slider-value" id="label-param-value">4000.</div>
                </div>
            </div>
        </div>

        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <div id="fit-results">\(a_{Ar} = 4,345.\) \(r^2 = 1.000\)</div>
            <div id="plot-container"></div>
        </div>
    </div>

    <!-- CDN Scripts -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // State management
        let state = {
            model: 'ep',
            tmaxLimit: 'low',
            n: 6,
            tmin: 25,
            tmax: 100,
            tref: 50,
            param: 4000
        };

        // Initialize event listeners
        function initEventListeners() {
            // Model selection buttons
            document.getElementById('btn-model-ep').addEventListener('click', () => {
                state.model = 'ep';
                updateButtonStates();
                update();
            });

            document.getElementById('btn-model-ar').addEventListener('click', () => {
                state.model = 'ar';
                updateButtonStates();
                update();
            });

            // T_max limit buttons
            document.getElementById('btn-tmax-low').addEventListener('click', () => {
                state.tmaxLimit = 'low';
                updateButtonStates();
                updateSliderConstraints();
                update();
            });

            document.getElementById('btn-tmax-high').addEventListener('click', () => {
                state.tmaxLimit = 'high';
                updateButtonStates();
                updateSliderConstraints();
                update();
            });

            // Sliders
            const sliders = ['n', 'tmin', 'tmax', 'tref', 'param'];
            sliders.forEach(slider => {
                document.getElementById(`slider-${slider}`).addEventListener('input', (e) => {
                    state[slider] = parseInt(e.target.value);
                    updateSliderConstraints();
                    update();
                });
            });
        }

        // Update button visual states
        function updateButtonStates() {
            // Model buttons
            document.getElementById('btn-model-ep').classList.toggle('active', state.model === 'ep');
            document.getElementById('btn-model-ar').classList.toggle('active', state.model === 'ar');

            // T_max limit buttons
            document.getElementById('btn-tmax-low').classList.toggle('active', state.tmaxLimit === 'low');
            document.getElementById('btn-tmax-high').classList.toggle('active', state.tmaxLimit === 'high');
        }

        // Update slider constraints
        function updateSliderConstraints() {
            const tmaxSlider = document.getElementById('slider-tmax');
            const tminSlider = document.getElementById('slider-tmin');
            const trefSlider = document.getElementById('slider-tref');

            // Update T_max max value
            tmaxSlider.max = state.tmaxLimit === 'low' ? 1200 : 6000;

            // Ensure T_min < T_max
            if (state.tmin >= state.tmax) {
                state.tmin = Math.max(0, state.tmax - 1);
                tminSlider.value = state.tmin;
            }

            // Ensure T_min <= T_ref <= T_max
            if (state.tref < state.tmin) {
                state.tref = state.tmin;
                trefSlider.value = state.tref;
            } else if (state.tref > state.tmax) {
                state.tref = state.tmax;
                trefSlider.value = state.tref;
            }

            // Ensure T_max is within its new max
            if (state.tmax > parseInt(tmaxSlider.max)) {
                state.tmax = parseInt(tmaxSlider.max);
                tmaxSlider.value = state.tmax;
            }
        }

        // Update parameter label
        function updateParameterLabel() {
            const label = document.getElementById('label-param-name');
            label.textContent = state.model === 'ep' ? '\\(a_{EP}\\)' : '\\(a_{Ar}\\)';
            MathJax.typeset([label]);
        }

        // Update slider value displays
        function updateSliderDisplays() {
            document.getElementById('label-n').textContent = state.n;
            document.getElementById('label-tmin').textContent = state.tmin + '.';
            document.getElementById('label-tmax').textContent = state.tmax + '.';
            document.getElementById('label-tref').textContent = state.tref + '.';
            document.getElementById('label-param-value').textContent = state.param + '.';
        }

        // Generate data points
        function generateData() {
            const data = [];
            const tMinK = state.tmin + 273.15;
            const tMaxK = state.tmax + 273.15;
            const tRefK = state.tref + 273.15;

            for (let i = 0; i < state.n; i++) {
                const tC = state.tmin + (state.tmax - state.tmin) * i / (state.n - 1);
                const tK = tC + 273.15;
                let y;

                if (state.model === 'ep') {
                    // Eyring-Polanyi: y = (T_K / T_ref_K) * exp(a_EP * (1/T_ref_K - 1/T_K))
                    y = (tK / tRefK) * Math.exp(state.param * (1/tRefK - 1/tK));
                } else {
                    // Arrhenius: y = exp(a_Ar * (1/T_ref_K - 1/T_K))
                    y = Math.exp(state.param * (1/tRefK - 1/tK));
                }

                data.push({ t: tC, y: y });
            }

            return data;
        }

        // Fit data using the other model
        function fitData(data) {
            const tRefK = state.tref + 273.15;
            const fitModel = state.model === 'ep' ? 'ar' : 'ep';
            let bestA = 0;
            let minRSS = Infinity;

            // Grid search for best fit parameter
            for (let a = 0; a <= 10000; a += 1) {
                let rss = 0;

                for (const point of data) {
                    const tK = point.t + 273.15;
                    let yFit;

                    if (fitModel === 'ep') {
                        yFit = (tK / tRefK) * Math.exp(a * (1/tRefK - 1/tK));
                    } else {
                        yFit = Math.exp(a * (1/tRefK - 1/tK));
                    }

                    rss += Math.pow(point.y - yFit, 2);
                }

                if (rss < minRSS) {
                    minRSS = rss;
                    bestA = a;
                }
            }

            // Calculate r^2
            const yMean = data.reduce((sum, point) => sum + point.y, 0) / data.length;
            let ssTot = 0;
            let ssRes = 0;

            for (const point of data) {
                ssTot += Math.pow(point.y - yMean, 2);
                const tK = point.t + 273.15;
                let yFit;

                if (fitModel === 'ep') {
                    yFit = (tK / tRefK) * Math.exp(bestA * (1/tRefK - 1/tK));
                } else {
                    yFit = Math.exp(bestA * (1/tRefK - 1/tK));
                }

                ssRes += Math.pow(point.y - yFit, 2);
            }

            const r2 = 1 - (ssRes / ssTot);

            return { a: bestA, r2: r2, model: fitModel };
        }

        // Update fit results display
        function updateFitResults(fit) {
            const resultsDiv = document.getElementById('fit-results');
            const paramName = fit.model === 'ep' ? 'a_{EP}' : 'a_{Ar}';
            resultsDiv.textContent = `\\(${paramName} = ${fit.a.toLocaleString()}.\\) \\(r^2 = ${fit.r2.toFixed(3)}\\)`;
            MathJax.typeset([resultsDiv]);
        }

        // Draw plot
        function drawPlot(data, fit) {
            const trace1 = {
                x: data.map(d => d.t),
                y: data.map(d => d.y),
                mode: 'markers',
                type: 'scatter',
                name: 'Data points',
                marker: {
                    color: 'blue',
                    line: {
                        color: 'blue',
                        width: 2
                    }
                }
            };

            // Generate fit curve
            const fitCurve = [];
            const tRefK = state.tref + 273.15;

            for (let i = 0; i < 200; i++) {
                const tC = state.tmin + (state.tmax - state.tmin) * i / 199;
                const tK = tC + 273.15;
                let y;

                if (fit.model === 'ep') {
                    y = (tK / tRefK) * Math.exp(fit.a * (1/tRefK - 1/tK));
                } else {
                    y = Math.exp(fit.a * (1/tRefK - 1/tK));
                }

                fitCurve.push({ t: tC, y: y });
            }

            const trace2 = {
                x: fitCurve.map(d => d.t),
                y: fitCurve.map(d => d.y),
                mode: 'lines',
                type: 'scatter',
                name: 'Fit curve',
                line: {
                    color: 'green',
                    width: 2
                }
            };

            const layout = {
                title: `${fit.model === 'ep' ? 'Eyring–Polanyi' : 'Arrhenius'} model fit to ${state.model === 'ep' ? 'Eyring–Polanyi' : 'Arrhenius'} data`,
                xaxis: {
                    title: 'T °C'
                },
                yaxis: {
                    title: 'k(T) / k(T_{ref})'
                },
                margin: {
                    l: 60,
                    r: 20,
                    t: 60,
                    b: 60
                }
            };

            Plotly.newPlot('plot-container', [trace1, trace2], layout);
        }

        // Main update function
        function update() {
            updateParameterLabel();
            updateSliderDisplays();

            const data = generateData();
            const fit = fitData(data);

            updateFitResults(fit);
            drawPlot(data, fit);
        }

        // Initialize everything
        window.addEventListener('load', () => {
            initEventListeners();
            updateButtonStates();
            updateSliderConstraints();
            update();
        });
    </script>
</body>
</html>
