                        <span id="number-value" class="value-display">40</span>
                    </div>
                </div>
            </div>

            <div class="control-group">
                <h4>Method</h4>
                <div class="button-group">
                    <button id="btn-prim" class="active">Prim</button>
                    <button id="btn-kruskal">Kruskal</button>
                </div>
            </div>

            <div class="control-group">
                <h4>Process</h4>
                <div class="animation-controls">
                    <button id="btn-play">►</button>
                    <button id="btn-pause" disabled>||</button>
                    <button id="btn-reset">|◄</button>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // Global state variables
        let dimension = '2D';
        let seed = 525;
        let number = 40;
        let algorithm = 'prim';
        let animationState = 'stopped';
        let points = [];
        let mstEdges = [];
        let currentStep = 0;
        let animationSpeed = 10;

        // P5.js variables
        let pg;
        let camera3D;

        // UI elements
        const btn2D = document.getElementById('btn-2d');
        const btn3D = document.getElementById('btn-3d');
        const sliderSeed = document.getElementById('slider-seed');
        const seedValue = document.getElementById('seed-value');
        const sliderNumber = document.getElementById('slider-number');
        const numberValue = document.getElementById('number-value');
        const btnPrim = document.getElementById('btn-prim');
        const btnKruskal = document.getElementById('btn-kruskal');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');

        // P5.js setup
        function setup() {
            const container = document.getElementById('canvas-container');
            const canvas = createCanvas(container.offsetWidth, container.offsetHeight);
            canvas.parent('canvas-container');

            frameRate(60);
            initializeVisualization();
        }

        function windowResized() {
            const container = document.getElementById('canvas-container');
            resizeCanvas(container.offsetWidth, container.offsetHeight);
        }

        // Initialize or reset visualization
        function initializeVisualization() {
            generatePoints();
            resetAlgorithm();
            redrawCanvas();
        }

        // Generate points based on dimension, seed, and number
        function generatePoints() {
            points = [];
            p5.randomSeed(seed);

            const margin = 50;
            if (dimension === '2D') {
                for (let i = 0; i < number; i++) {
                    const x = p5.random(margin, width - margin);
                    const y = p5.random(margin, height - margin);
                    points.push(createVector(x, y));
                }
            } else { // 3D
                const size = min(width, height) - margin * 2;
                for (let i = 0; i < number; i++) {
                    const x = p5.random(-size/2, size/2);
                    const y = p5.random(-size/2, size/2);
                    const z = p5.random(-size/2, size/2);
                    points.push(createVector(x, y, z));
                }
            }
        }

        // Reset algorithm state
        function resetAlgorithm() {
            mstEdges = [];
            currentStep = 0;
            if (animationState === 'playing') {
                pauseAnimation();
            }
            animationState = 'stopped';
            updateButtonStates();
        }

        // Calculate distance between two points
        function distance(p1, p2) {
            if (dimension === '2D') {
                return p5.dist(p1.x, p1.y, p2.x, p2.y);
            } else {
                return p5.dist(p1.x, p1.y, p1.z, p2.x, p2.y, p2.z);
            }
        }

        // Generate all possible edges
        function getAllEdges() {
            const edges = [];
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    edges.push({
                        from: i,
                        to: j,
                        weight: distance(points[i], points[j])
                    });
                }
            }
            return edges;
        }

        // Pre-calculate Prim's algorithm steps
        function calculatePrimSteps() {
            const edges = getAllEdges();
            const steps = [];
            const visited = new Set();
            const heap = [];
            visited.add(0);

            // Add all edges from vertex 0
            for (const edge of edges) {
                if (edge.from === 0 || edge.to === 0) {
                    heap.push(edge);
                }
            }

            while (visited.size < points.length && heap.length > 0) {
                // Find the minimum edge
                let minIndex = 0;
                for (let i = 1; i < heap.length; i++) {
                    if (heap[i].weight < heap[minIndex].weight) {
                        minIndex = i;
                    }
                }

                const minEdge = heap[minIndex];
                heap.splice(minIndex, 1);

                const newVertex = visited.has(minEdge.from) ? minEdge.to : minEdge.from;

                if (!visited.has(newVertex)) {
                    steps.push(minEdge);
                    visited.add(newVertex);

                    // Add all edges from the new vertex
                    for (const edge of edges) {
                        if ((edge.from === newVertex || edge.to === newVertex) && 
                            !visited.has(edge.from) || !visited.has(edge.to)) {
                            heap.push(edge);
                        }
                    }
                }
            }

            return steps;
        }

        // Pre-calculate Kruskal's algorithm steps
        function calculateKruskalSteps() {
            const edges = getAllEdges();
            edges.sort((a, b) => a.weight - b.weight);

            const steps = [];
            const parent = [];
            for (let i = 0; i < points.length; i++) {
                parent[i] = i;
            }

            function find(x) {
                if (parent[x] !== x) {
                    parent[x] = find(parent[x]);
                }
                return parent[x];
            }

            function union(x, y) {
                const rootX = find(x);
                const rootY = find(y);
                if (rootX !== rootY) {
                    parent[rootX] = rootY;
                    return true;
                }
                return false;
            }

            for (const edge of edges) {
                if (union(edge.from, edge.to)) {
                    steps.push(edge);
                    if (steps.length === points.length - 1) break;
                }
            }

            return steps;
        }

        // Get pre-calculated algorithm steps
        function getAlgorithmSteps() {
            if (algorithm === 'prim') {
                return calculatePrimSteps();
            } else {
                return calculateKruskalSteps();
            }
        }

        // Draw the canvas
        function draw() {
            background(240);

            if (dimension === '2D') {
                draw2D();
            } else {
                draw3D();
            }

            // Handle animation
            if (animationState === 'playing') {
                const steps = getAlgorithmSteps();
                if (frameCount % animationSpeed === 0 && currentStep < steps.length) {
                    mstEdges.push(steps[currentStep]);
                    currentStep++;
                }

                if (currentStep >= steps.length) {
                    animationComplete();
                }
            }
        }

        function draw2D() {
            // Draw MST edges
            stroke(255, 0, 0);
            strokeWeight(2);
            for (const edge of mstEdges) {
                line(points[edge.from].x, points[edge.from].y, 
                     points[edge.to].x, points[edge.to].y);
            }

            // Draw points
            noStroke();
            fill(0, 0, 255);
            for (let i = 0; i < points.length; i++) {
                circle(points[i].x, points[i].y, 10);
            }
        }

        function draw3D() {
            push();
            lights();
            orbitControl();

            const size = min(width, height) - 100;
            translate(width/2, height/2);

            // Draw bounding box
            stroke(150);
            strokeWeight(1);
            noFill();
            box(size);

            // Draw MST edges
            stroke(255, 0, 0);
            strokeWeight(3);
            for (const edge of mstEdges) {
                line(points[edge.from].x, points[edge.from].y, points[edge.from].z,
                     points[edge.to].x, points[edge.to].y, points[edge.to].z);
            }

            // Draw points
            noStroke();
            fill(0, 0, 255);
            for (const point of points) {
                push();
                translate(point.x, point.y, point.z);
                sphere(8);
                pop();
            }
            pop();
        }

        function redrawCanvas() {
            if (dimension === '2D') {
                // No specific redraw needed for 2D as draw() handles it
            }
            // For 3D, the draw function handles everything
        }

        function startAnimation() {
            animationState = 'playing';
            updateButtonStates();
        }

        function pauseAnimation() {
            animationState = 'paused';
            updateButtonStates();
        }

        function stopAnimation() {
            animationState = 'stopped';
            currentStep = 0;
            mstEdges = [];
            updateButtonStates();
        }

        function animationComplete() {
            animationState = 'stopped';
            updateButtonStates();
        }

        function updateButtonStates() {
            switch(animationState) {
                case 'playing':
                    btnPlay.disabled = true;
                    btnPause.disabled = false;
                    btnReset.disabled = false;
                    break;
                case 'paused':
                    btnPlay.disabled = false;
                    btnPause.disabled = true;
                    btnReset.disabled = false;
                    break;
                default: // stopped
                    btnPlay.disabled = currentStep >= getAlgorithmSteps().length;
                    btnPause.disabled = true;
                    btnReset.disabled = false;
            }
        }

        // Event listeners
        btn2D.addEventListener('click', () => {
            if (dimension !== '2D') {
                dimension = '2D';
                btn2D.classList.add('active');
                btn3D.classList.remove('active');
                initializeVisualization();
            }
        });

        btn3D.addEventListener('click', () => {
            if (dimension !== '3D') {
                dimension = '3D';
                btn3D.classList.add('active');
                btn2D.classList.remove('active');
                initializeVisualization();
            }
        });

        sliderSeed.addEventListener('input', (e) => {
            seed = parseInt(e.target.value);
            seedValue.textContent = seed;
        });

        sliderSeed.addEventListener('change', () => {
            initializeVisualization();
        });

        sliderNumber.addEventListener('input', (e) => {
            number = parseInt(e.target.value);
            numberValue.textContent = number;
        });

        sliderNumber.addEventListener('change', () => {
            initializeVisualization();
        });

        btnPrim.addEventListener('click', () => {
            if (algorithm !== 'prim') {
                algorithm = 'prim';
                btnPrim.classList.add('active');
                btnKruskal.classList.remove('active');
                resetAlgorithm();
            }
        });

        btnKruskal.addEventListener('click', () => {
            if (algorithm !== 'kruskal') {
                algorithm = 'kruskal';
                btnKruskal.classList.add('active');
                btnPrim.classList.remove('active');
                resetAlgorithm();
            }
        });

        btnPlay.addEventListener('click', () => {
            startAnimation();
        });

        btnPause.addEventListener('click', () => {
            pauseAnimation();
        });

        btnReset.addEventListener('click', () => {
            resetAlgorithm();
        });
    </script>
</body>
</html>
