            <p>estimate as: 1 × 2 = 2</p>
            <p>result is: 2.2</p>
        </div>
    </div>

    <script>
        // Helper function to convert number to integer string (remove decimal)
        function numberToIntString(num) {
            const str = num.toString();
            if (str.includes('.')) {
                return str.replace('.', '');
            }
            return str;
        }

        // Helper function to get factor (power of 10)
        function getFactor(num) {
            const str = num.toString();
            if (str.includes('.')) {
                const decimalPlaces = str.split('.')[1].length;
                return Math.pow(10, decimalPlaces);
            }
            return 1;
        }

        // Main update display function
        function updateDisplay() {
            const method = document.getElementById('select-method').value;
            const num1 = parseFloat(document.getElementById('slider-num1').value);
            const num2 = parseFloat(document.getElementById('slider-num2').value);

            const outputDisplay = document.getElementById('output-display');
            outputDisplay.innerHTML = '';

            if (method === 'estimation') {
                // Estimation method
                const num1Int = numberToIntString(num1);
                const num2Int = numberToIntString(num2);
                const productOfInts = parseInt(num1Int) * parseInt(num2Int);
                const roundedNum1 = Math.round(num1);
                const roundedNum2 = Math.round(num2);
                const productOfRounded = roundedNum1 * roundedNum2;
                const result = num1 * num2;

                // Create step elements
                const steps = [
                    `start with: ${num1} × ${num2}`,
                    `convert to: ${num1Int}. × ${num2Int}. = ${productOfInts}.`,
                    `estimate as: ${roundedNum1} × ${roundedNum2} = ${productOfRounded}`,
                    `result is: ${result}`
                ];

                steps.forEach(step => {
                    const p = document.createElement('p');
                    p.textContent = step;
                    outputDisplay.appendChild(p);
                });

            } else if (method === 'place values') {
                // Place values method
                const num1Int = numberToIntString(num1);
                const num2Int = numberToIntString(num2);
                const num1Factor = getFactor(num1);
                const num2Factor = getFactor(num2);
                const productOfInts = parseInt(num1Int) * parseInt(num2Int);
                const productOfFactors = num1Factor * num2Factor;
                const result = num1 * num2;

                // Create step elements with special handling for integers
                const steps = [];
                steps.push(`start with: ${num1} × ${num2}`);

                if (num1Factor === 1 && num2Factor === 1) {
                    steps.push(`rewrite as: ${num1} × ${num2}`);
                    steps.push(`= ${result}`);
                } else if (num1Factor === 1) {
                    steps.push(`rewrite as: ${num1} × (${num2Int}. ÷ ${num2Factor})`);
                    steps.push(`= ${num1} × ${num2Int}. ÷ ${num2Factor}`);
                    steps.push(`= ${num1 * num2Int}. ÷ ${num2Factor}`);
                    steps.push(`= ${result}`);
                } else if (num2Factor === 1) {
                    steps.push(`rewrite as: (${num1Int}. ÷ ${num1Factor}) × ${num2}`);
                    steps.push(`= ${num1Int}. × ${num2} ÷ ${num1Factor}`);
                    steps.push(`= ${num1Int * num2}. ÷ ${num1Factor}`);
                    steps.push(`= ${result}`);
                } else {
                    steps.push(`rewrite as: (${num1Int}. ÷ ${num1Factor}) × (${num2Int}. ÷ ${num2Factor})`);
                    steps.push(`= (${num1Int}. × ${num2Int}.) ÷ (${num1Factor} × ${num2Factor})`);
                    steps.push(`= ${productOfInts}. ÷ ${productOfFactors}`);
                    steps.push(`= ${result}`);
                }

                steps.forEach(step => {
                    const p = document.createElement('p');
                    p.textContent = step;
                    outputDisplay.appendChild(p);
                });
            }
        }

        // Event listeners
        document.getElementById('select-method').addEventListener('change', updateDisplay);
        document.getElementById('slider-num1').addEventListener('input', updateDisplay);
        document.getElementById('slider-num2').addEventListener('input', updateDisplay);

        // Integer toggle buttons
        document.getElementById('btn-int-yes').addEventListener('click', function() {
            this.classList.add('active');
            this.classList.remove('inactive');
            document.getElementById('btn-int-no').classList.add('inactive');
            document.getElementById('btn-int-no').classList.remove('active');

            const sliderNum2 = document.getElementById('slider-num2');
            sliderNum2.step = '1';
            sliderNum2.value = Math.round(parseFloat(sliderNum2.value));
            updateDisplay();
        });

        document.getElementById('btn-int-no').addEventListener('click', function() {
            this.classList.add('active');
            this.classList.remove('inactive');
            document.getElementById('btn-int-yes').classList.add('inactive');
            document.getElementById('btn-int-yes').classList.remove('active');

            document.getElementById('slider-num2').step = '0.1';
            updateDisplay();
        });

        // Initial update
        updateDisplay();
    </script>
</body>
</html>
