    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <select id="select-vibration-mode">
                <option value="symmetric-stretch">symmetric stretch</option>
                <option value="asymmetric-stretch">stretch (triply degenerate)</option>
                <option value="bending-e">bend (doubly degenerate)</option>
                <option value="bending-f2">bend (triply degenerate)</option>
                <option value="ir-spectrum">IR spectrum</option>
            </select>
            <span>run</span>
            <div class="control-buttons">
                <button id="btn-play">►</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◄</button>
            </div>
        </div>

        <div id="visualization-area">
            <div id="three-js-canvas-container"></div>
            <div id="plotly-chart-container"></div>
        </div>

        <div id="description-area">
            <p id="label-left" class="blue-text">stretching A₁ Raman active 2916 cm⁻¹</p>
            <p id="label-right" class="blue-text">no change in the molecular dipole moment</p>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Global variables
        let scene, renderer, cameraLeft, cameraRight, controlsLeft, controlsRight;
        let carbon, hydrogens = [], bonds = [];
        let dipoleVectors = [], netDipoleVector;
        let animationId = null;
        let time = 0;
        let isPlaying = false;
        let currentMode = 'symmetric-stretch';
        let plotRendered = false;

        // Initial positions for tetrahedral geometry
        const initialPositions = [
            new THREE.Vector3(1, 1, 1),
            new THREE.Vector3(1, -1, -1),
            new THREE.Vector3(-1, 1, -1),
            new THREE.Vector3(-1, -1, 1)
        ];

        // Initialize Three.js scene
        function initThreeJS() {
            const container = document.getElementById('three-js-canvas-container');

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setClearColor(0xfafafa);
            container.appendChild(renderer.domElement);

            // Scene
            scene = new THREE.Scene();

            // Lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Cameras
            const aspect = container.clientWidth / container.clientHeight / 2;
            cameraLeft = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
            cameraLeft.position.set(5, 3, 5);
            cameraLeft.lookAt(0, 0, 0);

            cameraRight = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
            cameraRight.position.set(5, 3, 5);
            cameraRight.lookAt(0, 0, 0);

            // Controls
            controlsLeft = new THREE.OrbitControls(cameraLeft, renderer.domElement);
            controlsLeft.enableDamping = true;
            controlsLeft.dampingFactor = 0.05;

            controlsRight = new THREE.OrbitControls(cameraRight, renderer.domElement);
            controlsRight.enableDamping = true;
            controlsRight.dampingFactor = 0.05;

            // Create molecule
            createMolecule();

            // Create dipole vectors
            createDipoleVectors();

            // Start render loop
            animate();
        }

        function createMolecule() {
            // Carbon atom
            const carbonGeometry = new THREE.SphereGeometry(0.3, 32, 32);
            const carbonMaterial = new THREE.MeshLambertMaterial({ color: 0x000000 });
            carbon = new THREE.Mesh(carbonGeometry, carbonMaterial);
            carbon.position.set(0, 0, 0);
            scene.add(carbon);

            // Hydrogen atoms
            const hydrogenGeometry = new THREE.SphereGeometry(0.2, 32, 32);
            const hydrogenMaterial = new THREE.MeshLambertMaterial({ color: 0xe0f0ff });

            for (let i = 0; i < 4; i++) {
                const hydrogen = new THREE.Mesh(hydrogenGeometry, hydrogenMaterial);
                hydrogen.position.copy(initialPositions[i]);
                hydrogens.push(hydrogen);
                scene.add(hydrogen);

                // Bond
                const bondGeometry = new THREE.CylinderGeometry(0.05, 0.05, 1);
                const bondMaterial = new THREE.MeshBasicMaterial({ color: 0x808080 });
                const bond = new THREE.Mesh(bondGeometry, bondMaterial);
                bonds.push(bond);
                scene.add(bond);
            }

            updateBonds();
        }

        function createDipoleVectors() {
            // Individual dipole vectors
            for (let i = 0; i < 4; i++) {
                const origin = new THREE.Vector3(0, 0, 0);
                const direction = new THREE.Vector3(0, 0, 1);
                const length = 1;
                const color = 0x0066cc;
                const arrowHelper = new THREE.ArrowHelper(direction, origin, length, color);
                dipoleVectors.push(arrowHelper);
                scene.add(arrowHelper);
            }

            // Net dipole vector
            const netOrigin = new THREE.Vector3(0, 0, 0);
            const netDirection = new THREE.Vector3(0, 0, 1);
            netDipoleVector = new THREE.ArrowHelper(netDirection, netOrigin, 0, 0xff0000);
            netDipoleVector.visible = false;
            scene.add(netDipoleVector);
        }

        function updateBonds() {
            for (let i = 0; i < 4; i++) {
                const bond = bonds[i];
                const start = carbon.position;
                const end = hydrogens[i].position;

                const direction = new THREE.Vector3().subVectors(end, start);
                const length = direction.length();

                bond.position.copy(start).add(direction.multiplyScalar(0.5));
                bond.scale.y = length;
                bond.lookAt(end);
            }
        }

        function updateDipoleVectors() {
            let netDipole = new THREE.Vector3(0, 0, 0);

            for (let i = 0; i < 4; i++) {
                const direction = new THREE.Vector3().subVectors(hydrogens[i].position, carbon.position).normalize();
                const length = hydrogens[i].position.distanceTo(carbon.position);

                dipoleVectors[i].setDirection(direction);
                dipoleVectors[i].setLength(length * 0.8, length * 0.2, length * 0.1);

                netDipole.add(direction.clone().multiplyScalar(length));
            }

            // Update net dipole
            const netLength = netDipole.length();
            if (netLength > 0.1) {
                netDipoleVector.setDirection(netDipole.normalize());
                netDipoleVector.setLength(netLength * 0.5, netLength * 0.1, netLength * 0.05);
                netDipoleVector.visible = true;
            } else {
                netDipoleVector.visible = false;
            }
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isPlaying) {
                time += 0.02;
                updateVibration();
            }

            controlsLeft.update();
            controlsRight.update();

            // Render left viewport
            renderer.setViewport(0, 0, renderer.domElement.width / 2, renderer.domElement.height);
            renderer.setScissor(0, 0, renderer.domElement.width / 2, renderer.domElement.height);
            renderer.setScissorTest(true);
            renderer.render(scene, cameraLeft);

            // Render right viewport
            renderer.setViewport(renderer.domElement.width / 2, 0, renderer.domElement.width / 2, renderer.domElement.height);
            renderer.setScissor(renderer.domElement.width / 2, 0, renderer.domElement.width / 2, renderer.domElement.height);
            renderer.render(scene, cameraRight);
            renderer.setScissorTest(false);
        }

        function updateVibration() {
            const amplitude = 0.2;
            const frequency = 2;

            switch (currentMode) {
                case 'symmetric-stretch':
                    // All H atoms move radially together
                    for (let i = 0; i < 4; i++) {
                        const direction = initialPositions[i].clone().normalize();
                        const displacement = amplitude * Math.sin(frequency * time);
                        hydrogens[i].position.copy(initialPositions[i].clone().add(direction.multiplyScalar(displacement)));
                    }
                    break;

                case 'asymmetric-stretch':
                    // Asymmetric stretching
                    const stretch1 = amplitude * Math.sin(frequency * time);
                    const stretch2 = -amplitude * Math.sin(frequency * time);
                    hydrogens[0].position.copy(initialPositions[0].clone().multiplyScalar(1 + stretch1));
                    hydrogens[1].position.copy(initialPositions[1].clone().multiplyScalar(1 + stretch2));
                    hydrogens[2].position.copy(initialPositions[2].clone().multiplyScalar(1 + stretch1 * 0.5));
                    hydrogens[3].position.copy(initialPositions[3].clone().multiplyScalar(1 + stretch2 * 0.5));
                    break;

                case 'bending-e':
                    // Bending mode E
                    const bendAngle = amplitude * Math.sin(frequency * time);
                    for (let i = 0; i < 4; i++) {
                        const pos = initialPositions[i].clone();
                        const axis = new THREE.Vector3(0, 1, 0);
                        pos.applyAxisAngle(axis, bendAngle * (i % 2 === 0 ? 1 : -1));
                        hydrogens[i].position.copy(pos);
                    }
                    break;

                case 'bending-f2':
                    // Bending mode F2
                    const bendAngle2 = amplitude * Math.sin(frequency * time);
                    for (let i = 0; i < 4; i++) {
                        const pos = initialPositions[i].clone();
                        const axis = new THREE.Vector3(1, 0, 0);
                        pos.applyAxisAngle(axis, bendAngle2 * (i < 2 ? 1 : -1));
                        hydrogens[i].position.copy(pos);
                    }
                    break;
            }

            updateBonds();
            updateDipoleVectors();
        }

        function resetAnimation() {
            time = 0;
            for (let i = 0; i < 4; i++) {
                hydrogens[i].position.copy(initialPositions[i]);
            }
            updateBonds();
            updateDipoleVectors();
        }

        function createIRSpectrum() {
            if (plotRendered) return;

            // Generate spectrum data
            const x = [];
            const y = [];
            const numPoints = 1000;
            const minWavenumber = 400;
            const maxWavenumber = 4000;

            for (let i = 0; i < numPoints; i++) {
                const wavenumber = minWavenumber + (maxWavenumber - minWavenumber) * i / numPoints;
                x.push(wavenumber);

                // Baseline with noise
                let transmittance = 0.95 + (Math.random() - 0.5) * 0.02;

                // Add peaks
                transmittance -= 0.8 * Math.exp(-Math.pow((wavenumber - 1306) / 30, 2));
                transmittance -= 0.8 * Math.exp(-Math.pow((wavenumber - 3019) / 30, 2));

                // Add fine structure
                for (let j = -3; j <= 3; j++) {
                    if (j !== 0) {
                        transmittance -= 0.1 * Math.exp(-Math.pow((wavenumber - 1306 - j * 10) / 5, 2));
                        transmittance -= 0.1 * Math.exp(-Math.pow((wavenumber - 3019 - j * 10) / 5, 2));
                    }
                }

                y.push(Math.max(0, transmittance));
            }

            // Create molecule images for annotations
            const moleculeImages = createMoleculeImages();

            const layout = {
                title: 'Methane IR Spectrum',
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [400, 4000]
                },
                yaxis: {
                    title: 'Transmittance',
                    range: [0, 1.1]
                },
                annotations: [
                    {
                        x: 1306,
                        y: 0.3,
                        xref: 'x',
                        yref: 'y',
                        text: 'ν F₂ 1306 cm⁻¹',
                        showarrow: true,
                        arrowhead: 2,
                        ax: 0,
                        ay: -40,
                        font: { color: 'red' }
                    },
                    {
                        x: 3019,
                        y: 0.3,
                        xref: 'x',
                        yref: 'y',
                        text: 'ν F₂ 3019 cm⁻¹',
                        showarrow: true,
                        arrowhead: 2,
                        ax: 0,
                        ay: -40,
                        font: { color: 'red' }
                    }
                ],
                images: [
                    {
                        source: moleculeImages.bend,
                        xref: 'x',
                        yref: 'y',
                        x: 1306,
                        y: 0.5,
                        sizex: 200,
                        sizey: 200,
                        xanchor: 'center',
                        yanchor: 'middle'
                    },
                    {
                        source: moleculeImages.stretch,
                        xref: 'x',
                        yref: 'y',
                        x: 3019,
                        y: 0.5,
                        sizex: 200,
                        sizey: 200,
                        xanchor: 'center',
                        yanchor: 'middle'
                    }
                ]
            };

            const data = [{
                x: x,
                y: y,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 1 }
            }];

            Plotly.newPlot('plotly-chart-container', data, layout);
            plotRendered = true;
        }

        function createMoleculeImages() {
            // This is a simplified version - in a real implementation, 
            // you would create actual three.js scenes and render them to canvas
            return {
                bend: 'data:image/svg+xml;base64,',
                stretch: 'data:image/svg+xml;base64,'
            };
        }

        // Event listeners
        document.getElementById('select-vibration-mode').addEventListener('change', function(e) {
            currentMode = e.target.value;

            if (currentMode === 'ir-spectrum') {
                document.getElementById('three-js-canvas-container').style.display = 'none';
                document.getElementById('plotly-chart-container').style.display = 'block';
                document.getElementById('description-area').style.display = 'none';

                document.getElementById('btn-play').disabled = true;
                document.getElementById('btn-pause').disabled = true;
                document.getElementById('btn-reset').disabled = true;

                createIRSpectrum();
            } else {
                document.getElementById('three-js-canvas-container').style.display = 'flex';
                document.getElementById('plotly-chart-container').style.display = 'none';
                document.getElementById('description-area').style.display = 'flex';

                document.getElementById('btn-play').disabled = false;
                document.getElementById('btn-pause').disabled = false;
                document.getElementById('btn-reset').disabled = false;

                resetAnimation();
                updateLabels();
            }
        });

        document.getElementById('btn-play').addEventListener('click', function() {
            isPlaying = true;
        });

        document.getElementById('btn-pause').addEventListener('click', function() {
            isPlaying = false;
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            isPlaying = false;
            resetAnimation();
        });

        function updateLabels() {
            const leftLabel = document.getElementById('label-left');
            const rightLabel = document.getElementById('label-right');

            switch (currentMode) {
                case 'symmetric-stretch':
                    leftLabel.textContent = 'stretching A₁ Raman active 2916 cm⁻¹';
                    leftLabel.className = 'blue-text';
                    rightLabel.textContent = 'no change in the molecular dipole moment';
                    rightLabel.className = 'blue-text';
                    break;

                case 'asymmetric-stretch':
                    leftLabel.textContent = 'stretching F₂ IR active 3019 cm⁻¹';
                    leftLabel.className = 'red-text';
                    rightLabel.textContent = 'change in the molecular dipole moment';
                    rightLabel.className = 'red-text';
                    break;

                case 'bending-e':
                    leftLabel.textContent = 'bending E Raman active 1534 cm⁻¹';
                    leftLabel.className = 'blue-text';
                    rightLabel.textContent = 'no change in the molecular dipole moment';
                    rightLabel.className = 'blue-text';
                    break;

                case 'bending-f2':
                    leftLabel.textContent = 'bending F₂ IR active 1306 cm⁻¹';
                    leftLabel.className = 'red-text';
                    rightLabel.textContent = 'change in the molecular dipole moment';
                    rightLabel.className = 'red-text';
                    break;
            }
        }

        // Initialize on load
        window.addEventListener('load', function() {
            initThreeJS();
            updateLabels();
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const container = document.getElementById('three-js-canvas-container');
            cameraLeft.aspect = (container.clientWidth / 2) / container.clientHeight;
            cameraLeft.updateProjectionMatrix();
            cameraRight.aspect = (container.clientWidth / 2) / container.clientHeight;
            cameraRight.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        });
    </script>
</body>
</html>
