</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time (seconds)</label>
            <input type="range" id="slider-time" min="0" max="100" step="0.1" value="9.8">
            <span id="span-time-value">9.8</span>
            <button id="btn-reset">+</button>
        </div>

        <div>
            <div class="section-title">Material Visualization</div>
            <canvas id="canvas-material-bar" width="600" height="80"></canvas>
        </div>

        <div>
            <div class="section-title">Stefan Distance vs Time</div>
            <div id="div-plot"></div>
        </div>
    </div>

    <script>
        // Get DOM elements
        const sliderTime = document.getElementById('slider-time');
        const spanTimeValue = document.getElementById('span-time-value');
        const btnReset = document.getElementById('btn-reset');
        const canvasMaterialBar = document.getElementById('canvas-material-bar');
        const divPlot = document.getElementById('div-plot');

        const ctx = canvasMaterialBar.getContext('2d');

        // Constants
        const CANVAS_WIDTH = 600;
        const CANVAS_HEIGHT = 80;
        const TOTAL_LENGTH_MM = 16;
        const PIX_PER_MM = CANVAS_WIDTH / TOTAL_LENGTH_MM;
        const GLASS_LENGTH_MM = 6;

        // Stefan distance function
        function stefanDistance(t) {
            return 0.155 * Math.sqrt(t);
        }

        // Draw material bar
        function drawMaterialBar(t) {
            const s = stefanDistance(t);

            // Clear canvas
            ctx.clearRect(0, 0, CANVAS_WIDTH, CANVAS_HEIGHT);

            // Draw glass (0-6mm)
            const glassGradient = ctx.createLinearGradient(0, 0, GLASS_LENGTH_MM * PIX_PER_MM, 0);
            glassGradient.addColorStop(0, '#FF0000');
            glassGradient.addColorStop(1, '#FFFFFF');
            ctx.fillStyle = glassGradient;
            ctx.fillRect(0, 20, GLASS_LENGTH_MM * PIX_PER_MM, 40);

            // Draw water (6mm to 6+s mm)
            if (s > 0) {
                ctx.fillStyle = '#00BFFF';
                ctx.fillRect(GLASS_LENGTH_MM * PIX_PER_MM, 20, s * PIX_PER_MM, 40);
            }

            // Draw ice (6+s mm to 16mm)
            ctx.fillStyle = '#003366';
            ctx.fillRect((GLASS_LENGTH_MM + s) * PIX_PER_MM, 20, 
                        (TOTAL_LENGTH_MM - GLASS_LENGTH_MM - s) * PIX_PER_MM, 40);

            // Draw axis
            ctx.strokeStyle = '#000';
            ctx.lineWidth = 2;
            ctx.beginPath();
            ctx.moveTo(0, CANVAS_HEIGHT - 10);
            ctx.lineTo(CANVAS_WIDTH, CANVAS_HEIGHT - 10);
            ctx.stroke();

            // Draw axis labels
            ctx.fillStyle = '#000';
            ctx.font = '12px Arial';
            ctx.textAlign = 'center';
            const labels = [0, 5, 10, 15];
            labels.forEach(label => {
                const x = label * PIX_PER_MM;
                ctx.fillText(label.toString(), x, CANVAS_HEIGHT - 2);
            });
        }

        // Initialize Plotly chart
        function initPlot() {
            // Generate data for the curve
            const timePoints = [];
            const distancePoints = [];
            for (let t = 0; t <= 100; t += 0.5) {
                timePoints.push(t);
                distancePoints.push(stefanDistance(t));
            }

            const trace = {
                x: timePoints,
                y: distancePoints,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: '#FFA500',
                    width: 3
                },
                name: 'Stefan distance'
            };

            const layout = {
                title: 'Stefan distance versus time',
                xaxis: {
                    title: 'time (s)',
                    range: [0, 100],
                    showgrid: true,
                    zeroline: true
                },
                yaxis: {
                    title: 'distance (mm)',
                    range: [0, 1.6],
                    showgrid: true,
                    zeroline: true
                },
                margin: {
                    l: 60,
                    r: 40,
                    t: 50,
                    b: 50
                },
                shapes: []
            };

            const config = {
                responsive: true,
                displayModeBar: false
            };

            Plotly.newPlot(divPlot, [trace], layout, config);
        }

        // Update plot with indicator line
        function updatePlotIndicator(t) {
            const s = stefanDistance(t);

            const shape = {
                type: 'line',
                x0: t,
                y0: 0,
                x1: t,
                y1: s,
                line: {
                    color: '#000000',
                    width: 2
                }
            };

            const update = {
                shapes: [shape]
            };

            Plotly.relayout(divPlot, update);
        }

        // Master update function
        function updateVisualization(t) {
            // Update time display
            spanTimeValue.textContent = parseFloat(t).toFixed(1);

            // Redraw material bar
            drawMaterialBar(t);

            // Update plot indicator
            updatePlotIndicator(t);
        }

        // Event handlers
        sliderTime.addEventListener('input', (event) => {
            const t = parseFloat(event.target.value);
            updateVisualization(t);
        });

        btnReset.addEventListener('click', () => {
            sliderTime.value = 0;
            // Trigger input event to update visualization
            sliderTime.dispatchEvent(new Event('input'));
        });

        // Initialize on page load
        document.addEventListener('DOMContentLoaded', () => {
            initPlot();
            updateVisualization(sliderTime.value);
        });
    </script>
</body>
</html>
