            border-radius: 8px;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
            padding: 15px;
            text-align: center;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <label for="date-slider">date</label>
            <input type="range" id="date-slider" min="0" max="364" value="340" step="1">
            <span id="date-display">Friday, 07 December</span>
            <button id="today-button">today</button>
        </div>

        <div id="visualization-panel">
            <div id="clocks-container">
                <div id="local-clock-group" class="clock-group">
                    <h2>Local Time</h2>
                    <div id="local-clock-wrapper" class="clock-wrapper">
                        <div id="local-clock-canvas"></div>
                        <div id="local-time-digital" class="digital-display">00:00:00</div>
                    </div>
                </div>

                <div id="solar-clock-group" class="clock-group">
                    <h2>Solar Time</h2>
                    <div id="solar-clock-wrapper" class="clock-wrapper">
                        <div id="solar-clock-canvas"></div>
                        <div id="solar-time-digital" class="digital-display">00:00:00</div>
                    </div>
                </div>
            </div>
        </div>

        <div id="offset-display">
            offset of solar time from local time: -46 minutes
        </div>
    </div>

    <script>
        // Global variables
        let userLatitude = 51.5;
        let userLongitude = 0;
        let localClockSketch, solarClockSketch;

        // Initialize the application
        function init() {
            // Get user location
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        userLatitude = position.coords.latitude;
                        userLongitude = position.coords.longitude;
                    },
                    (error) => {
                        console.log("Using default location (Greenwich)");
                    }
                );
            }

            // Initialize date display
            updateDateDisplay(340);

            // Set up event listeners
            document.getElementById('date-slider').addEventListener('input', (e) => {
                updateDateDisplay(parseInt(e.target.value));
            });

            document.getElementById('today-button').addEventListener('click', () => {
                const today = new Date();
                const dayOfYear = getDayOfYear(today);
                document.getElementById('date-slider').value = dayOfYear;
                updateDateDisplay(dayOfYear);
            });

            // Create p5.js sketches for clocks
            localClockSketch = new p5((p) => createClockSketch(p, 'local'), 'local-clock-canvas');
            solarClockSketch = new p5((p) => createClockSketch(p, 'solar'), 'solar-clock-canvas');

            // Start update loop
            setInterval(updateClocks, 1000);
            updateClocks(); // Initial update
        }

        function createClockSketch(p, type) {
            let timeObject;

            p.setup = function() {
                p.createCanvas(200, 200);
                p.angleMode(p.DEGREES);
            };

            p.draw = function() {
                p.clear();
                if (timeObject) {
                    drawClock(p, timeObject, type === 'local' ? '#E6E6FA' : '#FAFAD2');
                }
            };

            p.updateTime = function(time) {
                timeObject = time;
            };
        }

        function drawClock(p, time, faceColor) {
            const centerX = p.width / 2;
            const centerY = p.height / 2;
            const radius = 80;

            // Draw clock face
            p.stroke(0);
            p.strokeWeight(4);
            p.fill(faceColor);
            p.circle(centerX, centerY, radius * 2);

            // Draw hour markers
            p.strokeWeight(3);
            for (let i = 0; i < 12; i++) {
                const angle = i * 30 - 90;
                const x1 = centerX + p.cos(angle) * (radius - 10);
                const y1 = centerY + p.sin(angle) * (radius - 10);
                const x2 = centerX + p.cos(angle) * radius;
                const y2 = centerY + p.sin(angle) * radius;
                p.line(x1, y1, x2, y2);
            }

            // Draw minute markers
            p.strokeWeight(1);
            for (let i = 0; i < 60; i++) {
                if (i % 5 !== 0) {
                    const angle = i * 6 - 90;
                    const x1 = centerX + p.cos(angle) * (radius - 5);
                    const y1 = centerY + p.sin(angle) * (radius - 5);
                    const x2 = centerX + p.cos(angle) * radius;
                    const y2 = centerY + p.sin(angle) * radius;
                    p.line(x1, y1, x2, y2);
                }
            }

            // Draw numbers
            p.noStroke();
            p.fill(0);
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(16);
            for (let i = 1; i <= 12; i++) {
                const angle = i * 30 - 90;
                const x = centerX + p.cos(angle) * (radius - 25);
                const y = centerY + p.sin(angle) * (radius - 25);
                p.text(i, x, y);
            }

            // Calculate hand angles
            const hours = time.getHours() % 12;
            const minutes = time.getMinutes();
            const seconds = time.getSeconds();

            const hourAngle = (hours + minutes / 60) * 30 - 90;
            const minuteAngle = (minutes + seconds / 60) * 6 - 90;
            const secondAngle = seconds * 6 - 90;

            // Draw hour hand
            p.stroke(0);
            p.strokeWeight(6);
            p.line(centerX, centerY, 
                   centerX + p.cos(hourAngle) * (radius * 0.5),
                   centerY + p.sin(hourAngle) * (radius * 0.5));

            // Draw minute hand
            p.strokeWeight(4);
            p.line(centerX, centerY,
                   centerX + p.cos(minuteAngle) * (radius * 0.7),
                   centerY + p.sin(minuteAngle) * (radius * 0.7));

            // Draw second hand
            p.stroke(255, 0, 0);
            p.strokeWeight(2);
            p.line(centerX, centerY,
                   centerX + p.cos(secondAngle) * (radius * 0.8),
                   centerY + p.sin(secondAngle) * (radius * 0.8));

            // Draw center pin
            p.noStroke();
            p.fill(0);
            p.circle(centerX, centerY, 8);
        }

        function updateClocks() {
            const localTime = new Date();
            const dayOfYear = parseInt(document.getElementById('date-slider').value);

            // Update local clock
            localClockSketch.updateTime(localTime);
            document.getElementById('local-time-digital').textContent = formatTime(localTime);

            // Calculate solar time
            const solarTime = calculateSolarTime(localTime, dayOfYear);
            solarClockSketch.updateTime(solarTime);
            document.getElementById('solar-time-digital').textContent = formatTime(solarTime);

            // Update offset display
            const offsetMinutes = Math.round((solarTime.getTime() - localTime.getTime()) / 60000);
            document.getElementById('offset-display').textContent = 
                `offset of solar time from local time: ${offsetMinutes} minutes`;
        }

        function calculateSolarTime(localTime, dayOfYear) {
            // Calculate Equation of Time
            const B = (360 / 365.24) * (dayOfYear - 81);
            const EoT = 9.87 * Math.sin(2 * B * Math.PI / 180) - 
                       7.53 * Math.cos(B * Math.PI / 180) - 
                       1.5 * Math.sin(B * Math.PI / 180);

            // Calculate solar time
            const UTCTime = localTime.getTime() + localTime.getTimezoneOffset() * 60000;
            const timeOffsetMinutes = EoT + 4 * userLongitude;
            const solarTimeMilliseconds = UTCTime + timeOffsetMinutes * 60000;

            return new Date(solarTimeMilliseconds);
        }

        function formatTime(date) {
            const hours = String(date.getHours()).padStart(2, '0');
            const minutes = String(date.getMinutes()).padStart(2, '0');
            const seconds = String(date.getSeconds()).padStart(2, '0');
            return `${hours}:${minutes}:${seconds}`;
        }

        function getDayOfYear(date) {
            const start = new Date(date.getFullYear(), 0, 0);
            const diff = date - start;
            return Math.floor(diff / (1000 * 60 * 60 * 24)) - 1;
        }

        function updateDateDisplay(dayOfYear) {
            const year = new Date().getFullYear();
            const date = new Date(year, 0, 1);
            date.setDate(date.getDate() + dayOfYear);

            const options = { weekday: 'long', day: 'numeric', month: 'long' };
            const dateString = date.toLocaleDateString('en-US', options);
            document.getElementById('date-display').textContent = dateString;
        }

        // Initialize when page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
