    </style>
</head>
<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="input-steps">steps</label>
                <input type="number" id="input-steps" min="0" max="6" step="1" value="2">
            </div>
            <div>
                <label for="checkbox-zoom">zoom</label>
                <input type="checkbox" id="checkbox-zoom">
            </div>
            <div>
                <label for="select-color">color</label>
                <select id="select-color">
                    <option value="DarkRainbow">DarkRainbow</option>
                    <option value="SiennaTones">SiennaTones</option>
                    <option value="RustTones">RustTones</option>
                    <option value="FuchsiaTones">FuchsiaTones</option>
                    <option value="Grays">Grays</option>
                </select>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Color palettes
        const colorPalettes = {
            'DarkRainbow': ['#1E4363', '#2E6341'],
            'SiennaTones': ['#A0522D', '#CD853F', '#D2B48C'],
            'RustTones': ['#B7410E', '#000033', '#FFFFFF'],
            'FuchsiaTones': ['#C71585', '#000000', '#FFFFFF'],
            'Grays': ['#404040', '#808080', '#C0C0C0']
        };

        // Global variables
        let currentSteps = 2;
        let currentColor = 'DarkRainbow';
        let isZoomed = false;
        let p5Instance;

        // p5.js sketch
        function sketch(p) {
            p.setup = function() {
                let canvas = p.createCanvas(800, 600);
                canvas.parent('canvas-container');
                p.angleMode(p.DEGREES);
                p.noLoop();

                // Attach event listeners
                document.getElementById('input-steps').addEventListener('input', handleStepsChange);
                document.getElementById('checkbox-zoom').addEventListener('change', handleZoomChange);
                document.getElementById('select-color').addEventListener('change', handleColorChange);
            };

            p.draw = function() {
                p.background(255);
                p.translate(p.width / 2, p.height / 2);

                // Apply scaling based on zoom state
                if (isZoomed) {
                    let scaleFactor = Math.pow(Math.sqrt(7), currentSteps);
                    p.scale(scaleFactor);
                } else {
                    // Auto-scale to fit the entire fractal
                    let scaleFactor = Math.min(p.width, p.height) / (600 * Math.pow(Math.sqrt(7), currentSteps));
                    p.scale(scaleFactor);
                }

                // Get current color palette
                let palette = colorPalettes[currentColor];

                // Start drawing from central hexagon
                if (currentSteps === 0) {
                    drawCentralHexagon(p, palette);
                } else {
                    // Draw the initial 6 rhombi that form the hexagon
                    let initialSize = 100;
                    for (let i = 0; i < 6; i++) {
                        let angle = i * 60;
                        let x = Math.cos(p.radians(angle)) * initialSize;
                        let y = Math.sin(p.radians(angle)) * initialSize;
                        let orientation = Math.floor(i / 2);
                        drawTiling(p, currentSteps - 1, x, y, initialSize * (1 / Math.sqrt(7)), orientation, palette);
                    }

                    // Draw central hexagon for RustTones and FuchsiaTones
                    if (currentColor === 'RustTones' || currentColor === 'FuchsiaTones') {
                        p.push();
                        p.noStroke();
                        p.fill(palette[2]);
                        drawHexagon(p, 0, 0, initialSize * 0.8);
                        p.pop();
                    }
                }
            };
        }

        function drawTiling(p, level, centerX, centerY, size, orientation, palette) {
            if (level === 0) {
                drawRhombus(p, centerX, centerY, size, orientation, palette);
            } else {
                // Calculate the 7 child rhombi
                let scaleFactor = 1 / Math.sqrt(7);
                let childSize = size * scaleFactor;

                // Rotation angle for subdivision
                let rotAngle = 19.1066; // atan(sqrt(3)/5) in degrees

                // Define the relative positions and orientations of child rhombi
                let children = [
                    { x: size * Math.sqrt(3) / 2, y: size / 2, angle: 120 + rotAngle, orientation: (orientation + 1) % 3 },
                    { x: size * Math.sqrt(3), y: 0, angle: 240 + rotAngle, orientation: (orientation + 2) % 3 },
                    { x: size * Math.sqrt(3) / 2, y: -size / 2, angle: rotAngle, orientation: orientation },
                    { x: 0, y: -size, angle: 120 + rotAngle, orientation: (orientation + 1) % 3 },
                    { x: -size * Math.sqrt(3) / 2, y: -size / 2, angle: 240 + rotAngle, orientation: (orientation + 2) % 3 },
                    { x: -size * Math.sqrt(3), y: 0, angle: rotAngle, orientation: orientation },
                    { x: -size * Math.sqrt(3) / 2, y: size / 2, angle: 120 + rotAngle, orientation: (orientation + 1) % 3 }
                ];

                // Apply rotation transformation based on current orientation
                let totalAngle = orientation * 120;

                // Recursively draw child rhombi
                for (let child of children) {
                    let rotatedX = child.x * Math.cos(totalAngle) - child.y * Math.sin(totalAngle);
                    let rotatedY = child.x * Math.sin(totalAngle) + child.y * Math.cos(totalAngle);

                    drawTiling(
                        p,
                        level - 1,
                        centerX + rotatedX,
                        centerY + rotatedY,
                        childSize,
                        child.orientation,
                        palette
                    );
                }
            }
        }

        function drawRhombus(p, centerX, centerY, size, orientation, palette) {
            p.push();
            p.translate(centerX, centerY);
            p.rotate(orientation * 120);

            // Fill with color based on orientation
            let colorIndex = orientation % palette.length;
            p.fill(palette[colorIndex]);
            p.stroke(0);
            p.strokeWeight(1);

            // Draw rhombus
            let angle = 60;
            let x1 = size * Math.cos(p.radians(30));
            let y1 = size * Math.sin(p.radians(30));
            let x2 = size * Math.cos(p.radians(30 + angle));
            let y2 = size * Math.sin(p.radians(30 + angle));
            let x3 = size * Math.cos(p.radians(30 + 2 * angle));
            let y3 = size * Math.sin(p.radians(30 + 2 * angle));
            let x4 = size * Math.cos(p.radians(30 + 3 * angle));
            let y4 = size * Math.sin(p.radians(30 + 3 * angle));

            p.beginShape();
            p.vertex(x1, y1);
            p.vertex(x2, y2);
            p.vertex(x3, y3);
            p.vertex(x4, y4);
            p.endShape(p.CLOSE);

            // Draw diagonal lines inside rhombus
            p.stroke(255, 255, 255, 100);
            p.strokeWeight(0.5);
            p.line(x1, y1, x3, y3);
            p.line(x2, y2, x4, y4);

            p.pop();
        }

        function drawCentralHexagon(p, palette) {
            p.push();
            p.translate(0, 0);

            let size = 100;
            for (let i = 0; i < 6; i++) {
                let angle = i * 60;
                let orientation = Math.floor(i / 2);
                drawRhombus(p, 0, 0, size, orientation, palette);
            }

            p.pop();
        }

        function drawHexagon(p, x, y, radius) {
            p.beginShape();
            for (let i = 0; i < 6; i++) {
                let angle = i * 60;
                let vx = x + radius * Math.cos(p.radians(angle));
                let vy = y + radius * Math.sin(p.radians(angle));
                p.vertex(vx, vy);
            }
            p.endShape(p.CLOSE);
        }

        // Event handlers
        function handleStepsChange(event) {
            currentSteps = parseInt(event.target.value);
            p5Instance.redraw();
        }

        function handleZoomChange(event) {
            isZoomed = event.target.checked;
            p5Instance.redraw();
        }

        function handleColorChange(event) {
            currentColor = event.target.value;
            p5Instance.redraw();
        }

        // Initialize p5.js sketch
        p5Instance = new p5(sketch);
    </script>
</body>
</html>
