    <script>
        // State variables
        let magnetOrientations = [3, 2, 4, 5, 0]; // Default state: [↓, ↑, ⊙, ⊗, →]
        let magnetStrength = 5;
        let plotType = 'stream';

        // Orientation symbols mapping
        const orientationSymbols = ['→', '←', '↑', '↓', '⊙', '⊗'];

        // Orientation vectors mapping
        const orientationVectors = [
            {x: 1, y: 0, z: 0},   // →
            {x: -1, y: 0, z: 0},  // ←
            {x: 0, y: 1, z: 0},   // ↑
            {x: 0, y: -1, z: 0},  // ↓
            {x: 0, y: 0, z: 1},   // ⊙
            {x: 0, y: 0, z: -1}   // ⊗
        ];

        // p5.js variables
        let canvas;
        const L = 50; // Scaled size of a magnet in pixels (1cm = 50px)

        // Initialize orientation controls
        function initializeOrientationControls() {
            const container = document.getElementById('orientation-controls-container');
            for (let i = 0; i < 5; i++) {
                const controlDiv = document.createElement('div');
                controlDiv.className = 'magnet-control';
                controlDiv.id = `magnet-control-${i}`;

                const display = document.createElement('span');
                display.id = `magnet-orientation-display-${i}`;
                display.textContent = orientationSymbols[magnetOrientations[i]];

                const downBtn = document.createElement('button');
                downBtn.id = `magnet-orientation-down-${i}`;
                downBtn.className = 'orientation-btn';
                downBtn.textContent = '🔽';
                downBtn.addEventListener('click', () => decrementOrientation(i));

                const upBtn = document.createElement('button');
                upBtn.id = `magnet-orientation-up-${i}`;
                upBtn.className = 'orientation-btn';
                upBtn.textContent = '🔼';
                upBtn.addEventListener('click', () => incrementOrientation(i));

                controlDiv.appendChild(downBtn);
                controlDiv.appendChild(display);
                controlDiv.appendChild(upBtn);
                container.appendChild(controlDiv);
            }
        }

        function incrementOrientation(index) {
            magnetOrientations[index] = (magnetOrientations[index] + 1) % 6;
            updateOrientationDisplay(index);
            redrawVisualization();
        }

        function decrementOrientation(index) {
            magnetOrientations[index] = (magnetOrientations[index] - 1 + 6) % 6;
            updateOrientationDisplay(index);
            redrawVisualization();
        }

        function updateOrientationDisplay(index) {
            const display = document.getElementById(`magnet-orientation-display-${index}`);
            display.textContent = orientationSymbols[magnetOrientations[index]];
        }

        // Initialize event listeners
        function initializeEventListeners() {
            // Strength slider
            const slider = document.getElementById('slider-strength');
            const valueDisplay = document.getElementById('slider-strength-value');
            slider.addEventListener('input', (e) => {
                magnetStrength = parseInt(e.target.value);
                valueDisplay.textContent = magnetStrength;
                redrawVisualization();
            });

            // Plot type buttons
            const streamBtn = document.getElementById('btn-stream-plot');
            const densityBtn = document.getElementById('btn-density-plot');

            streamBtn.addEventListener('click', () => {
                plotType = 'stream';
                streamBtn.classList.add('active');
                densityBtn.classList.remove('active');
                redrawVisualization();
            });

            densityBtn.addEventListener('click', () => {
                plotType = 'density';
                densityBtn.classList.add('active');
                streamBtn.classList.remove('active');
                redrawVisualization();
            });
        }

        function redrawVisualization() {
            if (window.p5Instance) {
                window.p5Instance.redraw();
            }
        }

        // p5.js sketch
        const sketch = (p) => {
            p.setup = () => {
                canvas = p.createCanvas(800, 600);
                canvas.parent('p5-canvas');
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);
                p.push();
                p.translate(p.width / 2, p.height / 2);

                if (plotType === 'stream') {
                    drawStreamPlot(p);
                } else {
                    drawDensityPlot(p);
                }

                drawMagnets(p);
                p.pop();
            };
        };

        function calculateField(x, y) {
            let Bx = 0, By = 0;
            const scale = 0.01; // Convert pixels to meters for calculation

            for (let i = 0; i < 5; i++) {
                const ix = (i - 2) * L; // Position x of magnet i
                const iz = 0; // All magnets at z=0

                // Vector from dipole to point
                const rx = x - ix;
                const rz = y - iz;
                const r = p.createVector(rx, rz);
                const r_mag = p.mag(rx, rz, 0);

                if (r_mag < 5) continue; // Avoid singularity at dipole location

                // Get orientation vector
                const orient = orientationVectors[magnetOrientations[i]];
                const m = p.createVector(orient.x * magnetStrength, orient.y * magnetStrength, orient.z * magnetStrength);

                // Magnetic dipole field equation
                const m_dot_r = p.dot(m, r);
                const r5 = p.pow(r_mag, 5);
                const r3 = p.pow(r_mag, 3);

                const term1 = p5.Vector.mult(r, 3 * m_dot_r / r5);
                const term2 = p5.Vector.mult(m, 1 / r3);

                const B = p5.Vector.sub(term1, term2);

                Bx += B.x;
                By += B.z; // Using z component as y in 2D view
            }

            return p.createVector(Bx, By);
        }

        function drawStreamPlot(p) {
            p.stroke(0, 0, 255);
            p.strokeWeight(1);
            p.noFill();

            const spacing = 20;
            const steps = 500;
            const stepSize = 2;

            for (let sx = -p.width/2; sx < p.width/2; sx += spacing) {
                for (let sy = -p.height/2; sy < p.height/2; sy += spacing) {
                    let x = sx;
                    let y = sy;
                    let path = [{x, y}];

                    // Trace forward
                    for (let i = 0; i < steps; i++) {
                        const B = calculateField(x, y);
                        const mag = p.mag(B.x, B.y);
                        if (mag < 0.001) break;

                        x += (B.x / mag) * stepSize;
                        y += (B.y / mag) * stepSize;

                        if (p.abs(x) > p.width/2 || p.abs(y) > p.height/2) break;

                        path.push({x, y});
                    }

                    // Draw streamline
                    if (path.length > 10) {
                        p.beginShape();
                        for (const point of path) {
                            p.vertex(point.x, point.y);
                        }
                        p.endShape();

                        // Draw arrowheads
                        for (let i = 20; i < path.length; i += 40) {
                            const B = calculateField(path[i].x, path[i].y);
                            const mag = p.mag(B.x, B.y);
                            if (mag > 0.001) {
                                drawArrowhead(p, path[i].x, path[i].y, B.x/mag, B.y/mag);
                            }
                        }
                    }
                }
            }
        }

        function drawArrowhead(p, x, y, dx, dy) {
            const size = 5;
            const angle = p.atan2(dy, dx);

            p.push();
            p.translate(x, y);
            p.rotate(angle);
            p.beginShape();
            p.vertex(0, 0);
            p.vertex(-size, -size/2);
            p.vertex(-size, size/2);
            p.endShape(p.CLOSE);
            p.pop();
        }

        function drawDensityPlot(p) {
            // Calculate min/max field magnitude
            let minMag = Infinity;
            let maxMag = -Infinity;
            const sampleSize = 10;

            for (let x = -p.width/2; x < p.width/2; x += sampleSize) {
                for (let y = -p.height/2; y < p.height/2; y += sampleSize) {
                    const B = calculateField(x, y);
                    const mag = p.mag(B.x, B.y);
                    minMag = p.min(minMag, mag);
                    maxMag = p.max(maxMag, mag);
                }
            }

            // Draw density plot
            p.noStroke();
            const cellSize = 4;

            for (let x = -p.width/2; x < p.width/2; x += cellSize) {
                for (let y = -p.height/2; y < p.height/2; y += cellSize) {
                    const B = calculateField(x, y);
                    const mag = p.mag(B.x, B.y);
                    const normalized = (mag - minMag) / (maxMag - minMag);

                    // Custom colormap: yellow -> orange -> red -> blue
                    let c;
                    if (normalized < 0.5) {
                        c = p.lerpColor(
                            p.color(255, 255, 200), // light yellow
                            p.color(255, 100, 0),   // orange
                            normalized * 2
                        );
                    } else {
                        c = p.lerpColor(
                            p.color(255, 0, 0),     // red
                            p.color(0, 0, 100),     // dark blue
                            (normalized - 0.5) * 2
                        );
                    }

                    p.fill(c);
                    p.rect(x, y, cellSize, cellSize);
                }
            }
        }

        function drawMagnets(p) {
            p.fill(200);
            p.stroke(0);
            p.strokeWeight(2);

            for (let i = 0; i < 5; i++) {
                const x = (i - 2) * L;
                const y = 0;
                const size = 30;

                // Draw pseudo-3D cube (simple isometric view)
                p.beginShape();
                p.vertex(x - size/2, y - size/2);
                p.vertex(x + size/2, y - size/2);
                p.vertex(x + size/2 + 10, y - size/2 - 10);
                p.vertex(x - size/2 + 10, y - size/2 - 10);
                p.endShape(p.CLOSE);

                p.beginShape();
                p.vertex(x + size/2, y - size/2);
                p.vertex(x + size/2, y + size/2);
                p.vertex(x + size/2 + 10, y + size/2 - 10);
                p.vertex(x + size/2 + 10, y - size/2 - 10);
                p.endShape(p.CLOSE);

                p.beginShape();
                p.vertex(x - size/2, y - size/2);
                p.vertex(x - size/2, y + size/2);
                p.vertex(x + size/2, y + size/2);
                p.vertex(x + size/2, y - size/2);
                p.endShape(p.CLOSE);

                // Draw orientation symbol
                p.fill(0, 0, 255);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(20);
                p.text(orientationSymbols[magnetOrientations[i]], x, y);
                p.fill(200);
                p.stroke(0);
            }
        }

        // Initialize everything when DOM is loaded
        document.addEventListener('DOMContentLoaded', () => {
            initializeOrientationControls();
            initializeEventListeners();
            window.p5Instance = new p5(sketch);
        });
    </script>
</body>
</html>
