            pA = createVector(center.x, center.y - triangleSize * 0.433);
            pB = createVector(center.x - triangleSize * 0.5, center.y + triangleSize * 0.433);
            pC = createVector(center.x + triangleSize * 0.5, center.y + triangleSize * 0.433);

            // Setup event listeners
            document.getElementById('btn-diagram').addEventListener('click', () => switchView('diagram'));
            document.getElementById('btn-phases').addEventListener('click', () => switchView('phases'));
            document.getElementById('check-grid-lines').addEventListener('change', toggleGridLines);
            document.getElementById('check-tie-lines').addEventListener('change', toggleTieLines);
            document.getElementById('check-alpha-phase').addEventListener('change', toggleAlphaPhase);
            document.getElementById('check-beta-phase').addEventListener('change', toggleBetaPhase);

            canvas.canvas.addEventListener('click', handleCanvasClick);

            updateInfoBoxes();
        }

        function draw() {
            background(255);

            if (currentView === 'diagram') {
                drawDiagramView();
            } else {
                drawPhasesView();
            }
        }

        function drawDiagramView() {
            // Draw triangle
            stroke(0);
            strokeWeight(2);
            fill(255);
            triangle(pA.x, pA.y, pB.x, pB.y, pC.x, pC.y);

            // Draw grid lines if enabled
            if (showGridLines) {
                drawGridLines();
            }

            // Draw axes labels and ticks
            drawAxesLabels();
            drawAxesTicks();

            // Draw phase envelope
            drawPhaseEnvelope();

            // Draw tie lines if enabled
            if (showTieLines) {
                drawTieLines();
            }

            // Check if point is in two-phase region
            let inTwoPhase = isInTwoPhaseRegion(userPoint);

            if (inTwoPhase) {
                lettieLine = findTieLine(userPoint);
                alphaPoint = tieLine.alpha;
                betaPoint = tieLine.beta;

                // Draw tie line through user point
                stroke(0);
                strokeWeight(1);
                setLineDash([5, 5]);
                let pAlpha = barycentricToCartesian(alphaPoint);
                let pUser = barycentricToCartesian(userPoint);
                let pBeta = barycentricToCartesian(betaPoint);
                line(pAlpha.x, pAlpha.y, pUser.x, pUser.y);
                line(pUser.x, pUser.y, pBeta.x, pBeta.y);
                setLineDash([]);

                // Draw alpha phase point if enabled
                if (showAlphaPhase) {
                    fill(255, 165, 0);
                    noStroke();
                    circle(pAlpha.x, pAlpha.y, 12);

                    // Draw projection lines for alpha
                    drawProjectionLines(pAlpha, '#FFA500', [5, 5]);
                }

                // Draw beta phase point if enabled
                if (showBetaPhase) {
                    fill(255, 0, 255);
                    noStroke();
                    circle(pBeta.x, pBeta.y, 12);

                    // Draw projection lines for beta
                    drawProjectionLines(pBeta, '#FF00FF', [5, 5]);
                }
            } else {
                alphaPoint = null;
                betaPoint = null;
            }

            // Draw user point
            fill(0);
            noStroke();
            let pUser = barycentricToCartesian(userPoint);
            circle(pUser.x, pUser.y, 10);

            // Draw projection lines
            drawProjectionLines(pUser, '#000000', []);
        }

        function drawPhasesView() {
            // Draw triangle
            stroke(0);
            strokeWeight(2);

            // Fill two-phase region
            fill(212, 234, 252);
            drawPolygon([...alphaCurvePoints, ...betaCurvePoints.reverse()]);

            // Fill one-phase region
            fill(251, 229, 230);
            drawPolygon([pA, pB, pC]);
            fill(212, 234, 252);
            drawPolygon([...alphaCurvePoints, ...betaCurvePoints.reverse()]);

            // Draw phase boundary
            strokeWeight(2);
            drawPhaseBoundary();

            // Labels
            textAlign(CENTER, CENTER);
            fill(0);
            noStroke();
            textSize(16);
            let onePhaseLabel = barycentricToCartesian({xA: 0.8, xB: 0.1, xC: 0.1});
            text("one phase", onePhaseLabel.x, onePhaseLabel.y);

            let twoPhaseLabel = barycentricToCartesian({xA: 0.3, xB: 0.4, xC: 0.3});
            text("two phases", twoPhaseLabel.x, twoPhaseLabel.y);

            // Label plait point
            let plaitPoint = barycentricToCartesian({xA: 0.25, xB: 0.25, xC: 0.5});
            text("plait point", plaitPoint.x + 30, plaitPoint.y);

            // Draw user point
            fill(0);
            noStroke();
            let pUser = barycentricToCartesian(userPoint);
            circle(pUser.x, pUser.y, 10);
        }

        function drawGridLines() {
            stroke(200);
            strokeWeight(0.5);

            // Draw lines parallel to sides
            for (let i = 1; i < 10; i++) {
                let frac = i / 10;

                // Lines parallel to BC
                let p1 = p5.Vector.lerp(pA, pB, frac);
                let p2 = p5.Vector.lerp(pA, pC, frac);
                line(p1.x, p1.y, p2.x, p2.y);

                // Lines parallel to AB
                p1 = p5.Vector.lerp(pB, pA, frac);
                p2 = p5.Vector.lerp(pB, pC, frac);
                line(p1.x, p1.y, p2.x, p2.y);

                // Lines parallel to AC
                p1 = p5.Vector.lerp(pC, pA, frac);
                p2 = p5.Vector.lerp(pC, pB, frac);
                line(p1.x, p1.y, p2.x, p2.y);
            }
        }

        function drawAxesLabels() {
            textAlign(CENTER, CENTER);
            textSize(18);
            noStroke();

            fill(0, 136, 0);
            text("A", pA.x, pA.y - 25);

            fill(0, 0, 255);
            text("B", pB.x - 25, pB.y + 10);

            fill(255, 0, 0);
            text("C", pC.x + 25, pC.y + 10);
        }

        function drawAxesTicks() {
            strokeWeight(1);
            textSize(10);

            // Axis A-B
            for (let i = 1; i < 10; i++) {
                let frac = i / 10;
                let p = p5.Vector.lerp(pA, pB, frac);

                stroke(0, 136, 0);
                let dirB = p5.Vector.sub(pB, pA).normalize().mult(5);
                line(p.x - dirB.y, p.y + dirB.x, p.x + dirB.y, p.y - dirB.x);

                stroke(0, 0, 255);
                let dirA = p5.Vector.sub(pA, pB).normalize().mult(5);
                line(p.x - dirA.y, p.y + dirA.x, p.x + dirA.y, p.y - dirA.x);

                fill(0);
                noStroke();
                textAlign(CENTER, CENTER);
                text(frac.toFixed(1), p.x, p.y + 15);
            }
        }

        function drawPhaseEnvelope() {
            // Alpha curve (orange)
            stroke(255, 165, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let p of alphaCurvePoints) {
                let cp = barycentricToCartesian(p);
                vertex(cp.x, cp.y);
            }
            endShape();

            // Beta curve (magenta)
            stroke(255, 0, 255);
            strokeWeight(2);
            beginShape();
            for (let p of betaCurvePoints) {
                let cp = barycentricToCartesian(p);
                vertex(cp.x, cp.y);
            }
            endShape();
        }

        function drawTieLines() {
            stroke(150);
            strokeWeight(1);

            for (let tie of tieLines) {
                let p1 = barycentricToCartesian(tie.alpha);
                let p2 = barycentricToCartesian(tie.beta);
                line(p1.x, p1.y, p2.x, p2.y);
            }
        }

        function drawPhaseBoundary() {
            stroke(255, 165, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let p of alphaCurvePoints) {
                let cp = barycentricToCartesian(p);
                vertex(cp.x, cp.y);
            }
            endShape();

            stroke(255, 0, 255);
            beginShape();
            for (let p of betaCurvePoints) {
                let cp = barycentricToCartesian(p);
                vertex(cp.x, cp.y);
            }
            endShape();
        }

        function drawProjectionLines(point, color, dashPattern) {
            stroke(color);
            strokeWeight(1);
            setLineDash(dashPattern);

            // Projection to A axis
            let dir = p5.Vector.sub(pB, pC).normalize();
            let len = dist(point.x, point.y, 
                point.x - dir.y * (triangleSize * 0.6),
                point.y + dir.x * (triangleSize * 0.6));

            drawArrow(
                point.x - dir.y * (len * 0.8),
                point.y + dir.x * (len * 0.8),
                point.x, point.y,
                color
            );

            // Projection to B axis
            dir = p5.Vector.sub(pC, pA).normalize();
            len = dist(point.x, point.y,
                point.x - dir.y * (triangleSize * 0.6),
                point.y + dir.x * (triangleSize * 0.6));

            drawArrow(
                point.x - dir.y * (len * 0.8),
                point.y + dir.x * (len * 0.8),
                point.x, point.y,
                color
            );

            // Projection to C axis
            dir = p5.Vector.sub(pA, pB).normalize();
            len = dist(point.x, point.y,
                point.x - dir.y * (triangleSize * 0.6),
                point.y + dir.x * (triangleSize * 0.6));

            drawArrow(
                point.x - dir.y * (len * 0.8),
                point.y + dir.x * (len * 0.8),
                point.x, point.y,
                color
            );

            setLineDash([]);
        }

        function drawArrow(x1, y1, x2, y2, color) {
            stroke(color);
            strokeWeight(1);
            line(x1, y1, x2, y2);

            let angle = atan2(y2 - y1, x2 - x1);
            let arrowSize = 8;

            push();
            translate(x2, y2);
            rotate(angle);
            fill(color);
            noStroke();
            triangle(0, 0, -arrowSize, -arrowSize/2, -arrowSize, arrowSize/2);
            pop();
        }

        function drawPolygon(points) {
            beginShape();
            for (let p of points) {
                vertex(p.x, p.y);
            }
            endShape(CLOSE);
        }

        function barycentricToCartesian(bary) {
            return createVector(
                bary.xA * pA.x + bary.xB * pB.x + bary.xC * pC.x,
                bary.xA * pA.y + bary.xB * pB.y + bary.xC * pC.y
            );
        }

        function cartesianToBarycentric(x, y) {
            let denom = (pB.y - pC.y) * (pA.x - pC.x) + (pC.x - pB.x) * (pA.y - pC.y);
            let xA = ((pB.y - pC.y) * (x - pC.x) + (pC.x - pB.x) * (y - pC.y)) / denom;
            let xB = ((pC.y - pA.y) * (x - pC.x) + (pA.x - pC.x) * (y - pC.y)) / denom;
            let xC = 1 - xA - xB;

            return {xA: xA, xB: xB, xC: xC};
        }

        function isInTriangle(x, y) {
            let p = cartesianToBarycentric(x, y);
            return p.xA >= 0 && p.xB >= 0 && p.xC >= 0;
        }

        function isInTwoPhaseRegion(point) {
            // Simple check using point-in-polygon
            let polygon = [...alphaCurvePoints, ...betaCurvePoints.reverse(), alphaCurvePoints[0]];
            return pointInPolygon(point, polygon);
        }

        function pointInPolygon(point, polygon) {
            let x = point.xA;
            let y = point.xB;
            let inside = false;

            for (let i = 0, j = polygon.length - 1; i < polygon.length; j = i++) {
                let xi = polygon[i].xA, yi = polygon[i].xB;
                let xj = polygon[j].xA, yj = polygon[j].xB;

                let intersect = ((yi > y) !== (yj > y))
                    && (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
                if (intersect) inside = !inside;
            }

            return inside;
        }

        function findTieLine(point) {
            // Interpolate between tie lines to find the one containing the point
            // This is a simplified version - in practice, you'd use proper interpolation
            for (let tie of tieLines) {
                if (onTieLine(point, tie.alpha, tie.beta)) {
                    return tie;
                }
            }

            // Return closest tie line as approximation
            return tieLines[Math.floor(tieLines.length / 2)];
        }

        function onTieLine(point, alpha, beta) {
            let pa = barycentricToCartesian(alpha);
            let pb = barycentricToCartesian(beta);
            let pp = barycentricToCartesian(point);

            // Check if point is close to tie line
            let d = distToLine(pp.x, pp.y, pa.x, pa.y, pb.x, pb.y);
            return d < 20;
        }

        function distToLine(px, py, x1, y1, x2, y2) {
            let A = px - x1;
            let B = py - y1;
            let C = x2 - x1;
            let D = y2 - y1;

            let dot = A * C + B * D;
            let lenSq = C * C + D * D;
            let param = -1;

            if (lenSq !== 0) param = dot / lenSq;

            let xx, yy;

            if (param < 0) {
                xx = x1;
                yy = y1;
            } else if (param > 1) {
                xx = x2;
                yy = y2;
            } else {
                xx = x1 + param * C;
                yy = y1 + param * D;
            }

            let dx = px - xx;
            let dy = py - yy;
            return sqrt(dx * dx + dy * dy);
        }

        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }

        function handleCanvasClick(event) {
            let rect = canvas.canvas.getBoundingClientRect();
            let x = event.clientX - rect.left;
            let y = event.clientY - rect.top;

            if (isInTriangle(x, y)) {
                userPoint = cartesianToBarycentric(x, y);
                updateInfoBoxes();
            }
        }

        function switchView(view) {
            currentView = view;

            let diagramBtn = document.getElementById('btn-diagram');
            let phasesBtn = document.getElementById('btn-phases');

            if (view === 'diagram') {
                diagramBtn.classList.add('active');
                phasesBtn.classList.remove('active');
                document.querySelectorAll('.checkbox-group').forEach(el => el.style.display = 'flex');
                document.getElementById('info-box-main').style.display = 'block';
            } else {
                diagramBtn.classList.remove('active');
                phasesBtn.classList.add('active');
                document.querySelectorAll('.checkbox-group').forEach(el => el.style.display = 'none');
                document.getElementById('info-box-main').style.display = 'none';
                document.getElementById('info-box-alpha').style.display = 'none';
                document.getElementById('info-box-beta').style.display = 'none';
            }
        }

        function toggleGridLines() {
            showGridLines = document.getElementById('check-grid-lines').checked;
        }

        function toggleTieLines() {
            showTieLines = document.getElementById('check-tie-lines').checked;
        }

        function toggleAlphaPhase() {
            showAlphaPhase = document.getElementById('check-alpha-phase').checked;
            updateInfoBoxes();
        }

        function toggleBetaPhase() {
            showBetaPhase = document.getElementById('check-beta-phase').checked;
            updateInfoBoxes();
        }

        function updateInfoBoxes() {
            // Update main box
            let mainBox = document.getElementById('info-box-main');
            mainBox.innerHTML = `
                <h3>mass fractionsh3>
                <div class="formula">\\(x_A = ${userPoint.xA.toFixed(2)}\\)</div>
                <div class="formula">\\(x_B = ${userPoint.xB.toFixed(2)}\\)</div>
                <div class="formula">\\(x_C = ${userPoint.xC.toFixed(2)}\\)</div>
            `;

            // Check if in two-phase region
            let inTwoPhase = isInTwoPhaseRegion(userPoint);

            if (inTwoPhase && alphaPoint && betaPoint) {
                // Update alpha box
                let alphaBox = document.getElementById('info-box-alpha');
                if (showAlphaPhase) {
                    alphaBox.style.display = 'block';
                    alphaBox.innerHTML = `
                        <h3>mass fractionsh3>
                        <div class="formula">\\(x_A^\\alpha = ${alphaPoint.xA.toFixed(2)}\\)</div>
                        <div class="formula">\\(x_B^\\alpha = ${alphaPoint.xB.toFixed(2)}\\)</div>
                        <div class="formula">\\(x_C^\\alpha = ${alphaPoint.xC.toFixed(2)}\\)</div>
                        <div class="phase-label">alpha phasediv>
                    `;
                } else {
                    alphaBox.style.display = 'none';
                }

                // Update beta box
                let betaBox = document.getElementById('info-box-beta');
                if (showBetaPhase) {
                    betaBox.style.display = 'block';
                    betaBox.innerHTML = `
                        <h3>mass fractionsh3>
                        <div class="formula">\\(x_A^\\beta = ${betaPoint.xA.toFixed(2)}\\)</div>
                        <div class="formula">\\(x_B^\\beta = ${betaPoint.xB.toFixed(2)}\\)</div>
                        <div class="formula">\\(x_C^\\beta = ${betaPoint.xC.toFixed(2)}\\)</div>
                        <div class="phase-label">beta phasediv>
                    `;
                } else {
                    betaBox.style.display = 'none';
                }
            } else {
                document.getElementById('info-box-alpha').style.display = 'none';
                document.getElementById('info-box-beta').style.display = 'none';
            }

            // Update MathJax
            if (typeof MathJax !== 'undefined') {
                MathJax.typeset();
            }
        }

        function windowResized() {
            let container = document.getElementById('canvas-container');
            resizeCanvas(container.offsetWidth, container.offsetHeight);

            triangleSize = min(width, height) * 0.8;
            center = createVector(width / 2, height / 2);

            pA = createVector(center.x, center.y - triangleSize * 0.433);
            pB = createVector(center.x - triangleSize * 0.5, center.y + triangleSize * 0.433);
            pC = createVector(center.x + triangleSize * 0.5, center.y + triangleSize * 0.433);
        }
    </script>
