                            <td>3-Opt</td>
                            <td id="timing-3opt">0.009883</td>
                            <td id="result-3opt">3.38496</td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <div id="winner-display">Tie</div>
        </div>
    </div>

    <script>
        // Seeded pseudo-random number generator
        class SeededRandom {
            constructor(seed) {
                this.seed = seed;
            }

            next() {
                this.seed = (this.seed * 9301 + 49297) % 233280;
                return this.seed / 233280;
            }
        }

        // Distance calculation
        function distance(p1, p2) {
            return Math.sqrt((p1.x - p2.x) ** 2 + (p1.y - p2.y) ** 2);
        }

        // Tour length calculation
        function tourLength(tour) {
            let length = 0;
            for (let i = 0; i < tour.length; i++) {
                length += distance(tour[i], tour[(i + 1) % tour.length]);
            }
            return length;
        }

        // Nearest Neighbor heuristic
        function nearestNeighbor(points) {
            const n = points.length;
            const unvisited = new Set(points.map((_, i) => i));
            const tour = [];
            let current = 0;
            tour.push(points[current]);
            unvisited.delete(current);

            while (unvisited.size > 0) {
                let minDist = Infinity;
                let next = -1;

                for (const i of unvisited) {
                    const dist = distance(points[current], points[i]);
                    if (dist < minDist) {
                        minDist = dist;
                        next = i;
                    }
                }

                current = next;
                tour.push(points[current]);
                unvisited.delete(current);
            }

            return tour;
        }

        // 2-Opt algorithm
        function solveTwoOpt(tour) {
            let improved = true;
            const n = tour.length;

            while (improved) {
                improved = false;
                for (let i = 0; i < n - 1; i++) {
                    for (let j = i + 1; j < n; j++) {
                        const newTour = twoOptSwap(tour, i, j);
                        if (tourLength(newTour) < tourLength(tour)) {
                            tour = newTour;
                            improved = true;
                        }
                    }
                }
            }

            return [...tour];
        }

        function twoOptSwap(tour, i, j) {
            const newTour = [];
            for (let k = 0; k < i; k++) {
                newTour.push(tour[k]);
            }
            for (let k = j; k >= i; k--) {
                newTour.push(tour[k]);
            }
            for (let k = j + 1; k < tour.length; k++) {
                newTour.push(tour[k]);
            }
            return newTour;
        }

        // 3-Opt algorithm
        function solve3Opt(tour) {
            let improved = true;
            const n = tour.length;

            while (improved) {
                improved = false;
                for (let i = 0; i < n; i++) {
                    for (let j = i + 2; j < n; j++) {
                        for (let k = j + 2; k < n; k++) {
                            const newTour = threeOptSwap(tour, i, j, k);
                            if (tourLength(newTour) < tourLength(tour)) {
                                tour = newTour;
                                improved = true;
                            }
                        }
                    }
                }
            }

            return [...tour];
        }

        function threeOptSwap(tour, i, j, k) {
            const a = tour.slice(0, i);
            const b = tour.slice(i, j);
            const c = tour.slice(j, k);
            const d = tour.slice(k);

            const possibilities = [
                [...a, ...b, ...c, ...d],
                [...a, ...b.reverse(), ...c, ...d],
                [...a, ...b, ...c.reverse(), ...d],
                [...a, ...b.reverse(), ...c.reverse(), ...d],
                [...a, ...c, ...b, ...d],
                [...a, ...c.reverse(), ...b, ...d],
                [...a, ...c, ...b.reverse(), ...d]
            ];

            return possibilities.reduce((best, current) => 
                tourLength(current) < tourLength(best) ? current : best
            );
        }

        // Or-Opt algorithm
        function solveOrOpt(tour) {
            let improved = true;
            const n = tour.length;

            while (improved) {
                improved = false;
                for (let len = 1; len <= 3; len++) {
                    for (let i = 0; i < n - len; i++) {
                        for (let j = 0; j < n; j++) {
                            if (j >= i && j <= i + len) continue;

                            const newTour = orOptSwap(tour, i, j, len);
                            if (tourLength(newTour) < tourLength(tour)) {
                                tour = newTour;
                                improved = true;
                            }
                        }
                    }
                }
            }

            return [...tour];
        }

        function orOptSwap(tour, i, j, len) {
            const newTour = [];
            const segment = tour.slice(i, i + len);

            // Copy everything before i
            for (let k = 0; k < i; k++) {
                newTour.push(tour[k]);
            }

            // Copy everything from i to n, skipping the segment
            for (let k = i + len; k < tour.length; k++) {
                newTour.push(tour[k]);
            }

            // Insert segment at j
            const insertPos = j > i ? j - len : j;
            const temp = newTour.splice(0, insertPos);
            return [...temp, ...segment, ...newTour];
        }

        // CCA (Christofides) algorithm - simplified version
        function solveCCA(points) {
            const n = points.length;
            if (n <= 2) return [...points];

            // Start with nearest neighbor for simplicity
            const initialTour = nearestNeighbor(points);

            // Apply 2-opt improvements
            return solveTwoOpt(initialTour);
        }

        // OrZweig algorithm (2.5-Opt)
        function solveOrZweig(tour) {
            let improved = true;
            const n = tour.length;

            while (improved) {
                improved = false;

                // Try 2-opt moves
                for (let i = 0; i < n - 1; i++) {
                    for (let j = i + 1; j < n; j++) {
                        const newTour = twoOptSwap(tour, i, j);
                        if (tourLength(newTour) < tourLength(tour)) {
                            tour = newTour;
                            improved = true;
                        }
                    }
                }

                // Try some 3-opt moves (subset)
                if (!improved) {
                    for (let i = 0; i < n; i += 2) {
                        for (let j = i + 2; j < n; j += 2) {
                            const k = (j + Math.floor(n / 3)) % n;
                            if (k === i || k === j) continue;

                            const newTour = threeOptSwap(tour, Math.min(i, j, k), 
                                                         [i, j, k].sort((a, b) => a - b)[1], 
                                                         Math.max(i, j, k));
                            if (tourLength(newTour) < tourLength(tour)) {
                                tour = newTour;
                                improved = true;
                            }
                        }
                    }
                }
            }

            return [...tour];
        }

        // Global state
        let currentPoints = [];
        let currentResults = {};
        let leftSketch, rightSketch;

        // Generate random points
        function generatePoints(n, seed) {
            const rng = new SeededRandom(seed);
            const points = [];
            for (let i = 0; i < n; i++) {
                points.push({ x: rng.next(), y: rng.next() });
            }
            return points;
        }

        // Main calculation and rendering process
        function calculateAndRender() {
            const numPoints = parseInt(document.getElementById('slider-points').value);
            const seed = parseInt(document.getElementById('seed-input').value);

            // Generate points
            currentPoints = generatePoints(numPoints, seed);

            // Get initial tour
            const initialTour = nearestNeighbor(currentPoints);

            // Run all algorithms
            const algorithms = [
                { name: 'OrZweig', solver: solveOrZweig },
                { name: 'OrOpt', solver: solveOrOpt },
                { name: 'TwoOpt', solver: solveTwoOpt },
                { name: 'CCA', solver: solveCCA },
                { name: '3opt', solver: solve3Opt }
            ];

            currentResults = {};

            algorithms.forEach(algo => {
                const startTime = performance.now();
                const resultTour = algo.solver([...initialTour]);
                const endTime = performance.now();
                const duration = endTime - startTime;
                const length = tourLength(resultTour);

                currentResults[algo.name] = {
                    tour: resultTour,
                    time: duration / 1000, // Convert to seconds
                    length: length
                };

                // Update table
                document.getElementById(`timing-${algo.name.toLowerCase()}`).textContent = 
                    (duration / 1000).toFixed(6);
                document.getElementById(`result-${algo.name.toLowerCase()}`).textContent = 
                    length.toFixed(5);
            });

            // Determine winner
            const mathMethods = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA'];
            const minMathResult = Math.min(...mathMethods.map(method => currentResults[method].length));
            const result3opt = currentResults['3opt'].length;
            const epsilon = 1e-6;

            let winnerText = 'Tie';
            if (result3opt < minMathResult - epsilon) {
                winnerText = '3-Opt wins';
            } else if (minMathResult < result3opt - epsilon) {
                winnerText = 'Mathematica wins';
            }

            document.getElementById('winner-display').textContent = winnerText;

            // Update plots
            updatePlots();
        }

        // Update plots
        function updatePlots() {
            const selectedMethod = document.querySelector('input[name="math-method"]:checked').value;
            const mathTour = currentResults[selectedMethod].tour;
            const threeOptTour = currentResults['3opt'].tour;

            if (leftSketch) {
                leftSketch.remove();
            }
            if (rightSketch) {
                rightSketch.remove();
            }

            leftSketch = new p5(p => create_left_plot(p, currentPoints, mathTour), 'canvas-left-wrapper');
            rightSketch = new p5(p => create_right_plot(p, currentPoints, threeOptTour), 'canvas-right-wrapper');
        }

        // Create left plot
        function create_left_plot(p, points, tour) {
            p.setup = function() {
                const wrapper = document.getElementById('canvas-left-wrapper');
                const width = wrapper.offsetWidth;
                const height = wrapper.offsetHeight;
                p.createCanvas(width, height);
            };

            p.draw = function() {
                p.background(255);
                p.translate(50, p.height - 50);
                p.scale(p.width - 100, -(p.height - 100));

                // Draw axes
                p.stroke(0);
                p.strokeWeight(0.005);
                p.line(0, 0, 1, 0);
                p.line(0, 0, 0, 1);

                // Draw tick marks and labels
                p.textAlign(p.CENTER, p.TOP);
                p.textFont('12px Arial');
                for (let i = 0.2; i <= 1; i += 0.2) {
                    p.line(i, 0, i, 0.01);
                    p.text(i.toFixed(1), i, 0.02);
                    p.line(0, i, 0.01, i);
                    p.text(i.toFixed(1), -0.02, i);
                }

                // Draw tour
                p.stroke(0, 114, 178);
                p.strokeWeight(0.003);
                p.noFill();
                p.beginShape();
                for (const point of tour) {
                    p.vertex(point.x, point.y);
                }
                p.endShape(p.CLOSE);

                // Draw points
                p.fill('red');
                p.noStroke();
                for (const point of points) {
                    p.circle(point.x, point.y, 0.01);
                }

                p.noLoop();
            };
        }

        // Create right plot
        function create_right_plot(p, points, tour) {
            p.setup = function() {
                const wrapper = document.getElementById('canvas-right-wrapper');
                const width = wrapper.offsetWidth;
                const height = wrapper.offsetHeight;
                p.createCanvas(width, height);
            };

            p.draw = function() {
                p.background(255);
                p.translate(50, p.height - 50);
                p.scale(p.width - 100, -(p.height - 100));

                // Draw axes
                p.stroke(0);
                p.strokeWeight(0.005);
                p.line(0, 0, 1, 0);
                p.line(0, 0, 0, 1);

                // Draw tick marks and labels
                p.textAlign(p.CENTER, p.TOP);
                p.textFont('12px Arial');
                for (let i = 0.2; i <= 1; i += 0.2) {
                    p.line(i, 0, i, 0.01);
                    p.text(i.toFixed(1), i, 0.02);
                    p.line(0, i, 0.01, i);
                    p.text(i.toFixed(1), -0.02, i);
                }

                // Draw tour
                p.stroke(230, 159, 0);
                p.strokeWeight(0.003);
                p.noFill();
                p.beginShape();
                for (const point of tour) {
                    p.vertex(point.x, point.y);
                }
                p.endShape(p.CLOSE);

                // Draw points
                p.fill('red');
                p.noStroke();
                for (const point of points) {
                    p.circle(point.x, point.y, 0.01);
                }

                p.noLoop();
            };
        }

        // Event listeners
        document.getElementById('btn-random-set').addEventListener('click', function() {
            const newSeed = Math.floor(Math.random() * 99999) + 1;
            document.getElementById('seed-input').value = newSeed;
            calculateAndRender();
        });

        document.getElementById('btn-reset-seed').addEventListener('click', function() {
            document.getElementById('seed-input').value = 0;
            calculateAndRender();
        });

        document.getElementById('slider-points').addEventListener('change', calculateAndRender);

        document.querySelectorAll('input[name="math-method"]').forEach(radio => {
            radio.addEventListener('change', function() {
                document.getElementById('left-plot-title').textContent = this.value;
                updatePlots();
            });
        });

        // Initialize
        calculateAndRender();
    </script>
</body>
</html>
