<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Automaton Visualization</title>
  <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
  <style>
    body {
      font-family: Arial, sans-serif;
      margin: 0;
      padding: 20px;
      background-color: #f5f5f5;
    }

    #main-container {
      display: flex;
      border: 2px solid #333;
      background-color: white;
      max-width: 1200px;
      margin: 0 auto;
      box-shadow: 0 4px 6px rgba(0,0,0,0.1);
    }

    #control-panel {
      width: 300px;
      padding: 20px;
      background-color: #f9f9f9;
      border-right: 1px solid #ddd;
    }

    #control-panel > div {
      margin-bottom: 15px;
    }

    label {
      display: block;
      margin-bottom: 5px;
      font-weight: bold;
      color: #333;
    }

    input[type="range"] {
      width: 100%;
      margin-bottom: 5px;
    }

    span {
      font-size: 14px;
      color: #666;
    }

    input[type="checkbox"] {
      margin-right: 8px;
    }

    #visualization-area {
      flex: 1;
      padding: 20px;
      display: flex;
      flex-direction: column;
      align-items: center;
    }

    #info-text {
      margin-bottom: 20px;
      text-align: center;
      color: #333;
      font-size: 16px;
    }

    #canvas-container {
      border: 1px solid #ccc;
    }
  </style>
</head>
<body>
  <div id="main-container">
    <!-- Left Column: Control Panel -->
    <div id="control-panel">
      <!-- Initialization Slider -->
      <div>
        <label for="slider-initialization">initialization</label>
        <input type="range" id="slider-initialization" min="0" max="65535" step="1" value="462">
        <span id="value-initialization">+ 462</span>
      </div>

      <!-- Max Steps Slider -->
      <div>
        <label for="slider-max-steps">max steps</label>
        <input type="range" id="slider-max-steps" min="1" max="50" step="1" value="9">
        <span id="value-max-steps">+ 9</span>
      </div>

      <!-- Checkboxes -->
      <div>
        <input type="checkbox" id="checkbox-cycle4">
        <label for="checkbox-cycle4">length 4 cycle</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-cycle2">
        <label for="checkbox-cycle2">length 2 cycles</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-cycle1">
        <label for="checkbox-cycle1">length 1 cycle points</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-all-points">
        <label for="checkbox-all-points">all map points</label>
      </div>
      <div>
        <input type="checkbox" id="checkbox-modify">
        <label for="checkbox-modify">modify initialization</label>
      </div>
    </div>

    <!-- Right Column: Visualization Area -->
    <div id="visualization-area">
      <p id="info-text">result of initial condition 462 does not repeat in 10 steps</p>
      <div id="canvas-container"></div>
    </div>
  </div>

  <script>
    // Global variables
    let canvas;
    let initializationSlider;
    let maxStepsSlider;
    let checkboxes = {};
    let infoText;
    let sequence = [];
    let cycleFound = false;
    let cycleLength = 0;
    let cycleStart = 0;

    // Hardcoded data
    const length2Cycles = [
      [10922, 21845], [13107, 26214], [13653, 27306], 
      [15339, 30678], [21844, 43691], [26213, 52427], 
      [27305, 54611], [30677, 61355], [43690, 54613]
    ];

    const length4Cycle = [43690, 54612, 32768, 49152];

    // Pre-calculate fixed points (length 1 cycles)
    const fixedPoints = [];
    for (let n = 0; n < 65536; n++) {
      if (f(n) === n) {
        fixedPoints.push(n);
      }
    }

    // BitReverse15 function: reverses the lowest 15 bits
    function BitReverse15(x) {
      let result = 0;
      for (let i = 0; i < 15; i++) {
        result = (result << 1) | (x & 1);
        x >>= 1;
      }
      return result;
    }

    // Automaton function
    function f(n) {
      return (n + BitReverse15(Math.floor(n / 2))) % 65536;
    }

    // Convert n to (x, y) coordinates
    function nToCoords(n) {
      const x = n & 255;
      const y = (n >> 8) & 255;
      return { x, y };
    }

    // Generate sequence and detect cycles
    function generateSequence() {
      sequence = [];
      const n0 = parseInt(initializationSlider.value);
      const maxSteps = parseInt(maxStepsSlider.value);

      let current = n0;
      sequence.push(current);

      // Generate sequence
      for (let i = 0; i <= maxSteps; i++) {
        current = f(current);
        sequence.push(current);
      }

      // Detect cycles
      cycleFound = false;
      cycleLength = 0;
      cycleStart = 0;

      for (let k = 1; k <= maxSteps + 1; k++) {
        for (let j = 0; j <= maxSteps + 1 - k; j++) {
          if (sequence[j] === sequence[j + k]) {
            cycleFound = true;
            cycleLength = k;
            cycleStart = j;
            return;
          }
        }
      }
    }

    // Update info text
    function updateInfoText() {
      const n0 = parseInt(initializationSlider.value);
      const maxSteps = parseInt(maxStepsSlider.value);

      if (cycleFound) {
        infoText.textContent = `result of initial condition ${n0} repeats in ${cycleLength} steps`;
      } else {
        infoText.textContent = `result of initial condition ${n0} does not repeat in ${maxSteps + 1} steps`;
      }
    }

    // p5.js setup
    function setup() {
      canvas = createCanvas(512, 512);
      canvas.parent('canvas-container');
      noLoop();

      // Get references to elements
      initializationSlider = document.getElementById('slider-initialization');
      maxStepsSlider = document.getElementById('slider-max-steps');
      infoText = document.getElementById('info-text');

      // Get checkbox references
      checkboxes.cycle4 = document.getElementById('checkbox-cycle4');
      checkboxes.cycle2 = document.getElementById('checkbox-cycle2');
      checkboxes.cycle1 = document.getElementById('checkbox-cycle1');
      checkboxes.allPoints = document.getElementById('checkbox-all-points');
      checkboxes.modify = document.getElementById('checkbox-modify');

      // Add event listeners
      initializationSlider.addEventListener('input', function() {
        document.getElementById('value-initialization').textContent = '+ ' + this.value;
        generateSequence();
        updateInfoText();
        redraw();
      });

      maxStepsSlider.addEventListener('input', function() {
        document.getElementById('value-max-steps').textContent = '+ ' + this.value;
        generateSequence();
        updateInfoText();
        redraw();
      });

      Object.values(checkboxes).forEach(checkbox => {
        checkbox.addEventListener('change', function() {
          redraw();
        });
      });

      // Initial setup
      generateSequence();
    }

    // p5.js draw
    function draw() {
      // Background
      background(224, 240, 224); // #e0f0e0

      // Set up coordinate system
      const scale = 512 / 256; // Map 256x256 to 512x512

      // Draw "all map points" overlay
      if (checkboxes.allPoints.checked) {
        stroke(128, 128, 128); // #808080
        strokeWeight(1);

        // Sample points for performance (every 16th point)
        for (let n = 0; n < 65536; n += 16) {
          const next = f(n);
          const coords = nToCoords(next);
          point(coords.x * scale, coords.y * scale);
        }
      }

      // Draw automaton path
      if (sequence.length > 1) {
        const points = sequence.map(n => nToCoords(n));

        // Draw polyline
        stroke(128, 0, 128); // #800080
        strokeWeight(2);
        noFill();
        beginShape();
        points.forEach(p => vertex(p.x * scale, p.y * scale));
        endShape();

        // Draw start point (red)
        fill(255, 0, 0); // #FF0000
        noStroke();
        const start = points[0];
        circle(start.x * scale, start.y * scale, 8);

        // Draw end point (blue)
        fill(0, 0, 255); // #0000FF
        const end = points[points.length - 1];
        circle(end.x * scale, end.y * scale, 8);
      }

      // Draw length 1 cycle points
      if (checkboxes.cycle1.checked) {
        fill(0, 255, 255); // #00FFFF
        noStroke();
        fixedPoints.forEach(n => {
          const coords = nToCoords(n);
          circle(coords.x * scale, coords.y * scale, 6);
        });
      }

      // Draw length 2 cycles
      if (checkboxes.cycle2.checked) {
        stroke(0, 128, 0); // #008000
        strokeWeight(2);

        length2Cycles.forEach(pair => {
          const p1 = nToCoords(pair[0]);
          const p2 = nToCoords(pair[1]);
          line(p1.x * scale, p1.y * scale, p2.x * scale, p2.y * scale);
        });
      }

      // Draw length 4 cycle
      if (checkboxes.cycle4.checked) {
        stroke(0, 128, 0); // #008000
        strokeWeight(2);

        // Draw cycle path
        const cyclePoints = length4Cycle.map(n => nToCoords(n));

        // Connect points in cycle
        for (let i = 0; i < 4; i++) {
          const p1 = cyclePoints[i];
          const p2 = cyclePoints[(i + 1) % 4];
          line(p1.x * scale, p1.y * scale, p2.x * scale, p2.y * scale);
        }

        // Draw bounding box
        noFill();
        stroke(255, 165, 0); // #FFA500
        strokeWeight(2);

        const xs = cyclePoints.map(p => p.x);
        const ys = cyclePoints.map(p => p.y);
        const minX = Math.min(...xs);
        const maxX = Math.max(...xs);
        const minY = Math.min(...ys);
        const maxY = Math.max(...ys);

        rect(minX * scale, minY * scale, (maxX - minX) * scale, (maxY - minY) * scale);

        // Draw perpendicular bisectors
        stroke(0, 128, 0);
        strokeWeight(1);

        for (let i = 0; i < 4; i++) {
          const p1 = cyclePoints[i];
          const p2 = cyclePoints[(i + 1) % 4];

          // Midpoint
          const midX = (p1.x + p2.x) / 2;
          const midY = (p1.y + p2.y) / 2;

          // Perpendicular direction
          const dx = p2.x - p1.x;
          const dy = p2.y - p1.y;
          const perpX = -dy;
          const perpY = dx;

          // Normalize and extend
          const len = Math.sqrt(perpX * perpX + perpY * perpY);
          if (len > 0) {
            const extendLen = 30 / scale;
            const lineStartX = midX - (perpX / len) * extendLen;
            const lineStartY = midY - (perpY / len) * extendLen;
            const lineEndX = midX + (perpX / len) * extendLen;
            const lineEndY = midY + (perpY / len) * extendLen;

            line(lineStartX * scale, lineStartY * scale, lineEndX * scale, lineEndY * scale);
          }
        }
      }
    }
  </script>
</body>
</html>
