            createEquator();

            // Create ecliptic
            createEcliptic();

            // Create stars
            createStars();

            // Create zenith
            createZenith();

            // Create First Point of Aries
            createAriesPoint();

            // Create polar axis
            createPolarAxis();

            // Create pole labels
            createPoleLabels();

            // Set up UI interactions
            setupUI();

            // Handle window resize
            window.addEventListener('resize', onWindowResize, false);

            // Start animation
            animate();
        }

        function createCelestialSphere() {
            const geometry = new THREE.SphereGeometry(SPHERE_RADIUS, 64, 64);

            // Create gradient colors
            const colors = [];
            const color1 = new THREE.Color(0xff6b35); // Orange/red
            const color2 = new THREE.Color(0x4169e1); // Blueish

            for (let i = 0; i <= geometry.attributes.position.count; i++) {
                const y = geometry.attributes.position.getY(i);
                const t = (y + SPHERE_RADIUS) / (2 * SPHERE_RADIUS);
                const color = color1.clone().lerp(color2, t);
                colors.push(color.r, color.g, color.b);
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            const material = new THREE.MeshBasicMaterial({
                vertexColors: true,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });

            sphere = new THREE.Mesh(geometry, material);
            scene.add(sphere);
        }

        function createEquator() {
            const points = [];
            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * Math.PI * 2;
                points.push(new THREE.Vector3(
                    Math.cos(angle) * SPHERE_RADIUS,
                    0,
                    Math.sin(angle) * SPHERE_RADIUS
                ));
            }

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0x4169e1 });
            equatorLine = new THREE.Line(geometry, material);
            scene.add(equatorLine);
        }

        function createEcliptic() {
            const points = [];
            const inclination = 23.5 * Math.PI / 180;

            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * Math.PI * 2;
                const y = Math.sin(inclination) * Math.sin(angle) * SPHERE_RADIUS;
                const xzRadius = Math.cos(inclination) * SPHERE_RADIUS;
                const x = Math.cos(angle) * xzRadius;
                const z = Math.sin(angle) * xzRadius;
                points.push(new THREE.Vector3(x, y, z));
            }

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0x9370db }); // Reddish-purple
            eclipticLine = new THREE.Line(geometry, material);
            scene.add(eclipticLine);
        }

        function createStars() {
            const positions = [];

            starsData.forEach(star => {
                const ra = star.ra * Math.PI / 12; // Convert hours to radians
                const dec = star.dec * Math.PI / 180; // Convert degrees to radians

                const x = SPHERE_RADIUS * Math.cos(dec) * Math.cos(ra);
                const y = SPHERE_RADIUS * Math.sin(dec);
                const z = SPHERE_RADIUS * Math.cos(dec) * Math.sin(ra);

                positions.push(x, y, z);
            });

            const geometry = new THREE.BufferGeometry();
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(positions, 3));

            const material = new THREE.PointsMaterial({
                color: 0xffffff,
                size: 0.2,
                sizeAttenuation: true
            });

            starsPoints = new THREE.Points(geometry, material);
            scene.add(starsPoints);
        }

        function createZenith() {
            const geometry = new THREE.SphereGeometry(0.3, 16, 16);
            const material = new THREE.MeshBasicMaterial({ color: 0x000000 });
            zenithPoint = new THREE.Mesh(geometry, material);

            // Create line from origin to zenith
            const lineGeometry = new THREE.BufferGeometry();
            const lineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });

            zenithLine = new THREE.Line(lineGeometry, lineMaterial);

            const zenithGroup = new THREE.Group();
            zenithGroup.add(zenithPoint);
            zenithGroup.add(zenithLine);
            scene.add(zenithGroup);

            updateZenithPosition();
        }

        function updateZenithPosition() {
            const ra = parseFloat(document.getElementById('slider-ra').value) * Math.PI / 180;
            const dec = parseFloat(document.getElementById('slider-dec').value) * Math.PI / 180;

            const x = SPHERE_RADIUS * Math.cos(dec) * Math.cos(ra);
            const y = SPHERE_RADIUS * Math.sin(dec);
            const z = SPHERE_RADIUS * Math.cos(dec) * Math.sin(ra);

            zenithPoint.position.set(x, y, z);

            const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, 0, 0),
                new THREE.Vector3(x, y, z)
            ]);
            zenithLine.geometry.dispose();
            zenithLine.geometry = lineGeometry;
        }

        function createAriesPoint() {
            const geometry = new THREE.SphereGeometry(0.4, 16, 16);
            const material = new THREE.MeshBasicMaterial({ color: 0xffd700 }); // Yellow
            ariesPoint = new THREE.Mesh(geometry, material);
            ariesPoint.position.set(SPHERE_RADIUS, 0, 0);
            scene.add(ariesPoint);
        }

        function createPolarAxis() {
            const geometry = new THREE.BufferGeometry().setFromPoints([
                new THREE.Vector3(0, -SPHERE_RADIUS * 1.2, 0),
                new THREE.Vector3(0, SPHERE_RADIUS * 1.2, 0)
            ]);
            const material = new THREE.LineBasicMaterial({ color: 0x000000 });
            polarAxis = new THREE.Line(geometry, material);
            scene.add(polarAxis);
        }

        function createPoleLabels() {
            const northDiv = document.createElement('div');
            northDiv.className = 'label';
            northDiv.textContent = 'N';
            northDiv.style.color = 'white';
            northDiv.style.fontSize = '20px';
            northDiv.style.fontWeight = 'bold';
            northLabel = new THREE.CSS2DObject(northDiv);
            northLabel.position.set(0, SPHERE_RADIUS * 1.1, 0);
            scene.add(northLabel);

            const southDiv = document.createElement('div');
            southDiv.className = 'label';
            southDiv.textContent = 'S';
            southDiv.style.color = 'white';
            southDiv.style.fontSize = '20px';
            southDiv.style.fontWeight = 'bold';
            southLabel = new THREE.CSS2DObject(southDiv);
            southLabel.position.set(0, -SPHERE_RADIUS * 1.1, 0);
            scene.add(southLabel);
        }

        function setupUI() {
            // Family dropdown change handler
            const familySelect = document.getElementById('select-family');
            familySelect.addEventListener('change', updateConstellationOptions);

            // Initial population of constellation options
            updateConstellationOptions();

            // Constellation dropdown change handler
            const constellationSelect = document.getElementById('select-constellation');
            constellationSelect.addEventListener('change', updateSelectedConstellation);

            // Checkbox handlers
            document.getElementById('check-sphere').addEventListener('change', (e) => {
                sphere.visible = e.target.checked;
            });

            document.getElementById('check-stars').addEventListener('change', (e) => {
                starsPoints.visible = e.target.checked;
            });

            document.getElementById('check-equator').addEventListener('change', (e) => {
                equatorLine.visible = e.target.checked;
            });

            document.getElementById('check-ecliptic').addEventListener('change', (e) => {
                eclipticLine.visible = e.target.checked;
            });

            document.getElementById('check-zenith').addEventListener('change', (e) => {
                zenithPoint.parent.visible = e.target.checked;
            });

            document.getElementById('check-aries').addEventListener('change', (e) => {
                ariesPoint.visible = e.target.checked;
            });

            // Slider handlers
            const raSlider = document.getElementById('slider-ra');
            const decSlider = document.getElementById('slider-dec');

            raSlider.addEventListener('input', (e) => {
                document.getElementById('ra-value').textContent = e.target.value + '°';
                updateZenithPosition();
            });

            decSlider.addEventListener('input', (e) => {
                document.getElementById('dec-value').textContent = e.target.value + '°';
                updateZenithPosition();
            });
        }

        function updateConstellationOptions() {
            const familySelect = document.getElementById('select-family');
            const constellationSelect = document.getElementById('select-constellation');
            const selectedFamily = familySelect.value;

            // Clear current options
            constellationSelect.innerHTML = '<option value="None">None</option>';

            if (selectedFamily === 'all') {
                // Add all constellations
                Object.values(constellationData).flat().forEach(constellation => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.textContent = constellation;
                    constellationSelect.appendChild(option);
                });
                constellationSelect.value = 'None';
            } else {
                // Add constellations from selected family
                constellationData[selectedFamily].forEach(constellation => {
                    const option = document.createElement('option');
                    option.value = constellation;
                    option.textContent = constellation;
                    constellationSelect.appendChild(option);
                });
                // Select first constellation
                constellationSelect.value = constellationData[selectedFamily][0] || 'None';
            }

            updateSelectedConstellation();
        }

        function updateSelectedConstellation() {
            const constellationSelect = document.getElementById('select-constellation');
            const selectedConstellation = constellationSelect.value;

            // Remove previous constellation lines
            if (selectedConstellationLines) {
                scene.remove(selectedConstellationLines);
                selectedConstellationLines.geometry.dispose();
                selectedConstellationLines.material.dispose();
            }

            if (selectedConstellation !== 'None' && constellationLines[selectedConstellation]) {
                const points = [];
                constellationLines[selectedConstellation].forEach(star => {
                    const ra = star.ra * Math.PI / 12;
                    const dec = star.dec * Math.PI / 180;

                    const x = SPHERE_RADIUS * Math.cos(dec) * Math.cos(ra);
                    const y = SPHERE_RADIUS * Math.sin(dec);
                    const z = SPHERE_RADIUS * Math.cos(dec) * Math.sin(ra);

                    points.push(new THREE.Vector3(x, y, z));
                });

                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0xff0000 });
                selectedConstellationLines = new THREE.Line(geometry, material);
                scene.add(selectedConstellationLines);
            }
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');

            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();

            renderer.setSize(container.clientWidth, container.clientHeight);
            labelRenderer.setSize(container.clientWidth, container.clientHeight);
        }

        function animate() {
            requestAnimationFrame(animate);

            controls.update();
            renderer.render(scene, camera);
            labelRenderer.render(scene, camera);
        }

        // Initialize the application
        init();
    </script>
</body>
</html>
