    </style>
</head>
<body>
    <div id="app-container">
        <!-- Left side: Control Panel -->
        <div id="control-panel">
            <!-- Coefficients Section -->
            <h3>coefficients</h3>
            <div class="control-group">
                <label for="slider-c1">c<sub>1</sub></label>
                <input type="range" id="slider-c1" min="-2" max="2" step="0.1" value="0">
                <span id="c1-value-display">0</span>
                <button id="reset-c1">+</button>
            </div>
            <div class="control-group">
                <label for="slider-c2">c<sub>2</sub></label>
                <input type="range" id="slider-c2" min="-2" max="2" step="0.1" value="0">
                <span id="c2-value-display">0</span>
                <button id="reset-c2">+</button>
            </div>

            <!-- Clipping Section -->
            <h3>clipping</h3>
            <div class="control-group">
                <input type="checkbox" id="checkbox-clipping">
                <label for="checkbox-clipping" style="display: inline; margin-left: 5px; font-weight: 500;">clipping</label>
            </div>
            <div id="level-control-group" class="control-group" style="display: none;">
                <label for="slider-level">level</label>
                <input type="range" id="slider-level" min="0" max="1.5" step="0.05" value="0.7">
                <span id="level-value-display">0.7</span>
            </div>

            <!-- DFT Section -->
            <h3>DFT</h3>
            <div class="control-group">
                <label>scale</label>
                <button id="btn-linear" class="active">linear</button>
                <button id="btn-decibel">decibel</button>
            </div>
        </div>

        <!-- Right side: Visualization Area -->
        <div id="visualization-area">
            <div id="plot-signal"></div>
            <div id="plot-spectrum"></div>
        </div>
    </div>

    <script>
        // Global variables
        let normalizedMag = [];
        let currentScale = 'linear';

        // Constants
        const f = 1000;      // Input signal frequency (Hz)
        const A = 1;         // Input signal amplitude
        const N = 2048;      // Number of samples
        const fs = 32000;    // Sampling frequency (Hz)

        // FFT function (Radix-2 implementation)
        function fft(real, imag) {
            const n = real.length;
            let bits = Math.log2(n);

            // Bit-reversal
            for (let i = 0; i < n; i++) {
                let j = 0;
                for (let b = 0; b < bits; b++) {
                    j = (j << 1) | ((i >> b) & 1);
                }
                if (j > i) {
                    [real[i], real[j]] = [real[j], real[i]];
                    [imag[i], imag[j]] = [imag[j], imag[i]];
                }
            }

            // Cooley-Tukey FFT
            for (let len = 2; len <= n; len <<= 1) {
                const angle = -2 * Math.PI / len;
                const wReal = Math.cos(angle);
                const wImag = Math.sin(angle);

                for (let i = 0; i < n; i += len) {
                    let wRealTemp = 1;
                    let wImagTemp = 0;

                    for (let j = 0; j < len / 2; j++) {
                        const k = i + j;
                        const k2 = i + j + len / 2;

                        const tReal = wRealTemp * real[k2] - wImagTemp * imag[k2];
                        const tImag = wRealTemp * imag[k2] + wImagTemp * real[k2];

                        real[k2] = real[k] - tReal;
                        imag[k2] = imag[k] - tImag;
                        real[k] = real[k] + tReal;
                        imag[k] = imag[k] + tImag;

                        const nextWReal = wRealTemp * wReal - wImagTemp * wImag;
                        const nextWImag = wRealTemp * wImag + wImagTemp * wReal;
                        wRealTemp = nextWReal;
                        wImagTemp = nextWImag;
                    }
                }
            }
        }

        // Update simulation function
        function updateSimulation() {
            // Read control values
            const c1 = parseFloat(document.getElementById('slider-c1').value);
            const c2 = parseFloat(document.getElementById('slider-c2').value);
            const clippingEnabled = document.getElementById('checkbox-clipping').checked;
            const clippingLevel = parseFloat(document.getElementById('slider-level').value);

            // Generate time vector
            const t = [];
            for (let i = 0; i < N; i++) {
                t.push(i / fs);
            }

            // Generate input signal x(t)
            const x = [];
            for (let i = 0; i < N; i++) {
                x.push(A * Math.sin(2 * Math.PI * f * t[i]));
            }

            // Calculate output signal y(t)
            const y = [];
            for (let i = 0; i < N; i++) {
                let val = x[i] + c1 * x[i] * x[i] + c2 * x[i] * x[i] * x[i];
                y.push(val);
            }

            // Apply clipping if enabled
            if (clippingEnabled) {
                for (let i = 0; i < N; i++) {
                    y[i] = Math.max(-clippingLevel, Math.min(clippingLevel, y[i]));
                }
            }

            // Update signal plot (show only first part up to 0.005s)
            const samplesToShow = Math.floor(0.005 * fs);
            const tTruncated = t.slice(0, samplesToShow);
            const yTruncated = y.slice(0, samplesToShow);

            const signalTrace = {
                x: tTruncated,
                y: yTruncated,
                type: 'scatter',
                mode: 'lines',
                line: { color: '#3498db', width: 2 }
            };

            const signalLayout = {
                title: 'signal',
                xaxis: { title: 'time[s]', range: [0, 0.005] },
                yaxis: { title: '', range: [-2, 2] },
                margin: { l: 30, r: 20, b: 40, t: 40 },
                showlegend: false
            };

            Plotly.react('plot-signal', [signalTrace], signalLayout);

            // Perform DFT
            const real = [...y];
            const imag = new Array(N).fill(0);

            fft(real, imag);

            // Calculate magnitude
            const mag = [];
            for (let i = 0; i < N / 2; i++) {
                const magnitude = Math.sqrt(real[i] * real[i] + imag[i] * imag[i]);
                mag.push(magnitude);
            }

            // Normalize spectrum
            normalizedMag = [];
            for (let i = 0; i < mag.length; i++) {
                let normalized;
                if (i === 0) {
                    normalized = mag[i] / N;
                } else {
                    normalized = 2 * mag[i] / N;
                }
                normalizedMag.push(normalized);
            }

            // Update spectrum plot
            updateSpectrumScale();

            // Update value displays
            document.getElementById('c1-value-display').textContent = c1.toFixed(1);
            document.getElementById('c2-value-display').textContent = c2.toFixed(1);
            document.getElementById('level-value-display').textContent = clippingLevel.toFixed(2);
        }

        // Update spectrum scale function
        function updateSpectrumScale() {
            // Create frequency vector
            const freq = [];
            for (let i = 0; i < normalizedMag.length; i++) {
                freq.push(i * fs / N);
            }

            let yData;
            let yRange;
            let yTitle = '';

            if (currentScale === 'linear') {
                yData = normalizedMag;
                yRange = [0, 1.1];
                yTitle = '';
            } else {
                yData = normalizedMag.map(val => {
                    if (val < 1e-10) return -100;
                    return 20 * Math.log10(val);
                });
                yRange = [-100, 0];
                yTitle = '';
            }

            const spectrumTrace = {
                x: freq,
                y: yData,
                type: 'bar',
                marker: { color: '#e74c3c' },
                width: 5
            };

            const spectrumLayout = {
                title: 'magnitude spectrum (DFT)',
                xaxis: { title: 'frequency[Hz]', range: [0, 8000] },
                yaxis: { title: yTitle, range: yRange },
                margin: { l: 30, r: 20, b: 40, t: 40 },
                showlegend: false
            };

            Plotly.react('plot-spectrum', [spectrumTrace], spectrumLayout);
        }

        // Event listeners
        document.getElementById('slider-c1').addEventListener('input', updateSimulation);
        document.getElementById('slider-c2').addEventListener('input', updateSimulation);
        document.getElementById('checkbox-clipping').addEventListener('change', function() {
            const levelGroup = document.getElementById('level-control-group');
            levelGroup.style.display = this.checked ? 'flex' : 'none';
            updateSimulation();
        });
        document.getElementById('slider-level').addEventListener('input', updateSimulation);

        document.getElementById('reset-c1').addEventListener('click', function() {
            document.getElementById('slider-c1').value = 0;
            updateSimulation();
        });

        document.getElementById('reset-c2').addEventListener('click', function() {
            document.getElementById('slider-c2').value = 0;
            updateSimulation();
        });

        document.getElementById('btn-linear').addEventListener('click', function() {
            currentScale = 'linear';
            this.classList.add('active');
            document.getElementById('btn-decibel').classList.remove('active');
            updateSpectrumScale();
        });

        document.getElementById('btn-decibel').addEventListener('click', function() {
            currentScale = 'decibel';
            this.classList.add('active');
            document.getElementById('btn-linear').classList.remove('active');
            updateSpectrumScale();
        });

        // Initialize
        updateSimulation();
    </script>
</body>
</html>
