    <script>
        // Control values
        let controls = {
            rayCount: 15,
            beamWidth: 1.25,
            incidentAngle: -45,
            refractionRatio: 2.0,
            showInjected: true,
            showRefracted: true,
            showReflected: true,
            showEjected: true,
            showNormals: false,
            showIntersections: false,
            showBeamTop: true,
            showBeamBottom: true
        };

        function setup() {
            let canvas = createCanvas(windowWidth - 300, windowHeight);
            canvas.parent('p5-canvas');

            // Set up event listeners
            setupEventListeners();
        }

        function setupEventListeners() {
            // Sliders
            const sliders = [
                { id: 'slider-ray-count', control: 'rayCount', displayId: 'value-ray-count' },
                { id: 'slider-beam-width', control: 'beamWidth', displayId: 'value-beam-width' },
                { id: 'slider-incident-angle', control: 'incidentAngle', displayId: 'value-incident-angle' },
                { id: 'slider-refraction-ratio', control: 'refractionRatio', displayId: 'value-refraction-ratio' }
            ];

            sliders.forEach(slider => {
                const element = document.getElementById(slider.id);
                const display = document.getElementById(slider.displayId);

                element.addEventListener('input', (e) => {
                    const value = parseFloat(e.target.value);
                    controls[slider.control] = value;
                    display.textContent = value;
                });
            });

            // Checkboxes
            const checkboxes = [
                { id: 'checkbox-injected', control: 'showInjected' },
                { id: 'checkbox-refracted', control: 'showRefracted' },
                { id: 'checkbox-reflected', control: 'showReflected' },
                { id: 'checkbox-ejected', control: 'showEjected' },
                { id: 'checkbox-normals', control: 'showNormals' },
                { id: 'checkbox-intersections', control: 'showIntersections' },
                { id: 'checkbox-beam-top', control: 'showBeamTop' },
                { id: 'checkbox-beam-bottom', control: 'showBeamBottom' }
            ];

            checkboxes.forEach(checkbox => {
                const element = document.getElementById(checkbox.id);
                element.addEventListener('change', (e) => {
                    controls[checkbox.control] = e.target.checked;
                });
            });
        }

        function draw() {
            background(255);

            // Set up coordinate system
            push();
            translate(width / 2, height / 2);
            const scale = min(width, height) / 6;
            scale(scale, -scale);

            // Draw axes
            drawAxes();

            // Draw sphere
            stroke(0);
            noFill();
            circle(0, 0, 2);

            // Draw rays
            drawRays();

            pop();
        }

        function drawAxes() {
            stroke(150);
            strokeWeight(0.01);

            // X axis
            line(-2.5, 0, 2.5, 0);
            // Y axis
            line(0, -2.5, 0, 2.5);

            // Tick marks and labels
            textAlign(CENTER, TOP);
            textSize(0.1);

            for (let i = -2; i <= 2; i++) {
                if (i !== 0) {
                    // X axis ticks
                    line(i, -0.05, i, 0.05);
                    text(i.toString(), i, 0.1);

                    // Y axis ticks
                    line(-0.05, i, 0.05, i);
                    text(i.toString(), 0.1, i);
                }
            }
        }

        function drawRays() {
            const angleRad = radians(controls.incidentAngle);
            const beamDir = createVector(cos(angleRad), sin(angleRad));
            const beamPerp = createVector(-beamDir.y, beamDir.x);

            for (let i = 0; i < controls.rayCount; i++) {
                const t = (i - (controls.rayCount - 1) / 2) / (controls.rayCount - 1);
                const rayOffset = t * controls.beamWidth;
                const rayOrigin = beamPerp.copy().mult(rayOffset).sub(beamDir.copy().mult(3));

                // Check if we should draw this ray based on beam half settings
                if (!shouldDrawRay(t)) continue;

                traceRay(rayOrigin, beamDir);
            }
        }

        function shouldDrawRay(t) {
            if (controls.showBeamTop && controls.showBeamBottom) return true;
            if (controls.showBeamTop && t >= 0) return true;
            if (controls.showBeamBottom && t <= 0) return true;
            return false;
        }

        function traceRay(origin, direction) {
            // First intersection (ray entering sphere)
            const intersection1 = rayCircleIntersection(origin, direction, createVector(0, 0), 1);
            if (!intersection1) return;

            // Draw injected ray
            if (controls.showInjected) {
                stroke(255, 0, 0);
                strokeWeight(0.02);
                line(origin.x, origin.y, intersection1.x, intersection1.y);
            }

            // Draw first intersection point
            if (controls.showIntersections) {
                drawIntersectionPoint(intersection1, color(255, 0, 0));
            }

            // First normal
            const normal1 = createVector(intersection1.x, intersection1.y).normalize();
            if (controls.showNormals) {
                drawNormal(intersection1, normal1);
            }

            // Refraction (entering sphere)
            const refractedDir = refract(direction, normal1, 1, controls.refractionRatio);
            if (!refractedDir) return;

            // Second intersection (inside sphere)
            const intersection2 = rayCircleIntersection(intersection1, refractedDir, createVector(0, 0), 1);
            if (!intersection2) return;

            // Draw refracted ray
            if (controls.showRefracted) {
                stroke(0, 255, 0);
                strokeWeight(0.02);
                line(intersection1.x, intersection1.y, intersection2.x, intersection2.y);
            }

            // Draw second intersection point
            if (controls.showIntersections) {
                drawIntersectionPoint(intersection2, color(0, 255, 0));
            }

            // Second normal (pointing inward for reflection)
            const normal2 = createVector(intersection2.x, intersection2.y).normalize().mult(-1);
            if (controls.showNormals) {
                drawNormal(intersection2, normal2.copy().mult(-1));
            }

            // Reflection
            const reflectedDir = reflect(refractedDir, normal2);

            // Third intersection (exiting sphere)
            const intersection3 = rayCircleIntersection(intersection2, reflectedDir, createVector(0, 0), 1);
            if (!intersection3) return;

            // Draw reflected ray
            if (controls.showReflected) {
                stroke(0, 0, 255);
                strokeWeight(0.02);
                line(intersection2.x, intersection2.y, intersection3.x, intersection3.y);
            }

            // Draw third intersection point
            if (controls.showIntersections) {
                drawIntersectionPoint(intersection3, color(0, 0, 255));
            }

            // Third normal
            const normal3 = createVector(intersection3.x, intersection3.y).normalize();
            if (controls.showNormals) {
                drawNormal(intersection3, normal3);
            }

            // Refraction (exiting sphere)
            const ejectedDir = refract(reflectedDir, normal3.copy().mult(-1), controls.refractionRatio, 1);
            if (!ejectedDir) return;

            // Draw ejected ray
            if (controls.showEjected) {
                stroke(0, 0, 0);
                strokeWeight(0.02);
                const endP = p5.Vector.add(intersection3, ejectedDir.mult(3));
                line(intersection3.x, intersection3.y, endP.x, endP.y);
            }
        }

        function rayCircleIntersection(rayOrigin, rayDir, circleCenter, radius) {
            const oc = p5.Vector.sub(rayOrigin, circleCenter);
            const a = rayDir.dot(rayDir);
            const b = 2.0 * oc.dot(rayDir);
            const c = oc.dot(oc) - radius * radius;
            const discriminant = b * b - 4 * a * c;

            if (discriminant < 0) return null;

            const t = (-b - sqrt(discriminant)) / (2.0 * a);
            if (t < 0.001) return null; // Avoid self-intersection

            const intersection = p5.Vector.add(rayOrigin, rayDir.mult(t));
            return intersection;
        }

        function refract(incident, normal, n1, n2) {
            const cosI = -p5.Vector.dot(incident, normal);
            const sinT2 = (n1 / n2) * (n1 / n2) * (1.0 - cosI * cosI);

            if (sinT2 > 1.0) return null; // Total internal reflection

            const cosT = sqrt(1.0 - sinT2);
            const refracted = p5.Vector.mult(incident, n1 / n2);
            refracted.add(p5.Vector.mult(normal, (n1 / n2) * cosI - cosT));
            return refracted.normalize();
        }

        function reflect(incident, normal) {
            return p5.Vector.sub(incident, p5.Vector.mult(normal, 2 * p5.Vector.dot(incident, normal)));
        }

        function drawIntersectionPoint(point, col) {
            fill(col);
            noStroke();
            circle(point.x, point.y, 0.05);
        }

        function drawNormal(point, normal) {
            stroke(100);
            strokeWeight(0.01);
            drawingContext.setLineDash([0.05, 0.05]);
            const end = p5.Vector.add(point, p5.Vector.mult(normal, 0.3));
            line(point.x, point.y, end.x, end.y);
            drawingContext.setLineDash([]);
        }

        function windowResized() {
            resizeCanvas(windowWidth - 300, windowHeight);
        }
    </script>
