<body>
    <div id="controls-container">
        <span>size of bipartite graphspan>
        <div id="size-controls">
            <button id="size-6">6button>
            <button id="size-10">10button>
            <button id="size-14">14button>
            <button id="size-18">18button>
            <button id="size-22">22button>
            <button id="size-26">26button>
            <button id="size-30">30button>
        </div>

        <span>maximum degree of lower partspan>
        <div id="degree-controls">
            <button id="degree-2">2button>
            <button id="degree-3">3button>
            <button id="degree-4">4button>
            <button id="degree-5">5button>
            <button id="degree-6">6button>
        </div>

        <span>algorithm stepspan>
        <div id="step-controls"></div>

        <button id="reset-button" title="Generate new graph">(+)</button>
    </div>

    <div id="visualization-container">
        <p id="status-text"></p>
        <div id="canvas-wrapper">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global state management
        let currentSize = 18;
        let currentDegree = 3;
        let currentStep = 0;
        let history = [];
        let graph = {
            n: 0,
            m: 0,
            adj: []
        };

        // p5.js sketch variables
        let canvas;
        let leftPanelWidth, rightPanelWidth;
        let canvasWidth = 1200, canvasHeight = 600;

        function setup() {
            canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('main-canvas');

            leftPanelWidth = canvasWidth * 0.4;
            rightPanelWidth = canvasWidth * 0.6;

            // Initialize controls
            initializeControls();

            // Generate initial graph
            generateGraph();
        }

        function draw() {
            background(255);

            if (history.length > 0 && currentStep < history.length) {
                let state = history[currentStep];

                // Draw dividing line
                stroke(200);
                strokeWeight(2);
                line(leftPanelWidth, 0, leftPanelWidth, canvasHeight);

                // Draw BFS tree on left
                drawBFSTree(state.bfsTree);

                // Draw bipartite graph on right
                drawBipartiteGraph(state);

                // Update status text
                document.getElementById('status-text').textContent = state.statusText;
            }
        }

        function initializeControls() {
            // Size controls
            document.getElementById('size-18').classList.add('active');
            document.querySelectorAll('[id^="size-"]').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('[id^="size-"]').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentSize = parseInt(this.id.split('-')[1]);
                    generateGraph();
                });
            });

            // Degree controls
            document.getElementById('degree-3').classList.add('active');
            document.querySelectorAll('[id^="degree-"]').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('[id^="degree-"]').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentDegree = parseInt(this.id.split('-')[1]);
                    generateGraph();
                });
            });

            // Reset button
            document.getElementById('reset-button').addEventListener('click', function() {
                generateGraph();
            });
        }

        function generateGraph() {
            history = [];
            currentStep = 0;

            // Generate bipartite graph
            graph.n = currentSize / 2; // Each partition has n vertices
            graph.m = currentSize / 2;
            graph.adj = Array(graph.n + graph.m + 1).fill(null).map(() => []);

            // Generate random edges ensuring each bottom vertex has degree between 1 and currentDegree
            for (let v = graph.n + 1; v <= graph.n + graph.m; v++) {
                let degree = Math.floor(Math.random() * currentDegree) + 1;
                let neighbors = [];

                // Get random neighbors from top partition
                while (neighbors.length < degree && neighbors.length < graph.n) {
                    let u = Math.floor(Math.random() * graph.n) + 1;
                    if (!neighbors.includes(u)) {
                        neighbors.push(u);
                        graph.adj[u].push(v);
                        graph.adj[v].push(u);
                    }
                }
            }

            // Run Hungarian algorithm and collect states
            runHungarianAlgorithm();

            // Update step controls
            updateStepControls();
        }

        function runHungarianAlgorithm() {
            let matching = Array(graph.n + graph.m + 1).fill(0);
            let parent = Array(graph.n + graph.m + 1).fill(0);
            let state = {
                matching: [...matching],
                cover: [],
                bfsTree: {},
                highlightedPath: [],
                statusText: "Initial state: No edges matched"
            };

            history.push(JSON.parse(JSON.stringify(state)));

            for (let u = 1; u <= graph.n; u++) {
                if (matching[u] === 0) {
                    let bfsState = {
                        matching: [...matching],
                        cover: [],
                        bfsTree: {},
                        highlightedPath: [],
                        statusText: `Starting BFS from vertex ${u}`
                    };
                    history.push(JSON.parse(JSON.stringify(bfsState)));

                    let visited = Array(graph.n + graph.m + 1).fill(false);
                    let queue = [u];
                    visited[u] = true;
                    parent[u] = 0;

                    // BFS tree construction
                    let tree = {};
                    tree[u] = { parent: 0, children: [] };

                    while (queue.length > 0) {
                        let curr = queue.shift();

                        if (curr > graph.n && matching[curr] === 0) {
                            // Augmenting path found
                            let path = [];
                            let node = curr;
                            while (node !== 0) {
                                path.push(node);
                                node = parent[node];
                            }
                            path.reverse();

                            let beforeMatching = [...matching];

                            // Update matching
                            for (let i = 0; i < path.length - 1; i += 2) {
                                matching[path[i]] = path[i + 1];
                                matching[path[i + 1]] = path[i];
                            }

                            state = {
                                matching: [...matching],
                                cover: [],
                                bfsTree: tree,
                                highlightedPath: path,
                                statusText: `Augmenting path found! Matched edges: ${countMatchingEdges(matching)}`
                            };
                            history.push(JSON.parse(JSON.stringify(state)));

                            break;
                        }

                        for (let neighbor of graph.adj[curr]) {
                            if (!visited[neighbor]) {
                                visited[neighbor] = true;
                                parent[neighbor] = curr;
                                queue.push(neighbor);

                                // Update BFS tree
                                if (!tree[curr]) {
                                    tree[curr] = { parent: parent[curr], children: [] };
                                }
                                tree[curr].children.push(neighbor);
                                tree[neighbor] = { parent: curr, children: [] };

                                let bfsStep = {
                                    matching: [...matching],
                                    cover: [],
                                    bfsTree: JSON.parse(JSON.stringify(tree)),
                                    highlightedPath: [],
                                    statusText: `Exploring edge (${curr},${neighbor})`
                                };
                                history.push(JSON.parse(JSON.stringify(bfsStep)));
                            }
                        }
                    }
                }
            }

            // Add final state with Kőnig's theorem vertex cover
            let cover = findVertexCover(matching);
            state = {
                matching: [...matching],
                cover: cover,
                bfsTree: {},
                highlightedPath: [],
                statusText: `Complete! Matched edges: ${countMatchingEdges(matching)}, Vertex cover size: ${cover.length}`
            };
            history.push(JSON.parse(JSON.stringify(state)));
        }

        function countMatchingEdges(matching) {
            let count = 0;
            for (let i = 1; i <= graph.n; i++) {
                if (matching[i] > 0) count++;
            }
            return count;
        }

        function findVertexCover(matching) {
            let cover = [];

            // Simple implementation: matched vertices from U and unmatched vertices from V
            for (let u = 1; u <= graph.n; u++) {
                if (matching[u] !== 0) {
                    cover.push(u);
                }
            }
            for (let v = graph.n + 1; v <= graph.n + graph.m; v++) {
                if (matching[v] === 0) {
                    cover.push(v);
                }
            }

            return cover;
        }

        function updateStepControls() {
            let stepControls = document.getElementById('step-controls');
            stepControls.innerHTML = '';

            for (let i = 0; i < history.length; i++) {
                let btn = document.createElement('button');
                btn.id = `step-${i + 1}`;
                btn.textContent = i + 1;
                btn.addEventListener('click', function() {
                    document.querySelectorAll('#step-controls button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentStep = i;
                    redraw();
                });
                stepControls.appendChild(btn);
            }

            // Set first step as active
            if (history.length > 0) {
                document.getElementById('step-1').classList.add('active');
            }
        }

        function drawBFSTree(tree) {
            if (Object.keys(tree).length === 0) return;

            push();
            translate(leftPanelWidth / 2, 50);

            // Calculate tree layout
            let positions = calculateTreeLayout(tree);

            // Draw edges
            stroke(255, 0, 0);
            strokeWeight(2);
            for (let node in tree) {
                if (tree[node].parent !== 0) {
                    let pos1 = positions[node];
                    let pos2 = positions[tree[node].parent];
                    line(pos1.x, pos1.y, pos2.x, pos2.y);
                }
            }

            // Draw nodes
            for (let node in positions) {
                let pos = positions[node];
                fill(255, 165, 0);
                stroke(0);
                strokeWeight(2);
                rectMode(CENTER);
                rect(pos.x, pos.y, 40, 30, 5);

                fill(128, 128, 192);
                noStroke();
                textAlign(CENTER, CENTER);
                textSize(14);
                text(node, pos.x, pos.y);
            }

            pop();
        }

        function calculateTreeLayout(tree) {
            let positions = {};
            let levels = {};
            let levelCounts = {};

            // Assign levels
            for (let node in tree) {
                let level = 0;
                let current = node;
                while (tree[current].parent !== 0) {
                    current = tree[current].parent;
                    level++;
                }
                if (!levels[level]) levels[level] = [];
                levels[level].push(node);
            }

            // Calculate positions
            let maxLevel = Math.max(...Object.keys(levels).map(Number));
            for (let l = 0; l <= maxLevel; l++) {
                if (levels[l]) {
                    let y = 80 + l * 80;
                    let count = levels[l].length;
                    let spacing = Math.min(100, (leftPanelWidth - 100) / (count + 1));
                    let startX = -(count - 1) * spacing / 2;

                    levels[l].forEach((node, i) => {
                        positions[node] = {
                            x: startX + i * spacing,
                            y: y
                        };
                    });
                }
            }

            return positions;
        }

        function drawBipartiteGraph(state) {
            push();
            translate(leftPanelWidth + rightPanelWidth / 2, 0);

            // Calculate vertex positions
            let topY = 100;
            let bottomY = canvasHeight - 100;
            let topPositions = [];
            let bottomPositions = [];

            let topSpacing = rightPanelWidth / (graph.n + 1);
            let bottomSpacing = rightPanelWidth / (graph.m + 1);

            for (let i = 1; i <= graph.n; i++) {
                topPositions[i] = { x: -rightPanelWidth / 2 + i * topSpacing, y: topY };
            }
            for (let i = 1; i <= graph.m; i++) {
                bottomPositions[graph.n + i] = { x: -rightPanelWidth / 2 + i * bottomSpacing, y: bottomY };
            }

            // Draw default edges
            stroke(128, 128, 128);
            strokeWeight(1);
            for (let u = 1; u <= graph.n; u++) {
                for (let v of graph.adj[u]) {
                    if (v > graph.n) {
                        line(topPositions[u].x, topPositions[u].y, bottomPositions[v].x, bottomPositions[v].y);
                    }
                }
            }

            // Draw matching edges
            stroke(160, 160, 255);
            strokeWeight(4);
            for (let u = 1; u <= graph.n; u++) {
                if (state.matching[u] !== 0) {
                    let v = state.matching[u];
                    line(topPositions[u].x, topPositions[u].y, bottomPositions[v].x, bottomPositions[v].y);
                }
            }

            // Draw highlighted path edges
            if (state.highlightedPath.length > 0) {
                stroke(255, 0, 0);
                strokeWeight(3);
                for (let i = 0; i < state.highlightedPath.length - 1; i++) {
                    let u = state.highlightedPath[i];
                    let v = state.highlightedPath[i + 1];
                    if (u <= graph.n) {
                        line(topPositions[u].x, topPositions[u].y, bottomPositions[v].x, bottomPositions[v].y);
                    } else {
                        line(topPositions[v].x, topPositions[v].y, bottomPositions[u].x, bottomPositions[u].y);
                    }
                }
            }

            // Draw vertices
            for (let i = 1; i <= graph.n; i++) {
                let pos = topPositions[i];

                // Determine color
                if (state.cover.includes(i)) {
                    fill(0, 192, 0);
                } else if (state.bfsTree[i] && state.bfsTree[i].parent === 0) {
                    fill(255, 0, 0);
                } else {
                    fill(51, 51, 51);
                }

                noStroke();
                ellipse(pos.x, pos.y, 30, 30);

                fill(255);
                textAlign(CENTER, CENTER);
                textSize(16);
                text(i, pos.x, pos.y);
            }

            for (let i = graph.n + 1; i <= graph.n + graph.m; i++) {
                let pos = bottomPositions[i];

                // Determine color
                if (state.cover.includes(i)) {
                    fill(0, 192, 0);
                } else if (state.bfsTree[i] && state.bfsTree[i].parent === 0) {
                    fill(255, 0, 0);
                } else {
                    fill(51, 51, 51);
                }

                noStroke();
                ellipse(pos.x, pos.y, 30, 30);

                fill(255);
                textAlign(CENTER, CENTER);
                textSize(16);
                text(i - graph.n, pos.x, pos.y);
            }

            pop();
        }
    </script>
body>
html>
