    <script>
        // Helper function to calculate factorial
        function factorial(n) {
            if (n <= 1) return 1;
            return n * factorial(n - 1);
        }

        // Function to generate x values
        function generateXValues(max) {
            const x = [];
            for (let i = 1; i <= max; i++) {
                x.push(i);
            }
            return x;
        }

        // Function to calculate y values for each algorithm
        function calculateYValues(x) {
            const algorithms = {
                bubble: x.map(n => n * n),
                selection: x.map(n => 0.25 * n * n),
                insertion: x.map(n => n * factorial(n)),
                merge: x.map(n => n * Math.log2(n)),
                quick: x.map(n => 1.2 * n * Math.log2(n)),
                heap: x.map(n => 1.5 * n * Math.log2(n)),
                bogo: x.map(n => n * factorial(n))
            };
            return algorithms;
        }

        // Initial setup
        let currentMax = 10;
        let currentScale = 'linear';
        let currentRange = 'fixed';

        // Get control elements
        const sliderElements = document.getElementById('slider-elements');
