        const radioScaleLogarithmic = document.getElementById('radio-scale-logarithmic');
        const radioRangeFixed = document.getElementById('radio-range-fixed');
        const radioRangeAutomatic = document.getElementById('radio-range-automatic');

        // Get checkbox elements
        const checkBubble = document.getElementById('check-bubble');
        const checkSelection = document.getElementById('check-selection');
        const checkInsertion = document.getElementById('check-insertion');
        const checkMerge = document.getElementById('check-merge');
        const checkQuick = document.getElementById('check-quick');
        const checkHeap = document.getElementById('check-heap');
        const checkBogo = document.getElementById('check-bogo');

        // Function to create plot data
        function createPlotData() {
            const x = generateXValues(currentMax);
            const yValues = calculateYValues(x);

            const traces = [
                {
                    x: x,
                    y: yValues.bubble,
                    name: 'bubble',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'green', width: 5 },
                    visible: checkBubble.checked
                },
                {
                    x: x,
                    y: yValues.selection,
                    name: 'selection',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'orange', dash: 'dot' },
                    visible: checkSelection.checked
                },
                {
                    x: x,
                    y: yValues.insertion,
                    name: 'insertion',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                    visible: checkInsertion.checked || checkBogo.checked
                },
                {
                    x: x,
                    y: yValues.merge,
                    name: 'merge',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    visible: checkMerge.checked
                },
                {
                    x: x,
                    y: yValues.quick,
                    name: 'quick',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'purple', width: 2 },
                    visible: checkQuick.checked
                },
                {
                    x: x,
                    y: yValues.heap,
                    name: 'heap',
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'cyan', width: 2 },
                    visible: checkHeap.checked
                }
            ];

            return traces;
        }

        // Function to create plot layout
        function createPlotLayout() {
            const layout = {
                title: 'time complexity',
                xaxis: {
                    title: 'number of elements'
                },
                yaxis: {
                    title: 'operations',
                    type: currentScale
                },
                legend: {
                    orientation: 'h',
                    y: -0.1
                }
            };

            if (currentRange === 'fixed') {
                layout.xaxis.range = [0, 20];
                layout.xaxis.autorange = false;
                layout.yaxis.range = [0, 500];
                layout.yaxis.autorange = false;
            } else {
                layout.xaxis.autorange = true;
                layout.yaxis.autorange = true;
            }

            return layout;
        }

        // Function to update the plot
        function updatePlot() {
            const data = createPlotData();
            const layout = createPlotLayout();

            if (window.plot) {
                Plotly.react('plot-container', data, layout);
            } else {
                window.plot = true;
                Plotly.newPlot('plot-container', data, layout);
            }
        }

        // Event listeners
        sliderElements.addEventListener('input', function() {
            currentMax = parseInt(this.value);
            displayElements.textContent = `+ ${currentMax}`;
            updatePlot();
        });

        radioScaleLinear.addEventListener('change', function() {
            if (this.checked) {
                currentScale = 'linear';
                updatePlot();
            }
        });

        radioScaleLogarithmic.addEventListener('change', function() {
            if (this.checked) {
                currentScale = 'log';
                updatePlot();
            }
        });

        radioRangeFixed.addEventListener('change', function() {
            if (this.checked) {
                currentRange = 'fixed';
                updatePlot();
            }
        });

        radioRangeAutomatic.addEventListener('change', function() {
            if (this.checked) {
                currentRange = 'automatic';
                updatePlot();
            }
        });

        // Checkbox event listeners
        const checkboxes = [checkBubble, checkSelection, checkInsertion, checkMerge, checkQuick, checkHeap, checkBogo];
        checkboxes.forEach(checkbox => {
            checkbox.addEventListener('change', function() {
                updatePlot();
            });
        });

        // MathJax configuration
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        // Initialize plot
        updatePlot();
    </script>
