<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        // Constants
        const a = 9;
        const b = -0.6;

        // VLE function
        function y(x) {
            return (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
        }

        // Reflux ratio function
        function r(x, xD) {
            const y_val = y(x);
            return (xD - y_val) / (y_val - x);
        }

        // Update plot function
        function updatePlot(xD) {
            // Generate data for the curve
            const x_vals = [];
            const r_vals = [];

            // Generate points for the bifurcation curve
            for (let x = 0.001; x < xD; x += 0.001) {
                const r_val = r(x, xD);
                // Only include valid points (positive and finite)
                if (r_val >= 0 && r_val < 5 && isFinite(r_val)) {
                    x_vals.push(x);
                    r_vals.push(r_val);
                }
            }

            // Find tangent pinch point (local maximum)
            const pinch_x = [];
            const pinch_r = [];

            if (r_vals.length > 2) {
                for (let i = 1; i < r_vals.length - 1; i++) {
                    if (r_vals[i-1] < r_vals[i] && r_vals[i] > r_vals[i+1]) {
                        pinch_x.push(x_vals[i]);
                        pinch_r.push(r_vals[i]);
                        break; // Only need one pinch point
                    }
                }
            }

            // Create traces for Plotly
            const traces = [
                // Bifurcation curve
                {
                    x: x_vals,
                    y: r_vals,
                    mode: 'lines',
                    type: 'scatter',
                    line: {
                        color: '#8B0000',
                        width: 2.5
                    }
                },
                // Tangent pinch point marker
                {
                    x: pinch_x,
                    y: pinch_r,
                    mode: 'markers',
                    type: 'scatter',
                    marker: {
                        color: 'royalblue',
                        size: 10
                    }
                }
            ];

            // Layout configuration
            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    gridcolor: '#e0e0e0',
                    tick0: 0,
                    dtick: 0.2
                },
                yaxis: {
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    tick0: 0,
                    dtick: 0.5
                },
                showlegend: false,
                margin: {
                    l: 40,
                    r: 20,
                    b: 40,
                    t: 20
                },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            // Update the plot
            Plotly.react('plot', traces, layout);
        }

        // Initialize the page
        document.addEventListener('DOMContentLoaded', function() {
            const slider = document.getElementById('slider-xD');
            const label = document.getElementById('label-xD');
            const plotDiv = document.getElementById('plot');

            // Initial plot
            updatePlot(parseFloat(slider.value));

            // Slider event listener
            slider.addEventListener('input', function() {
                const value = parseFloat(this.value);
                label.textContent = value.toFixed(2);
                updatePlot(value);
            });
        });
    </script>
