        <button id="btn-element-Mn">Mn</button>
        <button id="btn-element-Fe">Fe</button>
        <button id="btn-element-Co">Co</button>
        <button id="btn-element-Cu">Cu</button>
        <button id="btn-element-Zn">Zn</button>
        <button id="btn-element-Al">Al</button>
        <button id="btn-element-Si">Si</button>
    </div>

    <div id="canvas-container">
        <canvas id="drawing-canvas"></canvas>
    </div>

    <div id="footer-controls">
        <button id="btn-undo">undo</button>
        <div class="structure-control">
            <select id="select-structure" size="4">
                <option value="bond" selected>—</option>
                <option value="water">Water</option>
                <option value="cyclopropyl_ethane">Cyclopropyl-ethane</option>
                <option value="borazine">Borazine</option>
                <option value="isopropylamine">Isopropylamine</option>
            </select>
            <button id="btn-add">add</button>
        </div>
    </div>

    <script>
        let canvas;
        let sceneObjects = [];
        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let initialMouseOffset = { x: 0, y: 0 };
        let initialAngle = 0;

        // p5.js setup
        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('drawing-canvas');
            textAlign(CENTER, CENTER);
            textSize(16);

            // Set up event listeners
            setupEventListeners();
        }

        // p5.js draw loop
        function draw() {
            background(255);

            // Draw all objects
            for (let obj of sceneObjects) {
                push();
                translate(obj.x, obj.y);
                rotate(obj.rotation);

                if (obj.type === 'atom' || obj.type === 'charge') {
                    text(obj.value, 0, 0);
                } else if (obj.type === 'structure') {
                    drawStructure(obj.value);
                }

                pop();
            }

            // Draw preview of selected tool
            if (selectedTool && mouseX >= 0 && mouseX <= width && mouseY >= 0 && mouseY <= height) {
                push();
                fill(150);
                text(selectedTool.value, mouseX, mouseY);
                pop();
            }
        }

        // Draw predefined structures
        function drawStructure(type) {
            strokeWeight(2);
            stroke(0);

            switch(type) {
                case 'bond':
                    line(-20, 0, 20, 0);
                    break;

                case 'water':
                    // O at center
                    text('O', 0, 0);
                    // H atoms
                    text('H', -25, -25);
                    text('H', 25, -25);
                    // Bonds
                    line(0, 0, -15, -15);
                    line(0, 0, 15, -15);
                    break;

                case 'cyclopropyl_ethane':
                    // Cyclopropyl ring
                    text('C', 0, -30);
                    text('C', -26, 15);
                    text('C', 26, 15);
                    // Ring bonds
                    line(0, -15, -13, 7);
                    line(-13, 7, 13, 7);
                    line(13, 7, 0, -15);
                    // Ethane chain
                    text('C', 0, 50);
                    text('C', 0, 80);
                    line(0, 30, 0, 35);
                    line(0, 65, 0, 70);
                    break;

                case 'borazine':
                    // Hexagonal structure
                    text('B', 0, -40);
                    text('N', 35, -20);
                    text('B', 35, 20);
                    text('N', 0, 40);
                    text('B', -35, 20);
                    text('N', -35, -20);
                    // Bonds
                    line(0, -25, 22, -12);
                    line(22, -12, 22, 12);
                    line(22, 12, 0, 25);
                    line(0, 25, -22, 12);
                    line(-22, 12, -22, -12);
                    line(-22, -12, 0, -25);
                    break;

                case 'isopropylamine':
                    // Central carbon
                    text('C', 0, 0);
                    // Three branches
                    text('C', -40, 0);
                    text('C', 40, 0);
                    text('N', 0, 40);
                    // Bonds
                    line(-10, 0, -30, 0);
                    line(10, 0, 30, 0);
                    line(0, 10, 0, 30);
                    // NH2 on nitrogen
                    text('H', -20, 60);
                    text('H', 20, 60);
                    line(0, 50, -10, 55);
                    line(0, 50, 10, 55);
                    break;
            }
        }

        // Set up event listeners
        function setupEventListeners() {
            // Toolbar buttons
            document.getElementById('btn-charge-minus').addEventListener('click', () => selectTool('charge', '-'));
            document.getElementById('btn-charge-plus').addEventListener('click', () => selectTool('charge', '+'));
            document.getElementById('btn-charge-2').addEventListener('click', () => selectTool('charge', '2'));

            const elements = ['H', 'B', 'C', 'N', 'O', 'F', 'P', 'S', 'Cl', 'Se', 'Br', 'I', 
                            'Li', 'Na', 'K', 'Be', 'Mg', 'Ca', 'Cr', 'Mn', 'Fe', 'Co', 'Cu', 'Zn', 'Al', 'Si'];

            elements.forEach(element => {
                document.getElementById(`btn-element-${element}`).addEventListener('click', () => selectTool('atom', element));
            });

            // Footer controls
            document.getElementById('btn-undo').addEventListener('click', undo);
            document.getElementById('btn-add').addEventListener('click', addStructure);
        }

        // Select tool from toolbar
        function selectTool(type, value) {
            // Clear previous selection
            document.querySelectorAll('#toolbar button').forEach(btn => btn.classList.remove('selected'));

            // Set new selection
            selectedTool = { type, value };
            document.getElementById(`btn-${type}-${value}`).classList.add('selected');
        }

        // Mouse press event
        function mousePressed() {
            if (mouseX < 0 || mouseX > width || mouseY < 0 || mouseY > height) return;

            // Check if clicking on an existing object
            for (let i = sceneObjects.length - 1; i >= 0; i--) {
                let obj = sceneObjects[i];
                let d = dist(mouseX, mouseY, obj.x, obj.y);

                if (d < 25) {
                    activeObject = obj;
                    if (d < 15) {
                        interactionMode = 'rotate';
                        initialAngle = atan2(mouseY - obj.y, mouseX - obj.x) - obj.rotation;
                    } else {
                        interactionMode = 'translate';
                        initialMouseOffset.x = mouseX - obj.x;
                        initialMouseOffset.y = mouseY - obj.y;
                    }
                    return;
                }
            }

            // If no object clicked and tool selected, add new object
            if (selectedTool) {
                sceneObjects.push({
                    type: selectedTool.type,
                    value: selectedTool.value,
                    x: mouseX,
                    y: mouseY,
                    rotation: 0
                });

                // Clear selection
                selectedTool = null;
                document.querySelectorAll('#toolbar button').forEach(btn => btn.classList.remove('selected'));
            }
        }

        // Mouse drag event
        function mouseDragged() {
            if (!activeObject) return;

            if (interactionMode === 'translate') {
                activeObject.x = mouseX - initialMouseOffset.x;
                activeObject.y = mouseY - initialMouseOffset.y;
            } else if (interactionMode === 'rotate') {
                activeObject.rotation = atan2(mouseY - activeObject.y, mouseX - activeObject.x) - initialAngle;
            }
        }

        // Mouse release event
        function mouseReleased() {
            activeObject = null;
            interactionMode = null;
        }

        // Undo last action
        function undo() {
            sceneObjects.pop();
        }

        // Add selected structure
        function addStructure() {
            const select = document.getElementById('select-structure');
            const structure = select.value;

            sceneObjects.push({
                type: 'structure',
                value: structure,
                x: width / 2,
                y: height / 2,
                rotation: 0
            });
        }
    </script>
</body>
</html>
