                <label for="slider-wavelength">wavelength \(\lambda\)</label>
                <input type="range" id="slider-wavelength" min="1.0" max="10.0" step="0.1" value="4.5">
                <span id="value-wavelength">4.50</span>
            </div>
            <div class="control-row">
                <label for="slider-time">time \(t\)</label>
                <input type="range" id="slider-time" min="0" max="10" step="0.01" value="2.5">
                <span id="value-time">2.50</span>
            </div>
            <div class="control-row">
                <label for="select-phase">initial phase \(\phi_0\)</label>
                <select id="select-phase">
                    <option value="-3.141592653589793">-π</option>
                    <option value="-2.0943951023931953">-2π/3</option>
                    <option value="-1.5707963267948966" selected>-π/2</option>
                    <option value="-1.0471975511965976">-π/3</option>
                    <option value="0">0</option>
                    <option value="1.0471975511965976">π/3</option>
                    <option value="1.5707963267948966">π/2</option>
                    <option value="2.0943951023931953">2π/3</option>
                    <option value="3.141592653589793">π</option>
                </select>
                <span id="value-phase">-π/2</span>
            </div>
        </div>

        <!-- Formula Display -->
        <div id="formula-display">
            \( y(x,t) = -A \sin(kx - \omega t) \)
        </div>

        <!-- Visualization Canvas -->
        <div id="canvas-container">
            <div id="canvas-wrapper"></div>
        </div>
    </div>

    <script>
        // Global variables for wave parameters
        let A, f, lambda, t, phi0, k, omega;
        let pixelScale = 30;
        let canvas;

        // Initialize MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['$', '$'], ['\\(', '\\)']],
                displayMath: [['$$', '$$'], ['\\[', '\\]']]
            }
        };

        // p5.js sketch
        function setup() {
            // Create canvas with specific dimensions
            canvas = createCanvas(800, 400);
            canvas.parent('canvas-wrapper');

            // Initialize controls
            initializeControls();
            updateWaveParameters();
        }

        function draw() {
            // Clear canvas
            background(255);

            // Set up coordinate system
            push();

            // Move origin to left of center, vertically centered
            translate(80, height / 2);

            // Invert y-axis so positive points up
            scale(1, -1);

            // Apply scaling factor
            scale(pixelScale, pixelScale);

            // Draw axes
            drawAxes();

            // Draw grid and ticks
            drawGridAndTicks();

            // Draw wave
            drawWave();

            // Draw points and vectors
            drawPointsAndVectors();

            pop();
        }

        function initializeControls() {
            // Add event listeners to all controls
            const controls = [
                'slider-amplitude', 'slider-frequency', 
                'slider-wavelength', 'slider-time', 'select-phase'
            ];

            controls.forEach(id => {
                const element = document.getElementById(id);

                if (element.type === 'range') {
                    element.addEventListener('input', handleControlChange);
                } else {
                    element.addEventListener('change', handleControlChange);
                }
            });
        }

        function handleControlChange() {
            // Update display values
            updateDisplayValues();

            // Update wave parameters
            updateWaveParameters();

            // Update formula
            updateFormula();

            // Trigger redraw
            redraw();
        }

        function updateDisplayValues() {
            // Update amplitude value
            const amplitude = parseFloat(document.getElementById('slider-amplitude').value);
            document.getElementById('value-amplitude').textContent = amplitude.toFixed(2);

            // Update frequency value
            const frequency = parseFloat(document.getElementById('slider-frequency').value);
            document.getElementById('value-frequency').textContent = frequency.toFixed(2);

            // Update wavelength value
            const wavelength = parseFloat(document.getElementById('slider-wavelength').value);
            document.getElementById('value-wavelength').textContent = wavelength.toFixed(2);

            // Update time value
            const time = parseFloat(document.getElementById('slider-time').value);
            document.getElementById('value-time').textContent = time.toFixed(2);

            // Update phase value display
            const phaseSelect = document.getElementById('select-phase');
            const phaseText = phaseSelect.options[phaseSelect.selectedIndex].text;
            document.getElementById('value-phase').textContent = phaseText;
        }

        function updateWaveParameters() {
            // Get values from controls
            A = parseFloat(document.getElementById('slider-amplitude').value);
            f = parseFloat(document.getElementById('slider-frequency').value);
            lambda = parseFloat(document.getElementById('slider-wavelength').value);
            t = parseFloat(document.getElementById('slider-time').value);
            phi0 = parseFloat(document.getElementById('select-phase').value);

            // Calculate derived parameters
            k = 2 * Math.PI / lambda;
            omega = 2 * Math.PI * f;
        }

        function updateFormula() {
            const formulaDiv = document.getElementById('formula-display');
            let formula = '';

            // Apply simplification logic
            if (phi0 === Math.PI / 2) {
                formula = `y(x,t) = ${A.toFixed(2)} \\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else if (phi0 === -Math.PI / 2) {
                formula = `y(x,t) = -${A.toFixed(2)} \\sin(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else if (phi0 === Math.PI || phi0 === -Math.PI) {
                formula = `y(x,t) = -${A.toFixed(2)} \\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else if (phi0 === 0) {
                formula = `y(x,t) = ${A.toFixed(2)} \\cos(${k.toFixed(2)}x - ${omega.toFixed(2)}t)`;
            } else {
                const phaseSign = phi0 > 0 ? '+' : '';
                formula = `y(x,t) = ${A.toFixed(2)} \\cos(${omega.toFixed(2)}t - ${k.toFixed(2)}x ${phaseSign} ${phi0.toFixed(2)})`;
            }

            formulaDiv.innerHTML = `$$ ${formula} $$`;

            // Trigger MathJax to re-render
            if (window.MathJax) {
                MathJax.typesetPromise([formulaDiv]).catch((e) => console.log(e.message));
            }
        }

        function drawAxes() {
            strokeWeight(2);
            stroke(0);

            // Draw x-axis
            line(0, 0, 20, 0);
            // X-axis arrowhead
            push();
            translate(20, 0);
            rotate(0);
            line(0, 0, -3, -1);
            line(0, 0, -3, 1);
            pop();

            // Draw y-axis
            line(0, 0, 0, 5);
            // Y-axis arrowhead
            push();
            translate(0, 5);
            rotate(Math.PI / 2);
            line(0, 0, -3, -1);
            line(0, 0, -3, 1);
            pop();

            // Labels
            push();
            scale(1, -1); // Reset scaling for text
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            text('x', 21.5, 0.5);
            text('y', 0.5, -5.5);
            pop();
        }

        function drawGridAndTicks() {
            strokeWeight(1);
            stroke(0, 200, 0, 100); // Light green
            noFill();

            // Draw vertical grid lines and ticks for x = 1 to 16
            for (let x = 1; x <= 16; x++) {
                if (x <= 20) { // Only draw within canvas bounds
                    // Grid line (vertical)
                    strokeWeight(0.5);
                    stroke(0, 200, 0, 80);
                    setLineDash([2, 2]);
                    line(x, -5, x, 5);
                    setLineDash([]);

                    // Tick mark on x-axis
                    strokeWeight(2);
                    stroke(0);
                    line(x, 0, x, 0.1);

                    // Label
                    push();
                    scale(1, -1); // Reset scaling for text
                    fill(0);
                    noStroke();
                    textAlign(CENTER, CENTER);
                    text(x.toString(), x, -0.3);
                    pop();
                }
            }
        }

        function drawWave() {
            strokeWeight(3);
            stroke(0, 0, 255); // Blue
            noFill();

            beginShape();
            for (let pixelX = 0; pixelX < canvas.width - 80; pixelX += 2) {
                const worldX = pixelX / pixelScale;
                const y = A * Math.cos(omega * t - k * worldX + phi0);
                vertex(worldX, y);
            }
            endShape();
        }

        function drawPointsAndVectors() {
            for (let x = 1; x <= 16; x++) {
                // Calculate particle state
                const y = A * Math.cos(omega * t - k * x + phi0);
                const v = -A * omega * Math.sin(omega * t - k * x + phi0);
                const a = -A * omega * omega * Math.cos(omega * t - k * x + phi0);

                // Draw point
                fill(0);
                noStroke();
                circle(x, y, 0.2);

                // Draw velocity vector (red)
                drawArrow(x, y, v * 0.001, color(255, 0, 0), '\\vec{v}');

                // Draw acceleration vector (black)
                drawArrow(x, y, a * 0.0002, color(0, 0, 0), '\\vec{a}');
            }
        }

        function drawArrow(baseX, baseY, vectorY, arrowColor, label) {
            push();

            // Set arrow color
            stroke(arrowColor);
            fill(arrowColor);

            // Draw arrow line
            strokeWeight(2);
            line(baseX, baseY, baseX, baseY + vectorY);

            // Draw arrowhead
            if (Math.abs(vectorY) > 0.1) {
                push();
                translate(baseX, baseY + vectorY);
                if (vectorY > 0) {
                    rotate(Math.PI / 2);
                } else {
                    rotate(-Math.PI / 2);
                }
                triangle(0, 0, -0.1, 0.1, 0.1, 0.1);
                pop();

                // Draw label
                push();
                scale(1, -1); // Reset scaling for text
                fill(arrowColor);
                noStroke();
                textAlign(CENTER, CENTER);
                textSize(0.2);
                text(label, baseX + 0.3, -(baseY + vectorY));
                pop();
            }

            pop();
        }

        // Helper function for dashed lines
        function setLineDash(list) {
            drawingContext.setLineDash(list);
        }

        // Initialize on page load
        window.addEventListener('load', () => {
            updateDisplayValues();
            updateFormula();
        });
    </script>
</body>
</html>
