            <div id="energy-display">
                energy = <span id="energy-value">\\(-\\frac{1}{4}\\)</span>
            </div>
        </div>
    </div>

    <script>
        // MathJax configuration
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        let sketch = function(p) {
            let eccentricity = 0;
            const p_value = 2; // semi-latus rectum
            let canvasWidth = 700;
            let canvasHeight = 500;
            let originX, originY;
            const scale = 30; // pixels per unit

            p.setup = function() {
                let canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');

                originX = canvasWidth * 0.7;
                originY = canvasHeight / 2;

                // Initial MathJax rendering
                setTimeout(() => {
                    if (window.MathJax) {
                        MathJax.typeset();
                    }
                }, 100);
            };

            p.draw = function() {
                // Clear canvas with light gray background
                p.background(240);

                // Set up coordinate system
                p.push();
                p.translate(originX, originY);
                p.scale(1, -1); // Invert y-axis

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(-canvasWidth * 0.7, 0, canvasWidth * 0.3, 0); // x-axis
                p.line(0, -canvasHeight/2, 0, canvasHeight/2); // y-axis

                // Draw axis labels
                p.scale(1, -1); // Temporarily invert for text
                p.noStroke();
                p.fill(0);
                p.textSize(12);
                p.textAlign(p.RIGHT, p.TOP);
                p.text("Global'x", canvasWidth * 0.3 - 10, -15);
                p.textAlign(p.LEFT, p.BOTTOM);
                p.text("Global'y", 5, -canvasHeight/2 + 5);
                p.scale(1, -1); // Restore inversion

                // Draw tick marks
                for (let i = -15; i <= 5; i++) {
                    if (i !== 0) {
                        p.line(i * scale, -3, i * scale, 3);
                        p.scale(1, -1);
                        p.textAlign(p.CENTER, p.TOP);
                        p.text(i, i * scale, 5);
                        p.scale(1, -1);
                    }
                }

                for (let i = -7; i <= 7; i++) {
                    if (i !== 0) {
                        p.line(-3, i * scale, 3, i * scale);
                        p.scale(1, -1);
                        p.textAlign(p.RIGHT, p.CENTER);
                        p.text(i, -5, -i * scale);
                        p.scale(1, -1);
                    }
                }

                // Draw central attracting body at origin
                p.fill(0);
                p.noStroke();
                p.circle(0, 0, 6);

                // Draw orbit path based on eccentricity
                p.stroke(74, 128, 181); // #4a80b5
                p.strokeWeight(2);
                p.noFill();

                if (eccentricity === 0) {
                    // Circle case
                    p.push();
                    p.translate(-2 * scale, 0);
                    p.circle(0, 0, 2 * 2 * scale);
                    p.pop();
                } else if (eccentricity < 1) {
                    // Ellipse case
                    p.beginShape();
                    for (let theta = 0; theta <= 2 * p.PI; theta += 0.01) {
                        let r = p_value / (1 + eccentricity * p.cos(theta));
                        let x = r * p.cos(theta);
                        let y = r * p.sin(theta);
                        p.vertex(x * scale, y * scale);
                    }
                    p.endShape(p.CLOSE);
                } else if (eccentricity === 1) {
                    // Parabola case
                    p.beginShape();
                    for (let theta = -2.2; theta <= 2.2; theta += 0.01) {
                        let r = p_value / (1 + eccentricity * p.cos(theta));
                        let x = r * p.cos(theta);
                        let y = r * p.sin(theta);
                        p.vertex(x * scale, y * scale);
                    }
                    p.endShape();
                } else {
                    // Hyperbola case
                    let thetaMin = -p.acos(-1 / eccentricity);
                    let thetaMax = p.acos(-1 / eccentricity);
                    p.beginShape();
                    for (let theta = thetaMin; theta <= thetaMax; theta += 0.01) {
                        let r = p_value / (1 + eccentricity * p.cos(theta));
                        let x = r * p.cos(theta);
                        let y = r * p.sin(theta);
                        p.vertex(x * scale, y * scale);
                    }
                    p.endShape();

                    // Draw the other branch of hyperbola
                    p.beginShape();
                    for (let theta = -thetaMax; theta >= thetaMin; theta -= 0.01) {
                        let r = p_value / (1 + eccentricity * p.cos(p.PI - theta));
                        let x = -r * p.cos(theta);
                        let y = r * p.sin(theta);
                        p.vertex(x * scale, y * scale);
                    }
                    p.endShape();
                }

                p.pop();
            };
        };

        // Create p5 instance
        let p5Instance = new p5(sketch);

        // Slider event listener
        const slider = document.getElementById('slider-eccentricity');
        const valueDisplay = document.getElementById('value-eccentricity');
        const energyValue = document.getElementById('energy-value');

        slider.addEventListener('input', function() {
            const value = parseFloat(this.value);
            valueDisplay.textContent = value.toFixed(1);

            // Update eccentricity
            if (p5Instance && p5Instance._renderer) {
                p5Instance._sketch.eccentricity = value;
                p5Instance.redraw();
            }

            // Update energy display
            const energy = -0.25 * (1 - value * value);

            if (value === 0) {
                energyValue.innerHTML = '\\(-\\frac{1}{4}\\)';
                setTimeout(() => {
                    if (window.MathJax) {
                        MathJax.typeset();
                    }
                }, 10);
            } else {
                energyValue.textContent = energy.toFixed(2);
            }
        });
    </script>
body>
