        const valueK = document.getElementById('value-k');
        const valueA = document.getElementById('value-a');

        // Helper function to format numbers
        function formatValue(value, id) {
            if (id === 'slider-poc' || id === 'slider-hg_m') {
                return parseFloat(value).toFixed(1);
            } else if (id === 'slider-eh') {
                return parseInt(value).toString();
            } else if (id === 'slider-k' || id === 'slider-a') {
                return parseFloat(value).toFixed(3);
            }
            return value;
        }

        // Update function for all plots
        function update() {
            // Update value displays
            valuePoc.textContent = formatValue(sliderPoc.value, 'slider-poc');
            valueEh.textContent = formatValue(sliderEh.value, 'slider-eh');
            valueHgM.textContent = formatValue(sliderHgM.value, 'slider-hg_m');
            valueK.textContent = formatValue(sliderK.value, 'slider-k');
            valueA.textContent = formatValue(sliderA.value, 'slider-a');

            // Get current slider values
