            const hgM = parseFloat(sliderHgM.value);
            const k = parseFloat(sliderK.value);
            const a = parseFloat(sliderA.value);

            // === Plot 1: MeHg index: variable POC ===
            const srb1 = Array.from({length: 101}, (_, i) => i);
            const C1 = 125 * (-eh + 200) * poc * hgM;
