            // === Plot 2: MI at river, estuarine and marine POC ===
            const srb2 = Array.from({length: 101}, (_, i) => i);
            const baseC = 125 * (-eh + 200) * hgM;

            // River (POC = 56)
            const CRiver = baseC * 56;
