                }]
            };

            Plotly.newPlot('plot3', [trace3], layout3, config);
        }

        // Add event listeners to all sliders
        sliderPoc.addEventListener('input', update);
        sliderEh.addEventListener('input', update);
        sliderHgM.addEventListener('input', update);
        sliderK.addEventListener('input', update);
        sliderA.addEventListener('input', update);

        // Initial update on page load
        update();
    </script>
