                    <button id="btn-adiabatic">adiabatic</button>
                    <button id="btn-discrete-adiabatic">discrete adiabatic</button>
                    <button id="btn-heuristic" class="active">heuristic</button>
                </div>
            </div>

            <div class="control-group">
                <div class="slider-container">
                    <label for="slider-total-steps">number of steps to complete the search</label>
                    <input type="range" id="slider-total-steps" min="1" max="200" step="1" value="10">
                    <span id="display-total-steps">10</span>
                </div>
            </div>

            <div class="control-group">
                <div class="slider-container">
                    <label for="slider-current-step" id="label-current-step">current step (0 to 10)</label>
                    <input type="range" id="slider-current-step" min="0" max="10" step="1" value="0">
                    <span id="display-current-step">0</span>
                </div>
            </div>
        </div>

        <div class="visualization-area">
            <div class="plot-container">
                <h4>amplitudes</h4>
                <div id="plot-amplitudes" class="plot"></div>
            </div>

            <div class="plot-container">
                <div class="plot-mode-selector">
                    <button id="btn-conflicts" class="active">conflicts</button>
                    <button id="btn-eigenvalues">eigenvalues</button>
                </div>
                <h4>probability</h4>
                <div id="plot-probability" class="plot"></div>
            </div>
        </div>
    </div>

    <script>
        // Global variables
        const N = 64; // Number of quantum states
        let solutionStates = [];
        let conflictNumbers = [];
        let currentAlgorithm = 'heuristic';
        let currentPlotMode = 'conflicts';

        // Algorithm default steps
        const algorithmDefaults = {
            'unstructured': 5,
            'adiabatic': 200,
            'discrete-adiabatic': 200,
            'heuristic': 10
        };

        // Initialize the application
        function init() {
            // Generate random solution states
            solutionStates = [];
            while (solutionStates.length < 2) {
                const state = Math.floor(Math.random() * N);
                if (!solutionStates.includes(state)) {
                    solutionStates.push(state);
                }
            }

            // Calculate conflict numbers (Hamming distance to nearest solution)
            conflictNumbers = [];
            for (let i = 0; i < N; i++) {
                if (solutionStates.includes(i)) {
                    conflictNumbers.push(-1); // Mark solutions
                } else {
                    let minDistance = Infinity;
                    for (const sol of solutionStates) {
                        const distance = calculateHammingDistance(i, sol);
                        minDistance = Math.min(minDistance, distance);
                    }
                    conflictNumbers.push(minDistance);
                }
            }

            // Set up event listeners
            setupEventListeners();

            // Initial update
            update();
        }

        // Calculate Hamming distance between two 6-bit indices
        function calculateHammingDistance(a, b) {
            let xor = a ^ b;
            let distance = 0;
            while (xor > 0) {
                distance += xor & 1;
                xor >>= 1;
            }
            return distance;
        }

        // Set up event listeners
        function setupEventListeners() {
            // Algorithm buttons
            document.getElementById('btn-unstructured').addEventListener('click', () => selectAlgorithm('unstructured'));
            document.getElementById('btn-adiabatic').addEventListener('click', () => selectAlgorithm('adiabatic'));
            document.getElementById('btn-discrete-adiabatic').addEventListener('click', () => selectAlgorithm('discrete-adiabatic'));
            document.getElementById('btn-heuristic').addEventListener('click', () => selectAlgorithm('heuristic'));

            // Sliders
            document.getElementById('slider-total-steps').addEventListener('input', onTotalStepsChange);
            document.getElementById('slider-current-step').addEventListener('input', onCurrentStepChange);

            // Plot mode buttons
            document.getElementById('btn-conflicts').addEventListener('click', () => selectPlotMode('conflicts'));
            document.getElementById('btn-eigenvalues').addEventListener('click', () => selectPlotMode('eigenvalues'));
        }

        // Select algorithm
        function selectAlgorithm(algorithm) {
            // Update button styles
            document.querySelectorAll('.algorithm-selector button').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-${algorithm}`).classList.add('active');

            // Update algorithm
            currentAlgorithm = algorithm;

            // Update total steps to default for this algorithm
            const defaultValue = algorithmDefaults[algorithm];
            document.getElementById('slider-total-steps').value = defaultValue;
            document.getElementById('display-total-steps').textContent = defaultValue;

            // Update current step max and value
            document.getElementById('slider-current-step').max = defaultValue;
            document.getElementById('slider-current-step').value = 0;
            document.getElementById('display-current-step').textContent = '0';

            // Update label
            updateCurrentStepLabel();

            // Update plots
            update();
        }

        // Handle total steps change
        function onTotalStepsChange() {
            const value = parseInt(document.getElementById('slider-total-steps').value);
            document.getElementById('display-total-steps').textContent = value;

            // Update current step max
            const currentStepSlider = document.getElementById('slider-current-step');
            currentStepSlider.max = value;

            // If current step exceeds new max, set it to max
            if (parseInt(currentStepSlider.value) > value) {
                currentStepSlider.value = value;
                document.getElementById('display-current-step').textContent = value;
            }

            updateCurrentStepLabel();
            update();
        }

        // Handle current step change
        function onCurrentStepChange() {
            const value = parseInt(document.getElementById('slider-current-step').value);
            document.getElementById('display-current-step').textContent = value;
            update();
        }

        // Update current step label
        function updateCurrentStepLabel() {
            const max = document.getElementById('slider-total-steps').value;
            document.getElementById('label-current-step').textContent = `current step (0 to ${max})`;
        }

        // Select plot mode
        function selectPlotMode(mode) {
            // Update button styles
            document.querySelectorAll('.plot-mode-selector button').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-${mode}`).classList.add('active');

            currentPlotMode = mode;
            update();
        }

        // Main update function
        function update() {
            const totalSteps = parseInt(document.getElementById('slider-total-steps').value);
            const currentStep = parseInt(document.getElementById('slider-current-step').value);

            // Calculate state vector
            const stateVector = calculateStateVector(currentAlgorithm, totalSteps, currentStep);

            // Update plots
            updateAmplitudesPlot(stateVector, currentStep);
            updateProbabilityPlot(stateVector);
        }

        // Calculate state vector based on algorithm
        function calculateStateVector(algorithm, totalSteps, currentStep) {
            const s = currentStep / totalSteps;

            // Initial state: uniform superposition
            let state = math.ones(N, 1).map(x => x / Math.sqrt(N));

            switch (algorithm) {
                case 'unstructured':
                    return calculateUnstructured(state, currentStep);
                case 'adiabatic':
                    return calculateAdiabatic(s);
                case 'discrete-adiabatic':
                    return calculateDiscreteAdiabatic(state, totalSteps, currentStep);
                case 'heuristic':
                    return calculateHeuristic(state, totalSteps, currentStep);
                default:
                    return state;
            }
        }

        // Unstructured algorithm (Grover's)
        function calculateUnstructured(initialState, k) {
            // Create oracle
            const U_w = math.diag(Array(N).fill(1).map((_, i) => solutionStates.includes(i) ? -1 : 1));

            // Create initial state projector
            const psi0 = math.ones(N, 1).map(x => x / Math.sqrt(N));
            const P_0 = math.multiply(psi0, math.transpose(psi0));
            const U_s = math.subtract(math.multiply(2, P_0), math.identity(N));

            // Grover operator
            const G = math.multiply(U_s, U_w);

            // Apply k times
            let state = initialState;
            for (let i = 0; i < k; i++) {
                state = math.multiply(G, state);
            }

            return state;
        }

        // Adiabatic algorithm
        function calculateAdiabatic(s) {
            // Initial Hamiltonian H_0 = -P_0
            const psi0 = math.ones(N, 1).map(x => x / Math.sqrt(N));
            const P_0 = math.multiply(psi0, math.transpose(psi0));
            const H_0 = math.multiply(-1, P_0);

            // Problem Hamiltonian H_p = diag(conflicts)
            const H_p = math.diag(conflictNumbers);

            // Time-dependent Hamiltonian
            const H = math.add(math.multiply(1 - s, H_0), math.multiply(s, H_p));

            // Find ground state (eigenvector with smallest eigenvalue)
            const eigen = math.eigs(H);
            let minIndex = 0;
            let minValue = eigen.values[0];

            for (let i = 1; i < eigen.values.length; i++) {
                if (eigen.values[i] < minValue) {
                    minValue = eigen.values[i];
                    minIndex = i;
                }
            }

            return math.squeeze(eigen.eigenvectors.map((val, idx) => idx === minIndex ? val : 0));
        }

        // Discrete adiabatic algorithm
        function calculateDiscreteAdiabatic(initialState, totalSteps, currentStep) {
            let state = initialState;

            for (let k = 0; k < currentStep; k++) {
                const s = (k + 1) / totalSteps;

                // Create Hamiltonian
                const psi0 = math.ones(N, 1).map(x => x / Math.sqrt(N));
                const P_0 = math.multiply(psi0, math.transpose(psi0));
                const H_0 = math.multiply(-1, P_0);
                const H_p = math.diag(conflictNumbers);
                const H = math.add(math.multiply(1 - s, H_0), math.multiply(s, H_p));

                // Evolution operator
                const U_k = math.expm(math.multiply(math.complex(0, -1), H));
                state = math.multiply(U_k, state);
            }

            return state;
        }

        // Heuristic algorithm (discrete adiabatic with T=10)
        function calculateHeuristic(initialState, totalSteps, currentStep) {
            return calculateDiscreteAdiabatic(initialState, 10, Math.floor(currentStep * 10 / totalSteps));
