                    colors.push('red');
                } else {
                    // Color mapping: high conflict = orange, low conflict = blue
                    const conflict = conflictNumbers[i];
                    if (conflict <= 1) colors.push('blue');
                    else if (conflict <= 2) colors.push('green');
                    else if (conflict <= 3) colors.push('yellow');
                    else colors.push('orange');
                }
            }

            const markerSize = stateVector.map((_, i) => solutionStates.includes(i) ? 10 : 6);

            traces.push({
                x: real,
                y: imag,
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    size: markerSize,
                    color: colors,
                    line: {
                        color: 'black',
                        width: 0.5
                    }
                },
                text: stateVector.map((_, i) => `State ${i}`),
                hoverinfo: 'text+x+y'
            });

            const layout = {
                title: '',
                xaxis: { title: 'Re', zeroline: true, gridcolor: '#e0e0e0' },
                yaxis: { title: 'Im', zeroline: true, gridcolor: '#e0e0e0' },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 40 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            Plotly.newPlot('plot-amplitudes', traces, layout, {responsive: true});
        }

        // Update probability plot
        function updateProbabilityPlot(stateVector) {
            const probabilities = stateVector.map(x => math.abs(x) ** 2);

