    <div id="main-container">
        <div id="controls-container">
            <div id="control-group-pressure" class="control-group">
                <label for="slider-pressure">pressure (bar)</label>
                <input type="range" id="slider-pressure" min="0.01" max="0.20" step="0.005" value="0.08">
                <span id="value-pressure">0.08</span>
            </div>

            <div id="control-group-temperature" class="control-group" style="display: none;">
                <label for="slider-temperature">temperature (K)</label>
                <input type="range" id="slider-temperature" min="450" max="490" step="1" value="475">
                <span id="value-temperature">475</span>
            </div>

            <div class="control-group">
                <label for="select-plot">Plot Type</label>
                <select id="select-plot">
                    <option value="vs_temp">fugacity versus temperature</option>
                    <option value="vs_press">fugacity versus pressure</option>
                </select>
            </div>

            <div id="checkbox-container" style="display: none;">
