    <script>
        // Initialize elements
        const selectPlot = document.getElementById('select-plot');
        const sliderPressure = document.getElementById('slider-pressure');
        const sliderTemperature = document.getElementById('slider-temperature');
        const checkboxHighPressure = document.getElementById('checkbox-high-pressure');
        const valuePressure = document.getElementById('value-pressure');
        const valueTemperature = document.getElementById('value-temperature');
        const controlGroupPressure = document.getElementById('control-group-pressure');
        const controlGroupTemperature = document.getElementById('control-group-temperature');
        const checkboxContainer = document.getElementById('checkbox-container');
        const plotContainer = document.getElementById('plot-container');

        // Event listeners
        selectPlot.addEventListener('change', handlePlotChange);
        sliderPressure.addEventListener('input', handlePressureChange);
        sliderTemperature.addEventListener('input', handleTemperatureChange);
        checkboxHighPressure.addEventListener('change', updatePlot);

        // Initial setup
        handlePlotChange();

        function handlePlotChange() {
            const plotType = selectPlot.value;

            if (plotType === 'vs_temp') {
                controlGroupPressure.style.display = 'flex';
                controlGroupTemperature.style.display = 'none';
                checkboxContainer.style.display = 'none';
            } else {
                controlGroupPressure.style.display = 'none';
                controlGroupTemperature.style.display = 'flex';
                checkboxContainer.style.display = 'flex';
            }

            updatePlot();
        }

        function handlePressureChange() {
            valuePressure.textContent = parseFloat(sliderPressure.value).toFixed(2);
            updatePlot();
        }

        function handleTemperatureChange() {
            valueTemperature.textContent = parseInt(sliderTemperature.value);
            updatePlot();
        }

        function updatePlot() {
            const plotType = selectPlot.value;

            if (plotType === 'vs_temp') {
                updateFugacityVsTemperature();
            } else {
                updateFugacityVsPressure();
            }
        }

        function updateFugacityVsTemperature() {
            const P = parseFloat(sliderPressure.value);

            // Calculate saturation temperature
            const T_sat = 5818 / (8.65 - Math.log10(P)) - 230;

            // Generate data points
            const T_min = 280;
            const T_max = 400;
            const T_values = [];
            const stableLiquid = [];
            const stableVapor = [];
            const metastableLiquid = [];
            const metastableVapor = [];

            for (let T = T_min; T <= T_max; T += 1) {
                T_values.push(T);
                const f_sat = Math.pow(10, 8.65 - 5818 / (230 + T));

                if (T <= T_sat) {
                    stableLiquid.push(f_sat);
                    metastableVapor.push(P);
                } else {
                    metastableLiquid.push(f_sat);
                    stableVapor.push(P);
                }
            }

            // Create traces
            const traces = [
                {
                    x: T_values.slice(0, Math.floor(T_sat - T_min) + 1),
                    y: stableLiquid.slice(0, Math.floor(T_sat - T_min) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#0000FF', width: 3 },
                    name: 'Stable Liquid'
                },
                {
                    x: T_values.slice(Math.floor(T_sat - T_min) + 1),
                    y: stableVapor.slice(Math.floor(T_sat - T_min) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#0000FF', width: 3 },
                    name: 'Stable Vapor'
                },
                {
                    x: T_values.slice(Math.floor(T_sat - T_min) + 1),
                    y: metastableLiquid.slice(Math.floor(T_sat - T_min) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                    name: 'Metastable Liquid'
                },
                {
                    x: T_values.slice(0, Math.floor(T_sat - T_min) + 1),
                    y: metastableVapor.slice(0, Math.floor(T_sat - T_min) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                    name: 'Metastable Vapor'
                },
                {
                    x: [T_sat],
                    y: [P],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: '#000000', size: 8 },
                    name: 'Saturation Point'
                },
                {
                    x: [T_sat, T_sat],
                    y: [0, P],
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#000000', width: 1, dash: 'dash' },
                    showlegend: false
                }
            ];

            const layout = {
                title: 'Fugacity versus Temperature',
                xaxis: {
                    title: 'temperature (°C)',
                    range: [280, 400],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'fugacity (bar)',
                    range: [0, 0.22],
                    gridcolor: '#e0e0e0'
                },
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
                font: { size: 14 },
                annotations: [
                    {
                        x: 310,
                        y: 0.01,
                        text: 'liquid',
                        showarrow: false,
                        font: { size: 16 }
                    },
                    {
                        x: 390,
                        y: P,
                        text: 'vapor',
                        showarrow: false,
                        font: { size: 16 }
                    },
                    {
                        x: T_sat,
                        y: P + 0.005,
                        text: 'T<sup>sat</sup>',
                        showarrow: false,
                        font: { size: 14 }
                    }
                ]
            };

            const config = { responsive: true };
            Plotly.react(plotContainer, traces, layout, config);
        }

        function updateFugacityVsPressure() {
            const T = parseFloat(sliderTemperature.value);
            const isHighPressure = checkboxHighPressure.checked;

            // Calculate saturation pressure
            const P_sat = Math.pow(10, 10.83 - 5047 / T);

            // Generate data points
            const P_min = 0;
            const P_max = 3.0;
            const P_values = [];
            const stableVapor = [];
            const stableLiquid = [];
            const metastableVapor = [];
            const metastableLiquid = [];

            for (let P = P_min; P <= P_max; P += 0.01) {
                P_values.push(P);

                let f_V;
                if (isHighPressure) {
                    f_V = P * Math.exp(-25 * P / T);
                } else {
                    f_V = P;
                }

                const f_sat = P_sat * (isHighPressure ? Math.exp(-25 * P_sat / T) : P_sat);

                if (P <= P_sat) {
                    stableVapor.push(f_V);
                    metastableLiquid.push(f_sat);
                } else {
                    metastableVapor.push(f_V);
                    stableLiquid.push(f_sat);
                }
            }

            const f_sat_value = P_sat * (isHighPressure ? Math.exp(-25 * P_sat / T) : P_sat);

            // Create traces
            const traces = [
                {
                    x: P_values.slice(0, Math.floor(P_sat / 0.01) + 1),
                    y: stableVapor.slice(0, Math.floor(P_sat / 0.01) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#0000FF', width: 3 },
                    name: 'Stable Vapor'
                },
                {
                    x: P_values.slice(Math.floor(P_sat / 0.01) + 1),
                    y: stableLiquid.slice(Math.floor(P_sat / 0.01) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#0000FF', width: 3 },
                    name: 'Stable Liquid'
                },
                {
                    x: P_values.slice(Math.floor(P_sat / 0.01) + 1),
                    y: metastableVapor.slice(Math.floor(P_sat / 0.01) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                    name: 'Metastable Vapor'
                },
                {
                    x: P_values.slice(0, Math.floor(P_sat / 0.01) + 1),
                    y: metastableLiquid.slice(0, Math.floor(P_sat / 0.01) + 1),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#A0A0FF', width: 2, dash: 'dash' },
                    name: 'Metastable Liquid'
                },
                {
                    x: [P_sat],
                    y: [f_sat_value],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: '#000000', size: 8 },
                    name: 'Saturation Point'
                },
                {
                    x: [P_sat, P_sat],
                    y: [0, f_sat_value],
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: '#000000', width: 1, dash: 'dash' },
                    showlegend: false
                }
            ];

            const layout = {
                title: 'Fugacity versus Pressure',
                xaxis: {
                    title: 'pressure (MPa)',
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'fugacity (MPa)', 
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0'
                },
                paper_bgcolor: 'white',
                plot_bgcolor: 'white',
                font: { size: 14 },
                annotations: [
                    {
                        x: 0.5,
                        y: 0.25,
                        text: 'vapor',
                        showarrow: false,
                        font: { size: 16 }
                    },
                    {
                        x: 2.7,
                        y: f_sat_value,
                        text: 'liquid',
                        showarrow: false,
                        font: { size: 16 }
                    },
                    {
                        x: P_sat,
                        y: f_sat_value + 0.1,
                        text: 'P<sup>sat</sup>',
                        showarrow: false,
                        font: { size: 14 }
                    }
                ]
            };

            const config = { responsive: true };
            Plotly.react(plotContainer, traces, layout, config);
        }
    </script>
