            <p>Incompatibility of target observables</p>
            <label for="slider-theta">θ</label>
            <input type="range" id="slider-theta" min="0" max="1.57079" step="0.001" value="0.44879">
            <span id="span-theta-value"></span>
            <span style="margin-left: 20px;">
                sin<sup>2</sup>θ = <span id="span-sinsq-theta-value"></span>
            </span>
        </div>

        <hr>

        <!-- Metric Error Measure Controls -->
        <div class="control-group">
            <p>Metric error measure</p>
            <input type="checkbox" id="checkbox-metric-circle">
            <label for="checkbox-metric-circle">display circle of equal metric error</label>
            <input type="checkbox" id="checkbox-metric-region" style="margin-left: 20px;">
            <label for="checkbox-metric-region">display region of joint measurability</label>
        </div>

        <hr>

        <!-- First-Order Optimization Controls -->
        <div class="control-group">
            <p>First-order optimization</p>
            <input type="checkbox" id="checkbox-first-order-vec">
            <label for="checkbox-first-order-vec">display first-order optimizing d vector</label>
            <input type="checkbox" id="checkbox-first-order-region" style="margin-left: 20px;">
            <label for="checkbox-first-order-region">display regions of joint measurability</label>
        </div>

        <hr>

        <!-- Optimal Approximators Controls -->
        <div class="control-group">
            <p>Optimal approximators</p>
            <input type="checkbox" id="checkbox-optimal-pair">
            <label for="checkbox-optimal-pair">display closest optimal pair of approximating vectors</label>
            <input type="checkbox" id="checkbox-optimal-region" style="margin-left: 20px;">
            <label for="checkbox-optimal-region">display regions of joint measurability</label>
        </div>
    </div>

    <!-- CDN Includes -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Global variables
        let theta = Math.PI / 7;
        let R = 150; // Radius of semi-circle
        let c = {x: R * 0.3, y: R * 0.7}; // Vector c position
        let a = {x: 0, y: 0}; // Vector a
        let b = {x: 0, y: 0}; // Vector b
        let d_opt = {x: 0, y: 0}; // Optimal d vector
        let dragging = false;
        let plotData = {};

        // Initialize and update values
        function updateTheta() {
            theta = parseFloat(document.getElementById('slider-theta').value);
            document.getElementById('span-theta-value').textContent = theta.toFixed(5);
            document.getElementById('span-sinsq-theta-value').textContent = Math.pow(Math.sin(theta), 2).toFixed(6);

            // Update vectors a and b
            a.x = R * Math.sin(theta);
            a.y = R * Math.cos(theta);
            b.x = -R * Math.sin(theta);
            b.y = R * Math.cos(theta);

            updateDOpt();
            updatePlot();
        }

        function updateDOpt() {
            // Calculate optimal d_opt based on c and b
            const cNorm = Math.sqrt(c.x * c.x + c.y * c.y) / R;
            const scaleFactor = Math.sqrt(1 - cNorm * cNorm);

            // Calculate d_opt as projection of b onto the ellipse constraint
            const bNorm = Math.sqrt(b.x * b.x + b.y * b.y) / R;
            const angle = Math.atan2(b.y, b.x);

            d_opt.x = b.x * scaleFactor;
            d_opt.y = b.y;
        }

        function drawArrow(p5, fromX, fromY, toX, toY, color) {
            p5.stroke(color);
            p5.strokeWeight(2);
            p5.line(fromX, fromY, toX, toY);

            // Calculate arrowhead
            const angle = Math.atan2(toY - fromY, toX - fromX);
            const arrowLength = 10;
            const arrowAngle = Math.PI / 6;

            p5.line(toX, toY, 
                   toX - arrowLength * Math.cos(angle - arrowAngle), 
                   toY - arrowLength * Math.sin(angle - arrowAngle));
            p5.line(toX, toY, 
                   toX - arrowLength * Math.cos(angle + arrowAngle), 
                   toY - arrowLength * Math.sin(angle + arrowAngle));
        }

        function drawLabel(p5, x, y, label) {
            p5.push();
            p5.noStroke();
            p5.fill(0);
            p5.textAlign(p5.CENTER);
            p5.text(label, x, y - 15);
            p5.pop();
        }

        // p5.js sketch
        const sketch = (p) => {
            p.setup = () => {
                const canvas = p.createCanvas(400, 400);
                canvas.parent('canvas-container');
                updateTheta();
            };

            p.draw = () => {
                p.background(255);

                // Set coordinate system with origin at bottom center
                p.translate(p.width / 2, p.height - 50);
                p.scale(1, -1);

                // Draw semi-circle
                p.noFill();
                p.stroke(0);
                p.strokeWeight(1);
                p.arc(0, 0, R * 2, R * 2, 0, p.PI);

                // Draw horizontal axis
                p.line(-R - 20, 0, R + 20, 0);

                // Draw vectors
                drawArrow(p, 0, 0, a.x, a.y, 'blue');
                drawLabel(p, a.x, a.y, 'a');

                drawArrow(p, 0, 0, b.x, b.y, 'red');
                drawLabel(p, b.x, b.y, 'b');

                drawArrow(p, 0, 0, c.x, c.y, 'green');
                drawLabel(p, c.x, c.y, 'c');

                // Draw optional elements based on checkboxes
                if (document.getElementById('checkbox-metric-circle').checked) {
                    const dist = Math.sqrt(Math.pow(c.x - a.x, 2) + Math.pow(c.y - a.y, 2));
                    p.drawingContext.setLineDash([5, 5]);
                    p.noFill();
                    p.stroke('blue');
                    p.ellipse(a.x, a.y, dist * 2, dist * 2);
                    p.drawingContext.setLineDash([]);
                }

                if (document.getElementById('checkbox-metric-region').checked || 
                    document.getElementById('checkbox-first-order-region').checked) {
                    p.drawingContext.setLineDash([5, 5]);
                    // Green arc for c
                    const cNorm = Math.sqrt(c.x * c.x + c.y * c.y);
                    p.noFill();
                    p.stroke('green');
                    p.arc(0, 0, cNorm * 2, cNorm * 2, 0, p.PI);

                    // Orange arc for d_opt
                    const dNorm = Math.sqrt(d_opt.x * d_opt.x + d_opt.y * d_opt.y);
                    p.stroke('orange');
                    p.arc(0, 0, dNorm * 2, dNorm * 2, 0, p.PI);
                    p.drawingContext.setLineDash([]);
                }

                if (document.getElementById('checkbox-first-order-vec').checked) {
                    p.drawingContext.setLineDash([5, 5]);
                    drawArrow(p, b.x, b.y, d_opt.x, d_opt.y, 'orange');
                    p.drawingContext.setLineDash([]);
                    drawLabel(p, (b.x + d_opt.x) / 2, (b.y + d_opt.y) / 2, 'd_err');
                }

                if (document.getElementById('checkbox-optimal-pair').checked) {
                    // Draw optimal vectors
                    const optX = R * Math.sin(theta) / 2;
                    const optY = R * Math.cos(theta) / 2;

                    drawArrow(p, 0, 0, optX, optY, 'purple');
                    drawLabel(p, optX, optY, 'c_opt');

                    drawArrow(p, 0, 0, -optX, optY, 'purple');
                    drawLabel(p, -optX, optY, 'd_opt');
                }
            };

            p.mousePressed = () => {
                const mx = p.mouseX - p.width / 2;
                const my = -(p.mouseY - (p.height - 50));

                // Check if clicking near vector c
                const dist = Math.sqrt(Math.pow(mx - c.x, 2) + Math.pow(my - c.y, 2));
                if (dist < 15) {
                    dragging = true;
                }
            };

            p.mouseDragged = () => {
                if (dragging) {
                    const mx = p.mouseX - p.width / 2;
                    const my = -(p.mouseY - (p.height - 50));

                    // Constrain c to be within semi-circle
                    const dist = Math.sqrt(mx * mx + my * my);
                    if (dist <= R && my >= 0) {
                        c.x = mx;
                        c.y = my;
                        updateDOpt();
                        updatePlot();
                    }
                }
            };

            p.mouseReleased = () => {
                dragging = false;
            };
        };

        // Initialize p5.js
        new p5(sketch);

        // Initialize Plotly.js
        function initPlot() {
            const layout = {
                title: 'Error Trade-off',
                xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                yaxis: { title: 'D(C, B)', range: [0, 1.5] },
                showlegend: true,
                width: 400,
                height: 400,
                margin: { l: 50, r: 50, t: 50, b: 50 }
            };

            updatePlot();
        }

        function updatePlot() {
            // Generate boundary curve
            const sin2theta = Math.sin(2 * theta);
            const xData = [];
            const yData = [];

            for (let i = 0; i <= 100; i++) {
                const x = (sin2theta * sin2theta) * i / 100;
                const y = Math.pow(sin2theta - Math.sqrt(x), 2);
                xData.push(x);
                yData.push(y);
            }

            // Calculate current errors
            const dCA = 0.5 * Math.pow(Math.sqrt(c.x*c.x + c.y*c.y)/R - Math.sin(theta), 2);
            const dDB = 0.5 * Math.pow(Math.abs(Math.sin(theta) - Math.abs(d_opt.x/R)), 2);

            // Calculate optimal point
            const optX = Math.pow(sin2theta / 2, 2);
            const optY = optX;

            const traces = [
                {
                    x: xData,
                    y: yData,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Boundary',
                    line: { color: 'blue' }
                },
                {
                    x: [dCA],
                    y: [dDB],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Current Error',
                    marker: { color: 'red', size: 10 }
                }
            ];

            // Add optimal point if checkbox is checked
            if (document.getElementById('checkbox-optimal-pair').checked) {
                traces.push({
                    x: [optX],
                    y: [optY],
                    type: 'scatter',
                    mode: 'markers',
                    name: 'Optimal Error',
                    marker: { color: 'green', size: 10 }
                });
            }

            Plotly.newPlot('plot-error', traces, 
                {title: 'Error Trade-off', 
                 xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                 yaxis: { title: 'D(D, B)', range: [0, 1.5] },
                 width: 400, height: 400});
        }

        // Event listeners
        document.getElementById('slider-theta').addEventListener('input', updateTheta);

        // All checkbox event listeners
        const checkboxes = [
            'checkbox-metric-circle',
            'checkbox-metric-region',
            'checkbox-first-order-vec',
            'checkbox-first-order-region',
            'checkbox-optimal-pair',
            'checkbox-optimal-region'
        ];

        checkboxes.forEach(id => {
            document.getElementById(id).addEventListener('change', () => {
                // Redraw p5.js canvas
                const canvas = document.getElementById('canvas-bloch');
                new p5(sketch);
                // Update plot if optimal-pair checkbox changed
                if (id === 'checkbox-optimal-pair') {
                    updatePlot();
                }
            });
        });

        // Initialize on load
        window.onload = () => {
            updateTheta();
            initPlot();
        };
    </script>
</body>
</html>
