            }

            // Draw Steiner points
            for (let point of steinerPoints) {
                fill(255);
                stroke(255, 0, 0);
                strokeWeight(2);
                circle(point.x, point.y, 16);
                circle(point.x, point.y, 8);
            }

            // Draw angles if enabled
            if (showAngles) {
                drawAngles();
            }
        }

        function initializePoints() {
            regularPoints = [];
            steinerPoints = [];

            // Initialize regular points in a regular polygon
            const centerX = canvasWidth / 2;
            const centerY = canvasHeight / 2;
            const radius = Math.min(canvasWidth, canvasHeight) * 0.35;

            for (let i = 0; i < numRegularPoints; i++) {
                const angle = (2 * Math.PI * i) / numRegularPoints - Math.PI / 2;
                regularPoints.push({
                    x: centerX + radius * Math.cos(angle),
                    y: centerY + radius * Math.sin(angle),
                    type: 'regular'
                });
            }

            // Initialize Steiner points at center
            for (let i = 0; i < numSteinerPoints; i++) {
                steinerPoints.push({
                    x: centerX,
                    y: centerY,
                    type: 'steiner'
                });
            }

            updateAllPoints();
        }

        function updateAllPoints() {
            allPoints = [...regularPoints, ...steinerPoints];
        }

        function calculateMST() {
            // Create all edges
            let edges = [];
            for (let i = 0; i < allPoints.length; i++) {
                for (let j = i + 1; j < allPoints.length; j++) {
                    edges.push({
                        from: allPoints[i],
                        to: allPoints[j],
                        weight: dist(allPoints[i].x, allPoints[i].y, allPoints[j].x, allPoints[j].y)
                    });
                }
            }

            // Sort edges by weight
            edges.sort((a, b) => a.weight - b.weight);

            // Kruskal's algorithm
            mstEdges = [];
            let sets = [];

            // Initialize disjoint sets
            for (let i = 0; i < allPoints.length; i++) {
                sets.push({ parent: i, rank: 0 });
            }

            function find(x) {
                if (sets[x].parent !== x) {
                    sets[x].parent = find(sets[x].parent);
                }
                return sets[x].parent;
            }

            function union(x, y) {
                let rootX = find(x);
                let rootY = find(y);

                if (rootX === rootY) return false;

                if (sets[rootX].rank < sets[rootY].rank) {
                    sets[rootX].parent = rootY;
                } else if (sets[rootX].rank > sets[rootY].rank) {
                    sets[rootY].parent = rootX;
                } else {
                    sets[rootY].parent = rootX;
                    sets[rootX].rank++;
                }

                return true;
            }

            for (let edge of edges) {
                let fromIndex = allPoints.indexOf(edge.from);
                let toIndex = allPoints.indexOf(edge.to);

                if (union(fromIndex, toIndex)) {
                    mstEdges.push(edge);
                    if (mstEdges.length === allPoints.length - 1) break;
                }
            }
        }

        function updateTotalLength() {
            totalLength = 0;
            for (let edge of mstEdges) {
                totalLength += edge.weight;
            }
            document.getElementById('text-total-length').textContent = totalLength.toFixed(5);
        }

        function drawAngles() {
            textAlign(CENTER, CENTER);
            textSize(14);
            fill(0);
            noStroke();

            for (let steiner of steinerPoints) {
                let neighbors = [];
                for (let edge of mstEdges) {
                    if (edge.from === steiner) neighbors.push(edge.to);
                    if (edge.to === steiner) neighbors.push(edge.from);
                }

                if (neighbors.length >= 2) {
                    // Calculate angles
                    let angles = [];
                    for (let neighbor of neighbors) {
                        let angle = Math.atan2(neighbor.y - steiner.y, neighbor.x - steiner.x);
                        angles.push({ point: neighbor, angle: angle });
                    }

                    // Sort by angle
                    angles.sort((a, b) => a.angle - b.angle);

                    // Calculate and display angles
                    for (let i = 0; i < angles.length; i++) {
                        let j = (i + 1) % angles.length;
                        let angleDiff = (angles[j].angle - angles[i].angle) * 180 / Math.PI;
                        if (angleDiff < 0) angleDiff += 360;

                        if (angleDiff < 180) {
                            let midAngle = (angles[i].angle + angles[j].angle) / 2;
                            let textX = steiner.x + 25 * Math.cos(midAngle);
                            let textY = steiner.y + 25 * Math.sin(midAngle);
                            text(Math.round(angleDiff) + '°', textX, textY);
                        }
                    }
                }
            }
        }

        function mousePressed() {
            let pointsToCheck = pointMode === 'regular' ? regularPoints : steinerPoints;

            for (let point of pointsToCheck) {
                if (dist(mouseX, mouseY, point.x, point.y) < 10) {
                    dragging = point;
                    dragOffset.x = mouseX - point.x;
                    dragOffset.y = mouseY - point.y;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (dragging) {
                dragging.x = mouseX - dragOffset.x;
                dragging.y = mouseY - dragOffset.y;
                calculateMST();
                updateTotalLength();
            }
        }

        function mouseReleased() {
            dragging = null;
        }

        // Event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Regular points buttons
            document.getElementById('btn-regular-3').addEventListener('click', function() {
                setActiveButton(this, 'regular-points-selector');
                numRegularPoints = 3;
                resetPoints();
            });

            document.getElementById('btn-regular-4').addEventListener('click', function() {
                setActiveButton(this, 'regular-points-selector');
                numRegularPoints = 4;
                resetPoints();
            });

            document.getElementById('btn-regular-5').addEventListener('click', function() {
                setActiveButton(this, 'regular-points-selector');
                numRegularPoints = 5;
                resetPoints();
            });

            // Steiner points buttons
            document.getElementById('btn-steiner-0').addEventListener('click', function() {
                setActiveButton(this, 'steiner-points-selector');
                numSteinerPoints = 0;
                resetPoints();
            });

            document.getElementById('btn-steiner-1').addEventListener('click', function() {
                setActiveButton(this, 'steiner-points-selector');
                numSteinerPoints = 1;
                resetPoints();
            });

            document.getElementById('btn-steiner-2').addEventListener('click', function() {
                setActiveButton(this, 'steiner-points-selector');
                numSteinerPoints = 2;
                resetPoints();
            });

            document.getElementById('btn-steiner-3').addEventListener('click', function() {
                setActiveButton(this, 'steiner-points-selector');
                numSteinerPoints = 3;
                resetPoints();
            });

            // Radio buttons
            document.getElementById('radio-regular').addEventListener('change', function() {
                if (this.checked) pointMode = 'regular';
            });

            document.getElementById('radio-steiner').addEventListener('change', function() {
                if (this.checked) pointMode = 'steiner';
            });

            // Checkbox
            document.getElementById('checkbox-show-angles').addEventListener('change', function() {
                showAngles = this.checked;
            });
        });

        function setActiveButton(button, containerId) {
            const container = document.getElementById(containerId);
            const buttons = container.getElementsByTagName('button');
            for (let btn of buttons) {
                btn.classList.remove('active');
            }
            button.classList.add('active');
        }

        function resetPoints() {
            initializePoints();
            calculateMST();
            updateTotalLength();
        }
    </script>
