    <script>
        // Global variables
        let canvas;
        let anovaData = {
            means: [0, 0, 0, 0],
            sds: [1, 1, 1, 1],
            grandMean: 0,
            msBetween: 0,
            msWithin: 0,
            fRatio: 0
        };

        const groups = ['W', 'X', 'Y', 'Z'];
        const k = 4; // number of groups
        const n = 10; // number of subjects per group

        // Initialize sliders
        function initializeSliders() {
            // Mean sliders
            const meanSliders = ['slider-mean-w', 'slider-mean-x', 'slider-mean-y', 'slider-mean-z'];
            const meanValues = ['value-mean-w', 'value-mean-x', 'value-mean-y', 'value-mean-z'];

            // SD sliders
            const sdSliders = ['slider-sd-w', 'slider-sd-x', 'slider-sd-y', 'slider-sd-z'];
            const sdValues = ['value-sd-w', 'value-sd-x', 'value-sd-y', 'value-sd-z'];

            // Add event listeners
            [...meanSliders, ...sdSliders].forEach(sliderId => {
                const slider = document.getElementById(sliderId);
                slider.addEventListener('input', updateAndRedraw);
            });

            // Initial update
            updateAndRedraw();
        }

        // Update values and perform ANOVA calculation
        function updateAndRedraw() {
            // Read all slider values
            anovaData.means = [
                parseFloat(document.getElementById('slider-mean-w').value),
                parseFloat(document.getElementById('slider-mean-x').value),
                parseFloat(document.getElementById('slider-mean-y').value),
                parseFloat(document.getElementById('slider-mean-z').value)
            ];

            anovaData.sds = [
                parseFloat(document.getElementById('slider-sd-w').value),
                parseFloat(document.getElementById('slider-sd-x').value),
                parseFloat(document.getElementById('slider-sd-y').value),
                parseFloat(document.getElementById('slider-sd-z').value)
            ];

            // Update displayed values
            document.getElementById('value-mean-w').textContent = anovaData.means[0].toFixed(1);
            document.getElementById('value-mean-x').textContent = anovaData.means[1].toFixed(1);
            document.getElementById('value-mean-y').textContent = anovaData.means[2].toFixed(1);
            document.getElementById('value-mean-z').textContent = anovaData.means[3].toFixed(1);

            document.getElementById('value-sd-w').textContent = anovaData.sds[0].toFixed(1);
            document.getElementById('value-sd-x').textContent = anovaData.sds[1].toFixed(1);
            document.getElementById('value-sd-y').textContent = anovaData.sds[2].toFixed(1);
            document.getElementById('value-sd-z').textContent = anovaData.sds[3].toFixed(1);

            // Perform ANOVA calculation
            performANOVACalculation();

            // Trigger redrawing if canvas exists
            if (canvas) {
                canvas.redraw();
            }
        }

        // Perform ANOVA calculation
        function performANOVACalculation() {
            // Calculate Grand Mean
            anovaData.grandMean = (anovaData.means[0] + anovaData.means[1] + 
                                   anovaData.means[2] + anovaData.means[3]) / 4;

            // Calculate Sum of Squares Between
            let ssBetween = 0;
            for (let i = 0; i < 4; i++) {
                ssBetween += Math.pow(anovaData.means[i] - anovaData.grandMean, 2);
            }
            ssBetween *= n;

            // Calculate Mean Square Between
            anovaData.msBetween = ssBetween / (k - 1);

            // Calculate Sum of Squares Within
            let ssWithin = 0;
            for (let i = 0; i < 4; i++) {
                ssWithin += Math.pow(anovaData.sds[i], 2) * (n - 1);
            }

            // Calculate Mean Square Within
            anovaData.msWithin = ssWithin / (n * k - k);

            // Calculate F-Ratio
            if (anovaData.msWithin > 0) {
                anovaData.fRatio = anovaData.msBetween / anovaData.msWithin;
            } else {
                anovaData.fRatio = Infinity;
            }
        }

        // p5.js sketch
        function sketch(p) {
            let topPlotHeight, bottomPlotY, plotLeft, plotWidth;

            p.setup = function() {
                const panel = document.getElementById('visualization-panel');
                const panelRect = panel.getBoundingClientRect();
                canvas = p.createCanvas(panelRect.width - 40, panelRect.height - 40);
                canvas.parent('p5-canvas');
                p.noLoop();

                // Layout calculations
                topPlotHeight = (p.height - 80) / 2;
                bottomPlotY = topPlotHeight + 60;
                plotLeft = 60;
                plotWidth = p.width - 100;
            };

            p.draw = function() {
                p.background(255);
                p.textAlign(p.LEFT, p.TOP);

                // Draw Group Data Plot (top half)
                drawGroupDataPlot(p);

                // Draw ANOVA Results Plot (bottom half)
                drawANOVAResultsPlot(p);
            };

            function drawGroupDataPlot(p) {
                const plotTop = 80;
                const plotHeight = topPlotHeight;
                const barWidth = 60;
                const barSpacing = plotWidth / 5;

                // Draw F-Ratio text
                p.textSize(18);
                p.fill(0);
                p.text(`F ratio = ${anovaData.fRatio.toFixed(anovaData.fRatio < 0.01 ? 4 : 2)}`, plotLeft, 20);

                // Draw axes
                p.stroke(0);
                p.line(plotLeft, plotTop, plotLeft, plotTop + plotHeight); // Y-axis
                p.line(plotLeft, plotTop + plotHeight, plotLeft + plotWidth, plotTop + plotHeight); // X-axis

                // Draw Y-axis labels and ticks
                p.textSize(12);
                p.textAlign(p.RIGHT, p.CENTER);
                for (let val = -10; val <= 10; val += 5) {
                    const y = plotTop + plotHeight/2 - (val * plotHeight/20);
                    p.text(val.toString(), plotLeft - 10, y);
                    p.line(plotLeft - 5, y, plotLeft, y);
                }

                // Draw X-axis labels
                p.textAlign(p.CENTER, p.TOP);
                for (let i = 0; i < 4; i++) {
                    const x = plotLeft + barSpacing * (i + 0.5);
                    p.text(groups[i], x, plotTop + plotHeight + 10);
                }

                // Draw Grand Mean line
                p.stroke(0);
                p.setLineDash([5, 5]);
                const grandMeanY = plotTop + plotHeight/2 - (anovaData.grandMean * plotHeight/20);
                p.line(plotLeft, grandMeanY, plotLeft + plotWidth, grandMeanY);
                p.setLineDash([]);

                // Draw group bars
                for (let i = 0; i < 4; i++) {
                    const x = plotLeft + barSpacing * (i + 0.5) - barWidth/2;
                    const mean = anovaData.means[i];
                    const sd = anovaData.sds[i];

                    const meanY = plotTop + plotHeight/2 - (mean * plotHeight/20);
                    const topY = plotTop + plotHeight/2 - ((mean + sd) * plotHeight/20);
                    const bottomY = plotTop + plotHeight/2 - ((mean - sd) * plotHeight/20);

                    // Draw orange bar
                    p.fill('#F57F29');
                    p.noStroke();
                    p.rect(x, topY, barWidth, bottomY - topY);

                    // Draw mean line
                    p.stroke(0);
                    p.line(x, meanY, x + barWidth, meanY);
                }
            }

            function drawANOVAResultsPlot(p) {
                const plotTop = bottomPlotY + 40;
                const plotHeight = (p.height - 60) - bottomPlotY - 40;
                const barHeight = 30;
                const barSpacing = plotHeight / 4;
                const maxValue = 200;

                // Draw title
                p.textAlign(p.LEFT, p.TOP);
                p.textSize(16);
                p.fill(0);
                p.text('Analysis of the F-Ratio', plotLeft, bottomPlotY);

                // Draw axes
                p.stroke(0);
                p.line(plotLeft, plotTop, plotLeft, plotTop + plotHeight); // Y-axis
                p.line(plotLeft, plotTop + plotHeight, plotLeft + plotWidth, plotTop + plotHeight); // X-axis

                // Draw X-axis labels and ticks
                p.textSize(12);
                p.textAlign(p.CENTER, p.TOP);
                for (let val = 0; val <= 200; val += 50) {
                    const x = plotLeft + (val / 200) * plotWidth;
                    p.text(val.toString(), x, plotTop + plotHeight + 10);
                    p.line(x, plotTop + plotHeight, x, plotTop + plotHeight + 5);
                }

                // Draw bars
                const data = [
                    { label: 'MS Between', value: anovaData.msBetween, color: '#4691A5' },
                    { label: 'MS Within', value: anovaData.msWithin, color: '#D49D3C' },
                    { label: 'F-Ratio', value: anovaData.fRatio, color: '#8C3A8D' }
                ];

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i < 3; i++) {
                    const y = plotTop + barSpacing * (i + 0.5);
                    const barWidth = Math.min((data[i].value / maxValue) * plotWidth, plotWidth);

                    // Draw label
                    p.textAlign(p.RIGHT, p.CENTER);
                    p.fill(0);
                    p.text(data[i].label, plotLeft - 10, y);

                    // Draw bar
                    p.fill(data[i].color);
                    p.noStroke();
                    p.rect(plotLeft, y - barHeight/2, barWidth, barHeight);
                }
            }

            p.windowResized = function() {
                const panel = document.getElementById('visualization-panel');
                const panelRect = panel.getBoundingClientRect();
                p.resizeCanvas(panelRect.width - 40, panelRect.height - 40);

                topPlotHeight = (p.height - 80) / 2;
                bottomPlotY = topPlotHeight + 60;
                plotLeft = 60;
                plotWidth = p.width - 100;
            };
        }

        // Initialize when DOM is loaded
        document.addEventListener('DOMContentLoaded', function() {
            initializeSliders();
            new p5(sketch);
        });
    </script>
</body>
