                        <input type="range" id="slider-xi2" min="-0.5" max="0.5" step="0.01" value="-0.05">
                        <input type="text" id="display-xi2" readonly value="-0.05">
                    </div>
                </div>
            </div>

            <div>
                <h3>return level curves</h3>
                <div class="slider-group">
                    <label for="slider-pmax">P_max</label>
                    <div class="slider-container">
                        <input type="range" id="slider-pmax" min="10" max="1000" step="1" value="100">
                        <input type="text" id="display-pmax" readonly value="100">
                    </div>
                </div>
                <div id="plot-return-level"></div>
            </div>
        </div>

        <div id="pdf-plot-area">
            <div id="plot-pdf"></div>
        </div>
    </div>

    <script>
        // Get all slider elements
        const sliders = {
            mu1: document.getElementById('slider-mu1'),
            sigma1: document.getElementById('slider-sigma1'),
            xi1: document.getElementById('slider-xi1'),
            mu2: document.getElementById('slider-mu2'),
            sigma2: document.getElementById('slider-sigma2'),
            xi2: document.getElementById('slider-xi2'),
            pmax: document.getElementById('slider-pmax')
        };

        // Get all display elements
        const displays = {
            mu1: document.getElementById('display-mu1'),
            sigma1: document.getElementById('display-sigma1'),
            xi1: document.getElementById('display-xi1'),
            mu2: document.getElementById('display-mu2'),
            sigma2: document.getElementById('display-sigma2'),
            xi2: document.getElementById('display-xi2'),
            pmax: document.getElementById('display-pmax')
        };

        // GEV PDF calculation
        function gevPDF(x, mu, sigma, xi) {
            if (xi === 0) {
                // Gumbel case
                const t = Math.exp(-(x - mu) / sigma);
                return (1 / sigma) * t * Math.exp(-t);
            } else {
                const z = 1 + xi * ((x - mu) / sigma);
                if (z <= 0) return 0;
                const t = Math.pow(z, -1 / xi);
                return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }
        }

        // GEV Return Level calculation
        function gevReturnLevel(p, mu, sigma, xi) {
            if (xi === 0) {
                // Gumbel case
                return mu - sigma * Math.log(-Math.log(1 - 1 / p));
            } else {
                return mu - (sigma / xi) * (1 - Math.pow(-Math.log(1 - 1 / p), -xi));
            }
        }

        // Update function
        function update() {
            // Update displays
            for (let key in sliders) {
                displays[key].value = sliders[key].value;
            }

            // Get current values
            const mu1 = parseFloat(sliders.mu1.value);
            const sigma1 = parseFloat(sliders.sigma1.value);
            const xi1 = parseFloat(sliders.xi1.value);
            const mu2 = parseFloat(sliders.mu2.value);
            const sigma2 = parseFloat(sliders.sigma2.value);
            const xi2 = parseFloat(sliders.xi2.value);
            const pmax = parseFloat(sliders.pmax.value);

            // Update PDF plot
            const xValues = [];
            for (let x = 2.5; x <= 7; x += 0.01) {
                xValues.push(x);
            }

            const pdf1 = xValues.map(x => gevPDF(x, mu1, sigma1, xi1));
            const pdf2 = xValues.map(x => gevPDF(x, mu2, sigma2, xi2));

            const pdfData = [
                {
                    x: xValues,
                    y: pdf1,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue' },
                    fill: 'tozeroy',
                    fillcolor: 'rgba(0, 0, 255, 0.2)',
                    name: 'GEV 1'
                },
                {
                    x: xValues,
                    y: pdf2,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red' },
                    fill: 'tozeroy',
                    fillcolor: 'rgba(255, 0, 0, 0.2)',
                    name: 'GEV 2'
                }
            ];

            const pdfLayout = {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: { range: [2.5, 7], showgrid: false },
                yaxis: { showgrid: false },
                margin: { t: 50, r: 20, b: 20, l: 20 },
                showlegend: false
            };

            Plotly.newPlot('plot-pdf', pdfData, pdfLayout, { responsive: true });

            // Update Return Level plot
            const pValues = [];
            for (let p = 1; p <= pmax; p += 0.5) {
                pValues.push(p);
            }

            const returnLevel1 = pValues.map(p => gevReturnLevel(p, mu1, sigma1, xi1));
            const returnLevel2 = pValues.map(p => gevReturnLevel(p, mu2, sigma2, xi2));

            const returnLevelData = [
                {
                    x: pValues,
                    y: returnLevel1,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue' },
                    name: 'GEV 1'
                },
                {
                    x: pValues,
                    y: returnLevel2,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red' },
                    name: 'GEV 2'
                }
            ];

            const returnLevelLayout = {
                xaxis: { 
                    title: 'Return Period (years)', 
                    range: [0, pmax],
                    showgrid: false
                },
                yaxis: { 
                    title: 'Maximum Sea Level (meters)', 
                    showgrid: false
                },
                margin: { t: 20, r: 20, b: 50, l: 60 },
                showlegend: false
            };

            Plotly.newPlot('plot-return-level', returnLevelData, returnLevelLayout, { responsive: true });
        }

        // Add event listeners to all sliders
        for (let key in sliders) {
            sliders[key].addEventListener('input', update);
        }

        // Initial update
        update();
    </script>
</body>
</html>
