        <div id="plot-bottom" class="plot-container"></div>
    </div>

    <script>
        // Signal definitions
        const signals = {
            triangle: [
                [0, 0], [3, 4], [6, 0]
            ],
            tophat: [
                [1, 0], [1, 4], [5, 4], [5, 0]
            ],
            stairstep: [
                [1, 0], [1, 1], [2, 1], [2, 4], [4, 4], [4, 1], [5, 1], [5, 0]
            ],
            rampdown: [
                [0, 4], [6, 0]
            ],
            uh: [
                [[1, 0], [1, 4], [1.5, 4], [1.5, 1], [2.5, 1], [2.5, 4], [3, 4], [3, 0]],
                [[4, 0], [4, 4], [4.5, 4], [4.5, 2], [5.5, 2], [5.5, 4], [6, 4], [6, 0]]
            ]
        };

        let currentSignal = 'triangle';
        let a = 2;
        let b = 4;
        let T = 2;

        // Initialize MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['$', '$'], ['\\(', '\\)']],
                displayMath: [['$$', '$$'], ['\\[', '\\]']]
            }
        };

        // Function to generate signal data points
        function generateSignalData(signalType, transform = null) {
            let points = signals[signalType];
            let data = [];

            if (signalType === 'uh') {
                // UH signal has two separate parts
                points.forEach(part => {
                    let partData = [];
                    for (let i = 0; i < part.length - 1; i++) {
                        let t1 = part[i][0];
                        let t2 = part[i + 1][0];
                        let v1 = part[i][1];
                        let v2 = part[i + 1][1];

                        // Generate points along the line segment
                        for (let t = t1; t <= t2; t += 0.1) {
                            let v = v1 + (v2 - v1) * (t - t1) / (t2 - t1);
                            let transformedT = t;

                            if (transform === 'y') {
                                transformedT = (t + b) / a;
                            } else if (transform === 'z') {
                                transformedT = t + b;
                            } else if (transform === 'w') {
                                transformedT = t / a;
                            }

                            partData.push([transformedT, v]);
                        }
                    }
                    data.push(partData);
                });
                return data;
            } else {
                // Other signals
                for (let i = 0; i < points.length - 1; i++) {
                    let t1 = points[i][0];
                    let t2 = points[i + 1][0];
                    let v1 = points[i][1];
                    let v2 = points[i + 1][1];

                    // Generate points along the line segment
                    for (let t = t1; t <= t2; t += 0.1) {
                        let v = v1 + (v2 - v1) * (t - t1) / (t2 - t1);
                        let transformedT = t;

                        if (transform === 'y') {
                            transformedT = (t + b) / a;
                        } else if (transform === 'z') {
                            transformedT = t + b;
                        } else if (transform === 'w') {
                            transformedT = t / a;
                        }

                        data.push([transformedT, v]);
                    }
                }
                return data;
            }
        }

        // Function to update plots
        function updatePlots() {
            const tRange = [];
            for (let t = -10; t <= 20; t += 0.1) {
                tRange.push(t);
            }

            // Generate signal data
            let xData = generateSignalData(currentSignal);
            let yData = generateSignalData(currentSignal, 'y');
            let zData = generateSignalData(currentSignal, 'z');
            let wData = generateSignalData(currentSignal, 'w');

            // Prepare traces for top plot
            let topTraces = [];

            if (currentSignal === 'uh') {
                xData.forEach(part => {
                    let x = part.map(p => p[0]);
                    let y = part.map(p => p[1]);
                    topTraces.push({
                        x: x,
                        y: y,
                        mode: 'lines',
                        line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                        name: '$x(t)$',
                        showlegend: false
                    });
                });
            } else {
                let x = xData.map(p => p[0]);
                let y = xData.map(p => p[1]);
                topTraces.push({
                    x: x,
                    y: y,
                    mode: 'lines',
                    line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                    name: '$x(t)$'
                });
            }

            // Prepare traces for bottom plot
            let bottomTraces = [];

            // x(t)
            if (document.getElementById('check-x').checked) {
                if (currentSignal === 'uh') {
                    xData.forEach(part => {
                        let x = part.map(p => p[0]);
                        let y = part.map(p => p[1]);
                        bottomTraces.push({
                            x: x,
                            y: y,
                            mode: 'lines',
                            line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                            name: '$x(t)$',
                            showlegend: false
                        });
                    });
                } else {
                    let x = xData.map(p => p[0]);
                    let y = xData.map(p => p[1]);
                    bottomTraces.push({
                        x: x,
                        y: y,
                        mode: 'lines',
                        line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                        name: '$x(t)$'
                    });
                }
            }

            // y(t)
            if (document.getElementById('check-y').checked) {
                if (currentSignal === 'uh') {
                    yData.forEach(part => {
                        let x = part.map(p => p[0]);
                        let y = part.map(p => p[1]);
                        bottomTraces.push({
                            x: x,
                            y: y,
                            mode: 'lines',
                            line: { color: 'red', width: 2.5 },
                            name: '$y(t) = x(at-b) = x(a(t-T))$',
                            showlegend: false
                        });
                    });
                } else {
                    let x = yData.map(p => p[0]);
                    let y = yData.map(p => p[1]);
                    bottomTraces.push({
                        x: x,
                        y: y,
                        mode: 'lines',
                        line: { color: 'red', width: 2.5 },
                        name: '$y(t) = x(at-b) = x(a(t-T))$'
                    });
                }
            }

            // z(t)
            if (document.getElementById('check-z').checked) {
                if (currentSignal === 'uh') {
                    zData.forEach(part => {
                        let x = part.map(p => p[0]);
                        let y = part.map(p => p[1]);
                        bottomTraces.push({
                            x: x,
                            y: y,
                            mode: 'lines',
                            line: { color: 'olivedrab', width: 2.5 },
                            name: '$z(t) = x(t-b)$',
                            showlegend: false
                        });
                    });
                } else {
                    let x = zData.map(p => p[0]);
                    let y = zData.map(p => p[1]);
                    bottomTraces.push({
                        x: x,
                        y: y,
                        mode: 'lines',
                        line: { color: 'olivedrab', width: 2.5 },
                        name: '$z(t) = x(t-b)$'
                    });
                }
            }

            // w(t)
            if (document.getElementById('check-w').checked) {
                if (currentSignal === 'uh') {
                    wData.forEach(part => {
                        let x = part.map(p => p[0]);
                        let y = part.map(p => p[1]);
                        bottomTraces.push({
                            x: x,
                            y: y,
                            mode: 'lines',
                            line: { color: 'saddlebrown', width: 2.5 },
                            name: '$w(t) = x(at)$',
                            showlegend: false
                        });
                    });
                } else {
                    let x = wData.map(p => p[0]);
                    let y = wData.map(p => p[1]);
                    bottomTraces.push({
                        x: x,
                        y: y,
                        mode: 'lines',
                        line: { color: 'saddlebrown', width: 2.5 },
                        name: '$w(t) = x(at)$'
                    });
                }
            }

            // Layout for both plots
            const layout = {
                xaxis: {
                    title: 'time (s)',
                    range: [-10, 20],
                    zeroline: true
                },
                yaxis: {
                    range: [-0.5, 4.5],
                    zeroline: true
                },
                showlegend: true,
                shapes: [
                    {
                        type: 'circle',
                        xref: 'x',
                        yref: 'y',
                        x0: T - 0.1,
                        y0: -0.1,
                        x1: T + 0.1,
                        y1: 0.1,
                        fillcolor: 'red',
                        line: { color: 'red' }
                    },
                    {
                        type: 'circle',
                        xref: 'x',
                        yref: 'y',
                        x0: b - 0.1,
                        y0: -0.1,
                        x1: b + 0.1,
                        y1: 0.1,
                        fillcolor: 'green',
                        line: { color: 'green' }
                    }
                ],
                annotations: [
                    {
                        x: T,
                        y: 0.3,
                        text: 'T',
                        showarrow: false,
                        font: { color: 'red', size: 14 }
                    },
                    {
                        x: b,
                        y: 0.3,
                        text: 'b',
                        showarrow: false,
                        font: { color: 'green', size: 14 }
                    }
                ]
            };

            const topLayout = {
                ...layout,
                title: 'signal',
                legend: { x: 0.01, y: 0.75 }
            };

            const bottomLayout = {
                ...layout,
                legend: { x: 0.01, y: 0.98 }
            };

            // Update plots
            Plotly.newPlot('plot-top', topTraces, topLayout, {responsive: true});
            Plotly.newPlot('plot-bottom', bottomTraces, bottomLayout, {responsive: true});
        }

        // Event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Signal buttons
            document.querySelectorAll('.signal-btn').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('.signal-btn').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentSignal = this.id.replace('btn-', '');
                    updatePlots();
                });
            });

            // Slider a
            document.getElementById('slider-a').addEventListener('input', function() {
                a = parseFloat(this.value);
                document.getElementById('slider-a-value').textContent = a.toFixed(1);
                T = b / a;
                document.getElementById('slider-t').value = T;
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
                updatePlots();
            });

            // Slider b
            document.getElementById('slider-b').addEventListener('input', function() {
                b = parseFloat(this.value);
                document.getElementById('slider-b-value').textContent = b.toFixed(1);
                T = b / a;
                document.getElementById('slider-t').value = T;
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
                updatePlots();
            });

            // Slider T
            document.getElementById('slider-t').addEventListener('input', function() {
                T = parseFloat(this.value);
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
                b = a * T;
                document.getElementById('slider-b').value = b;
                document.getElementById('slider-b-value').textContent = b.toFixed(1);
                updatePlots();
            });

            // Checkboxes
            document.getElementById('check-x').addEventListener('change', function() {
                Plotly.restyle('plot-bottom', {visible: this.checked}, [0]);
            });

            document.getElementById('check-y').addEventListener('change', function() {
                const index = document.getElementById('check-x').checked ? 1 : 0;
                Plotly.restyle('plot-bottom', {visible: this.checked}, [index]);
            });

            document.getElementById('check-z').addEventListener('change', function() {
                let index = 0;
                if (document.getElementById('check-x').checked) index++;
                if (document.getElementById('check-y').checked) index++;
                Plotly.restyle('plot-bottom', {visible: this.checked}, [index]);
            });

            document.getElementById('check-w').addEventListener('change', function() {
                let index = 0;
                if (document.getElementById('check-x').checked) index++;
                if (document.getElementById('check-y').checked) index++;
                if (document.getElementById('check-z').checked) index++;
                Plotly.restyle('plot-bottom', {visible: this.checked}, [index]);
            });

            // Initial plot
            updatePlots();
        });
    </script>
</body>
</html>
