    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let mainGroup;

        // Initialize the scene
        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            const aspect = window.innerWidth / window.innerHeight;
            camera = new THREE.PerspectiveCamera(45, aspect, 0.1, 1000);
            camera.position.set(5, 5, 5);

            // Renderer setup
            const canvas = document.getElementById('main-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Controls setup
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting setup
            const hemiLight = new THREE.HemisphereLight(0xffffff, 0x444444, 0.6);
            scene.add(hemiLight);

            const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight.position.set(5, 10, 5);
            scene.add(dirLight);

            // Main group for all generated objects
            mainGroup = new THREE.Group();
            scene.add(mainGroup);

            // Initial scene update
            updateScene();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Create curved triangular geometry
        function createCurvedTriangle(trim, centerColor, edgeColor) {
            const radius = 1;
            const segments = 20;

            // Define base triangle vertices on sphere
            const angle1 = 0;
            const angle2 = 2 * Math.PI / 3;
            const angle3 = 4 * Math.PI / 3;

            const v1 = new THREE.Vector3(
                radius * Math.sin(angle1),
                radius * Math.cos(angle1),
                0
            );
            const v2 = new THREE.Vector3(
                radius * Math.sin(angle2),
                radius * Math.cos(angle2),
                0
            );
            const v3 = new THREE.Vector3(
                radius * Math.sin(angle3),
                radius * Math.cos(angle3),
                0
            );

            // Create curved edges
            const vertices = [];
            const colors = [];

            // Add center vertex
            const center = new THREE.Vector3(0, 0, 0);
            vertices.push(center.x, center.y, center.z);
            colors.push(...hexToRgb(centerColor));

            // Generate vertices along curved edges
            for (let i = 0; i <= segments; i++) {
                const t = i / segments;

                // Interpolate between vertices with spherical curvature
                const p1 = slerp(v1, v2, t, trim);
                const p2 = slerp(v2, v3, t, trim);
                const p3 = slerp(v3, v1, t, trim);

                vertices.push(p1.x, p1.y, p1.z);
                colors.push(...hexToRgb(edgeColor));

                vertices.push(p2.x, p2.y, p2.z);
                colors.push(...hexToRgb(edgeColor));

                vertices.push(p3.x, p3.y, p3.z);
                colors.push(...hexToRgb(edgeColor));
            }

            // Create faces
            const indices = [];
            for (let i = 0; i < segments; i++) {
                const base = 1 + i * 3;

                // Triangle fan from center
                indices.push(0, base, base + 3);
                indices.push(0, base + 1, base + 4);
                indices.push(0, base + 2, base + 5);

                // Connect edge vertices
                if (i < segments - 1) {
                    indices.push(base, base + 3, base + 1);
                    indices.push(base + 1, base + 3, base + 4);
                    indices.push(base + 1, base + 4, base + 2);
                    indices.push(base + 2, base + 4, base + 5);
                    indices.push(base + 2, base + 5, base);
                    indices.push(base, base + 5, base + 3);
                }
            }

            // Create geometry
            const geometry = new THREE.BufferGeometry();
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            geometry.setIndex(indices);
            geometry.computeVertexNormals();

            return geometry;
        }

        // Spherical linear interpolation with trim control
        function slerp(v1, v2, t, trim) {
            const dot = v1.dot(v2);
            const theta = Math.acos(Math.min(Math.max(dot, -1), 1));
            const sinTheta = Math.sin(theta);

            if (sinTheta < 0.001) {
                return v1.clone().lerp(v2, t);
            }

            const w1 = Math.sin((1 - t) * theta * trim) / sinTheta;
            const w2 = Math.sin(t * theta * trim) / sinTheta;

            return v1.clone().multiplyScalar(w1).add(v2.clone().multiplyScalar(w2)).normalize();
        }

        // Convert hex color to RGB array
        function hexToRgb(hex) {
            const r = ((hex >> 16) & 255) / 255;
            const g = ((hex >> 8) & 255) / 255;
            const b = (hex & 255) / 255;
            return [r, g, b];
        }

        // Create outline for curved triangle
        function createOutline(trim) {
            const radius = 1;
            const segments = 30;
            const vertices = [];

            const angle1 = 0;
            const angle2 = 2 * Math.PI / 3;
            const angle3 = 4 * Math.PI / 3;

            const v1 = new THREE.Vector3(
                radius * Math.sin(angle1),
                radius * Math.cos(angle1),
                0
            );
            const v2 = new THREE.Vector3(
                radius * Math.sin(angle2),
                radius * Math.cos(angle2),
                0
            );
            const v3 = new THREE.Vector3(
                radius * Math.sin(angle3),
                radius * Math.cos(angle3),
                0
            );

            // Generate edge vertices
            for (let i = 0; i <= segments; i++) {
                const t = i / segments;
                const p1 = slerp(v1, v2, t, trim);
                vertices.push(p1.x, p1.y, p1.z);
            }

            for (let i = 0; i <= segments; i++) {
                const t = i / segments;
                const p2 = slerp(v2, v3, t, trim);
                vertices.push(p2.x, p2.y, p2.z);
            }

            for (let i = 0; i <= segments; i++) {
                const t = i / segments;
                const p3 = slerp(v3, v1, t, trim);
                vertices.push(p3.x, p3.y, p3.z);
            }

            const geometry = new THREE.BufferGeometry();
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

            const material = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 });
            return new THREE.LineSegments(geometry, material);
        }

        // Update the entire scene
        function updateScene() {
            // Clear existing objects
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Get control values
            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trim = parseFloat(document.getElementById('slider-trim').value);
            const showDouble = document.getElementById('checkbox-double').checked;
            const showSphere = document.getElementById('checkbox-sphere').checked;
            const showTriangles = document.getElementById('checkbox-triangles').checked;

            // Basis vectors for triangular lattice
            const basis1 = new THREE.Vector3(1.5, 0, 0);
            const basis2 = new THREE.Vector3(0.75, 1.299, 0);

            // Generate main lattice
            for (let i = -unitsX; i <= unitsX; i++) {
                for (let j = -unitsY; j <= unitsY; j++) {
                    const pos = new THREE.Vector3()
                        .addScaledVector(basis1, i)
                        .addScaledVector(basis2, j);

                    // Create curved triangle
                    const geometry = createCurvedTriangle(trim, 0xffc0cb, 0x904090);
                    const material = new THREE.MeshStandardMaterial({
                        vertexColors: true,
                        side: THREE.DoubleSide
                    });
                    const mesh = new THREE.Mesh(geometry, material);
                    mesh.position.copy(pos);

                    // Rotate alternating triangles
                    if ((i + j) % 2 === 1) {
                        mesh.rotation.z = Math.PI;
                    }

                    mainGroup.add(mesh);

                    // Add outline
                    const outline = createOutline(trim);
                    outline.position.copy(pos);
                    if ((i + j) % 2 === 1) {
                        outline.rotation.z = Math.PI;
                    }
                    mainGroup.add(outline);

                    // Create double pattern if enabled
                    if (showDouble) {
                        const doublePos = pos.clone()
                            .addScaledVector(basis1, 0.5)
                            .addScaledVector(basis2, 0.5);

                        const doubleGeometry = createCurvedTriangle(trim, 0x80ffff, 0x008080);
                        const doubleMaterial = new THREE.MeshStandardMaterial({
                            vertexColors: true,
                            side: THREE.DoubleSide
                        });
                        const doubleMesh = new THREE.Mesh(doubleGeometry, doubleMaterial);
                        doubleMesh.position.copy(doublePos);
                        doubleMesh.rotation.z = Math.PI;
                        mainGroup.add(doubleMesh);

                        const doubleOutline = createOutline(trim);
                        doubleOutline.position.copy(doublePos);
                        doubleOutline.rotation.z = Math.PI;
                        mainGroup.add(doubleOutline);
                    }

                    // Add flat triangles if enabled
                    if (showTriangles) {
                        const triangleGeometry = new THREE.BufferGeometry();
                        const vertices = [
                            0, 0.866, 0,
                            -0.75, -0.433, 0,
                            0.75, -0.433, 0
                        ];
                        triangleGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

                        const triangleMaterial = new THREE.MeshBasicMaterial({
                            color: 0x800080,
                            transparent: true,
                            opacity: 0.4,
                            side: THREE.DoubleSide
                        });
                        const triangleMesh = new THREE.Mesh(triangleGeometry, triangleMaterial);
                        triangleMesh.position.copy(pos);
                        if ((i + j) % 2 === 1) {
                            triangleMesh.rotation.z = Math.PI;
                        }
                        mainGroup.add(triangleMesh);

                        // Add vertex points
                        const pointsGeometry = new THREE.BufferGeometry();
                        pointsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
                        const pointsMaterial = new THREE.PointsMaterial({ color: 0x000000, size: 3 });
                        const points = new THREE.Points(pointsGeometry, pointsMaterial);
                        points.position.copy(pos);
                        if ((i + j) % 2 === 1) {
                            points.rotation.z = Math.PI;
                        }
                        mainGroup.add(points);
                    }
                }
            }

            // Add reference sphere if enabled
            if (showSphere) {
                const sphereGeometry = new THREE.SphereGeometry(0.3, 32, 32);
                const sphereMaterial = new THREE.MeshPhongMaterial({
                    color: 0xdddddd,
                    shininess: 80
                });
                const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphere.position.y = -0.866;
                mainGroup.add(sphere);
            }
        }

        // Handle window resize
        function onWindowResize() {
            const canvas = document.getElementById('main-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Event listeners
        document.querySelectorAll('input[name="unitsX"], input[name="unitsY"]').forEach(radio => {
            radio.addEventListener('change', updateScene);
        });

        document.getElementById('slider-trim').addEventListener('input', (e) => {
            document.getElementById('label-trim-value').textContent = parseFloat(e.target.value).toFixed(3);
            updateScene();
        });

        document.getElementById('checkbox-double').addEventListener('change', updateScene);
        document.getElementById('checkbox-sphere').addEventListener('change', updateScene);
        document.getElementById('checkbox-triangles').addEventListener('change', updateScene);

        document.getElementById('btn-reset-view').addEventListener('click', () => {
            controls.reset();
        });

        // Initialize and start
        init();
        animate();
    </script>
</body>
</html>
