<body>
    <div id="main-container">
        <button id="info-button">+</button>
        <div id="description-panel" style="display: none;">
            <p>This demo visualizes time encoding of analog signals. The blue curve shows the selected analog signal, while the red visual shows its encoded representation using either ASDM (Asynchronous Sigma-Delta Modulator) or IAF (Integrate-and-Fire) methods. Adjust the time window and signal parameters using the controls above.</p>
        </div>

        <div id="control-panel">
            <div class="control-row">
                <label for="slider-start-time">start time</label>
                <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
                <span id="start-time-value">-1.0</span>
            </div>
            <div class="control-row">
                <label for="slider-total-time">total time</label>
                <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
                <span id="total-time-value">2.2</span>
            </div>
            <div class="control-row">
                <label>function</label>
                <div class="button-group" id="function-buttons">
                    <button id="btn-f">f</button>
                    <button id="btn-g" class="active">g</button>
                    <button id="btn-sin">sin</button>
                    <button id="btn-cos">cos</button>
                    <button id="btn-tan">tan</button>
                </div>
            </div>
            <div class="control-row">
                <label>encoding method</label>
                <div class="button-group" id="encoding-buttons">
                    <button id="btn-asdm" class="active">ASDM</button>
                    <button id="btn-iaf">IAF</button>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // State variables
        let startTime = -1.0;
        let totalTime = 2.2;
        let selectedFunction = 'g';
        let encodingMethod = 'ASDM';

        // Get DOM elements
        const infoButton = document.getElementById('info-button');
        const descriptionPanel = document.getElementById('description-panel');
        const sliderStartTime = document.getElementById('slider-start-time');
        const sliderTotalTime = document.getElementById('slider-total-time');
        const startTimeValue = document.getElementById('start-time-value');
        const totalTimeValue = document.getElementById('total-time-value');
        const functionButtons = document.querySelectorAll('#function-buttons button');
        const encodingButtons = document.querySelectorAll('#encoding-buttons button');

        // Info button toggle
        infoButton.addEventListener('click', () => {
            descriptionPanel.style.display = 
                descriptionPanel.style.display === 'none' ? 'block' : 'none';
        });

        // Slider event listeners
        sliderStartTime.addEventListener('input', (e) => {
            startTime = parseFloat(e.target.value);
            startTimeValue.textContent = startTime.toFixed(1);
            if (mySketch) mySketch.redraw();
        });

        sliderTotalTime.addEventListener('input', (e) => {
            totalTime = parseFloat(e.target.value);
            totalTimeValue.textContent = totalTime.toFixed(1);
            if (mySketch) mySketch.redraw();
        });

        // Function button event listeners
        functionButtons.forEach(btn => {
            btn.addEventListener('click', (e) => {
                functionButtons.forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                selectedFunction = btn.id.replace('btn-', '');
                if (mySketch) mySketch.redraw();
            });
        });

        // Encoding button event listeners
        encodingButtons.forEach(btn => {
            btn.addEventListener('click', (e) => {
                encodingButtons.forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                encodingMethod = btn.id.replace('btn-', '');
                if (mySketch) mySketch.redraw();
            });
        });

        // Signal functions
        const signalFunctions = {
            f: (t) => 0.5 * t,
            g: (t) => Math.sin(2 * Math.PI * t) + 0.3 * Math.cos(3 * 2 * Math.PI * t),
            sin: (t) => Math.sin(2 * Math.PI * t),
            cos: (t) => Math.cos(2 * Math.PI * t),
            tan: (t) => Math.tan(Math.PI * t)
        };

        // p5.js sketch
        let mySketch = function(p) {
            let canvas;
            const margin = 60;
            const yMin = -1.2;
            const yMax = 1.2;

            p.setup = function() {
                const container = document.getElementById('canvas-container');
                canvas = p.createCanvas(container.clientWidth, 600);
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                // Calculate drawing area
                const drawWidth = p.width - 2 * margin;
                const drawHeight = p.height - 2 * margin;

                // Draw grid and axes
                drawAxes();

                // Map functions for coordinate transformation
                const mapX = (x) => p.map(x, startTime, startTime + totalTime, margin, p.width - margin);
                const mapY = (y) => p.map(y, yMax, yMin, margin, p.height - margin);

                // Draw analog signal
                drawAnalogSignal(mapX, mapY);

                // Draw encoded signal
                if (encodingMethod === 'ASDM') {
                    drawASDMSignal(mapX, mapY);
                } else {
                    drawIAFSignal(mapX, mapY);
                }
            };

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(2);

                const x0 = p.map(0, startTime, startTime + totalTime, margin, p.width - margin);
                const y0 = p.map(0, yMax, yMin, margin, p.height - margin);

                // X-axis
                p.line(margin, y0, p.width - margin, y0);

                // Y-axis
                p.line(x0, margin, x0, p.height - margin);

                // X-axis ticks and labels
                p.strokeWeight(1);
                p.textAlign(p.CENTER, p.TOP);
                for (let t = Math.ceil(startTime); t <= startTime + totalTime; t++) {
                    if (t % 0.5 === 0) {
                        const x = p.map(t, startTime, startTime + totalTime, margin, p.width - margin);
                        p.line(x, y0 - 5, x, y0 + 5);
                        p.text(t.toFixed(1), x, y0 + 10);
                    }
                }

                // Y-axis ticks and labels
                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = -1; y <= 1; y += 0.5) {
                    const yPix = p.map(y, yMax, yMin, margin, p.height - margin);
                    p.line(x0 - 5, yPix, x0 + 5, yPix);
                    p.text(y.toFixed(1), x0 - 10, yPix);
                }

                // Axis labels
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text('time', p.width / 2, p.height - 10);
                p.push();
                p.translate(20, p.height / 2);
                p.rotate(-Math.PI / 2);
                p.text('amplitude', 0, 0);
                p.pop();
            }

            function drawAnalogSignal(mapX, mapY) {
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                let prevY = null;

                for (let t = startTime; t <= startTime + totalTime; t += 0.001) {
                    const y = signalFunctions[selectedFunction](t);

                    // Handle discontinuities for tan function
                    if (selectedFunction === 'tan' && Math.abs(y) > 10) {
                        prevY = null;
                        p.endShape();
                        p.beginShape();
                        continue;
                    }

                    if (prevY !== null && Math.abs(y - prevY) > 2 && selectedFunction === 'tan') {
                        p.endShape();
                        p.beginShape();
                    }

                    p.vertex(mapX(t), mapY(y));
                    prevY = y;
                }

                p.endShape();
            }

            function drawASDMSignal(mapX, mapY) {
                const B = 0.25;
                const delta = 0.05;
                let currentTime = startTime;
                let outputLevel = B;
                let integral = 0;
                const flipTimes = [];

                // Simulate ASDM
                const dt = 0.0001;
                while (currentTime < startTime + totalTime) {
                    const analogValue = signalFunctions[selectedFunction](currentTime);
                    integral += (analogValue - outputLevel) * dt;

                    if ((outputLevel === B && integral < -delta) || 
                        (outputLevel === -B && integral > delta)) {
                        flipTimes.push({ time: currentTime, level: outputLevel });
                        outputLevel = -outputLevel;
                        integral = 0;
                    }

                    currentTime += dt;
                }

                // Draw square wave
                p.stroke(255, 0, 0);
                p.strokeWeight(2);

                let currentLevel = flipTimes.length > 0 ? -flipTimes[0].level : B;

                for (let i = 0; i < flipTimes.length; i++) {
                    const x1 = i === 0 ? mapX(startTime) : mapX(flipTimes[i].time);
                    const y1 = mapY(currentLevel);
                    const x2 = mapX(flipTimes[i].time);
                    currentLevel = flipTimes[i].level;
                    const y2 = mapY(currentLevel);

                    // Vertical line
                    p.line(x1, y1, x2, y1);
                    // Horizontal line (will be drawn in next iteration or at end)

                    if (i === flipTimes.length - 1) {
                        p.line(x2, y2, mapX(startTime + totalTime), y2);
                    }
                }

                if (flipTimes.length === 0) {
                    p.line(mapX(startTime), mapY(B), mapX(startTime + totalTime), mapY(B));
                }
            }

            function drawIAFSignal(mapX, mapY) {
                const theta = 0.05;
                const ySpike = 0.3;
                let currentTime = startTime;
                let integral = 0;
                const spikeTimes = [];

                // Simulate IAF
                const dt = 0.0001;
                while (currentTime < startTime + totalTime) {
                    const analogValue = signalFunctions[selectedFunction](currentTime);
                    integral += analogValue * dt;

                    if (integral >= theta) {
                        spikeTimes.push(currentTime);
                        integral = 0;
                    }

                    currentTime += dt;
                }

                // Draw spikes
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.fill(255, 0, 0);

                for (const spikeTime of spikeTimes) {
                    const x = mapX(spikeTime);
                    const y0 = mapY(0);
                    const yS = mapY(ySpike);

                    p.line(x, y0, x, yS);
                    p.circle(x, yS, 6);
                }
            }

            p.windowResized = function() {
                const container = document.getElementById('canvas-container');
                p.resizeCanvas(container.clientWidth, 600);
            };
        };

        // Create the p5 instance
        const mySketch = new p5(mySketch);
    </script>
</body>
</html>
