    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Global variables
        let scene, renderer, perspectiveCamera, orthographicCamera, activeCamera;
        let gridMesh, wireframe;
        let isPlaying = false;
        let time = 0;
        let animationId;

        // Wave parameters
        let pWaveType = 'sine';
        let pWaveAmplitude = 0.1;
        let pWaveWavelength = 2.0;
        let sWaveType = 'sine';
        let sWaveAmplitude = 0.1;
        let sWaveWavelength = 2.5;
        let sWaveDirection = 'xy';

        // Initialize the scene
        function init() {
            // Create scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xe0e0e0);

            // Create renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(
                document.getElementById('visualization-container').clientWidth,
                document.getElementById('visualization-container').clientHeight
            );
            renderer.setPixelRatio(window.devicePixelRatio);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Create cameras
            const aspectRatio = document.getElementById('visualization-container').clientWidth / 
                               document.getElementById('visualization-container').clientHeight;

            perspectiveCamera = new THREE.PerspectiveCamera(45, aspectRatio, 0.1, 1000);
            perspectiveCamera.position.set(10, 10, 10);
            perspectiveCamera.lookAt(0, 0, 0);

            const frustumSize = 15;
            orthographicCamera = new THREE.OrthographicCamera(
                frustumSize * aspectRatio / -2, 
                frustumSize * aspectRatio / 2, 
                frustumSize / 2, 
                frustumSize / -2, 
                0.1, 
                1000
            );
            orthographicCamera.position.set(0, 0, 15);
            orthographicCamera.lookAt(0, 0, 0);

            activeCamera = orthographicCamera;

            // Add lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(10, 20, 15);
            scene.add(directionalLight);

            // Create grid
            createGrid(40);

            // Start animation loop
            animate();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function createGrid(segments) {
            // Remove existing grid if it exists
            if (gridMesh) {
                scene.remove(gridMesh);
                scene.remove(wireframe);
                gridMesh.geometry.dispose();
                gridMesh.material.dispose();
                wireframe.geometry.dispose();
                wireframe.material.dispose();
            }

            // Create geometry
            const geometry = new THREE.PlaneGeometry(10, 10, segments, segments);

            // Create material for vertices
            const material = new THREE.MeshLambertMaterial({
                vertexColors: true,
                side: THREE.DoubleSide
            });

            // Create mesh
            gridMesh = new THREE.Mesh(geometry, material);
            gridMesh.rotation.x = -Math.PI / 2; // Rotate to horizontal plane
            scene.add(gridMesh);

            // Create wireframe
            const wireframeGeometry = new THREE.WireframeGeometry(geometry);
            const wireframeMaterial = new THREE.LineBasicMaterial({ color: 0x556b2f });
            wireframe = new THREE.LineSegments(wireframeGeometry, wireframeMaterial);
            wireframe.rotation.x = -Math.PI / 2;
            scene.add(wireframe);

            // Initialize vertex colors
            const colors = [];
            const positionAttribute = geometry.getAttribute('position');

            for (let i = 0; i < positionAttribute.count; i++) {
                colors.push(1, 1, 1); // Start with white
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
        }

        function updateGrid() {
            const positionAttribute = gridMesh.geometry.getAttribute('position');
            const colorAttribute = gridMesh.geometry.getAttribute('color');
            const positions = positionAttribute.array;
            const colors = colorAttribute.array;

            for (let i = 0; i < positionAttribute.count; i++) {
                const x = positions[i * 3];
                const y = positions[i * 3];
                const initialZ = 0;

                // Calculate distance from origin
                const r = Math.sqrt(x * x + y * y);

                // Skip calculation for points at the origin to avoid division by zero
                let displaceX = 0, displaceY = 0, displaceZ = 0;

                if (r > 0.01) {
                    // Calculate P-wave displacement (longitudinal)
                    const radialUnitX = x / r;
                    const radialUnitY = y / r;

                    const pWavePhase = (r / pWaveWavelength) - time;
                    const pWaveMagnitude = pWaveAmplitude * waveFunction(pWavePhase, pWaveType);

                    displaceX += radialUnitX * pWaveMagnitude;
                    displaceY += radialUnitY * pWaveMagnitude;

                    // Calculate S-wave displacement (transverse)
                    const sWavePhase = (r / sWaveWavelength) - time;
                    const sWaveMagnitude = sWaveAmplitude * waveFunction(sWavePhase, sWaveType);

                    if (sWaveDirection === 'z') {
                        displaceZ += sWaveMagnitude;
                    } else {
                        // Direction perpendicular to radial in x-y plane
                        const perpUnitX = -y / r;
                        const perpUnitY = x / r;
                        displaceX += perpUnitX * sWaveMagnitude;
                        displaceY += perpUnitY * sWaveMagnitude;
                    }
                }

                // Update position
                positions[i * 3] = x;
                positions[i * 3 + 1] = y;
                positions[i * 3 + 2] = initialZ + displaceZ;

                // Update color based on z displacement
                const normalizedZ = (displaceZ + 0.5) / 1.0; // Normalize between 0 and 1

                if (normalizedZ > 0.5) {
                    // Positive displacement -> Red gradient
                    const redIntensity = (normalizedZ - 0.5) * 2;
                    colors[i * 3] = 1;
                    colors[i * 3 + 1] = 1 - redIntensity;
                    colors[i * 3 + 2] = 1 - redIntensity;
                } else {
                    // Negative displacement -> Blue gradient
                    const blueIntensity = (0.5 - normalizedZ) * 2;
                    colors[i * 3] = 1 - blueIntensity;
                    colors[i * 3 + 1] = 1 - blueIntensity;
                    colors[i * 3 + 2] = 1;
                }
            }

            positionAttribute.needsUpdate = true;
            colorAttribute.needsUpdate = true;
            gridMesh.geometry.computeVertexNormals();

            // Update wireframe positions
            const wireframeAttribute = wireframe.geometry.getAttribute('position');
            const wireframePositions = wireframeAttribute.array;

            for (let i = 0; i < wireframeAttribute.count; i++) {
                // Copy positions from grid mesh
                wireframePositions[i * 3] = positions[i * 3];
                wireframePositions[i * 3 + 1] = positions[i * 3 + 1];
                wireframePositions[i * 3 + 2] = positions[i * 3 + 2];
            }

            wireframeAttribute.needsUpdate = true;
        }

        function waveFunction(phase, type) {
            if (type === 'sine') {
                return Math.sin(2 * Math.PI * phase);
            } else { // Gaussian pulse
                return Math.exp(-Math.pow(phase, 2));
            }
        }

        function animate() {
            animationId = requestAnimationFrame(animate);

            if (isPlaying) {
                time += 0.05; // Fixed time increment per frame
                if (time > 20) time = 0; // Loop time
                document.getElementById('time-slider').value = time;
            }

            updateGrid();
            renderer.render(scene, activeCamera);
        }

        function onWindowResize() {
            const width = document.getElementById('visualization-container').clientWidth;
            const height = document.getElementById('visualization-container').clientHeight;

            renderer.setSize(width, height);

            // Update camera aspect ratios
            const aspectRatio = width / height;

            perspectiveCamera.aspect = aspectRatio;
            perspectiveCamera.updateProjectionMatrix();

            const frustumSize = 15;
            orthographicCamera.left = frustumSize * aspectRatio / -2;
            orthographicCamera.right = frustumSize * aspectRatio / 2;
            orthographicCamera.top = frustumSize / 2;
            orthographicCamera.bottom = frustumSize / -2;
            orthographicCamera.updateProjectionMatrix();
        }

        // UI Event Handlers
        document.getElementById('play-pause-button').addEventListener('click', () => {
            isPlaying = !isPlaying;
            document.getElementById('play-pause-button').textContent = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('reset-button').addEventListener('click', () => {
            time = 0;
            isPlaying = false;
            document.getElementById('time-slider').value = 0;
            document.getElementById('play-pause-button').textContent = '►';
            updateGrid();
        });

        document.getElementById('step-button').addEventListener('click', () => {
            isPlaying = false;
            document.getElementById('play-pause-button').textContent = '►';
            time += 0.1;
            if (time > 20) time = 0;
            document.getElementById('time-slider').value = time;
            updateGrid();
        });

        document.getElementById('time-slider').addEventListener('input', (e) => {
            time = parseFloat(e.target.value);
            updateGrid();
        });

        document.getElementById('plot-points-slider').addEventListener('input', (e) => {
            createGrid(parseInt(e.target.value));
        });

        // P-wave controls
        document.getElementById('p-wave-sine-button').addEventListener('click', () => {
            pWaveType = 'sine';
            document.getElementById('p-wave-sine-button').classList.add('active');
            document.getElementById('p-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('p-wave-pulse-button').addEventListener('click', () => {
            pWaveType = 'pulse';
            document.getElementById('p-wave-pulse-button').classList.add('active');
            document.getElementById('p-wave-sine-button').classList.remove('active');
        });

        document.getElementById('p-wave-amplitude-slider').addEventListener('input', (e) => {
            pWaveAmplitude = parseFloat(e.target.value);
        });

        document.getElementById('p-wave-wavelength-slider').addEventListener('input', (e) => {
            pWaveWavelength = parseFloat(e.target.value);
        });

        // S-wave controls
        document.getElementById('s-wave-sine-button').addEventListener('click', () => {
            sWaveType = 'sine';
            document.getElementById('s-wave-sine-button').classList.add('active');
            document.getElementById('s-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('s-wave-pulse-button').addEventListener('click', () => {
            sWaveType = 'pulse';
            document.getElementById('s-wave-pulse-button').classList.add('active');
            document.getElementById('s-wave-sine-button').classList.remove('active');
        });

        document.getElementById('s-wave-amplitude-slider').addEventListener('input', (e) => {
            sWaveAmplitude = parseFloat(e.target.value);
        });

        document.getElementById('s-wave-wavelength-slider').addEventListener('input', (e) => {
            sWaveWavelength = parseFloat(e.target.value);
        });

        document.getElementById('s-wave-dir-xy-button').addEventListener('click', () => {
            sWaveDirection = 'xy';
            document.getElementById('s-wave-dir-xy-button').classList.add('active');
            document.getElementById('s-wave-dir-z-button').classList.remove('active');
        });

        document.getElementById('s-wave-dir-z-button').addEventListener('click', () => {
            sWaveDirection = 'z';
            document.getElementById('s-wave-dir-z-button').classList.add('active');
            document.getElementById('s-wave-dir-xy-button').classList.remove('active');
        });

        // Viewpoint controls
        document.getElementById('viewpoint-top-button').addEventListener('click', () => {
            activeCamera = orthographicCamera;
            document.getElementById('viewpoint-top-button').classList.add('active');
            document.getElementById('viewpoint-default-button').classList.remove('active');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', () => {
            activeCamera = perspectiveCamera;
            document.getElementById('viewpoint-default-button').classList.add('active');
            document.getElementById('viewpoint-top-button').classList.remove('active');
        });

        // Initialize the application
        init();
    </script>
</body>
</html>
