    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // State variables
        let a = 1, b = -1, c = -1;
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;
        let p5Canvas;

        // Get DOM elements
        const sliderZ = document.getElementById('slider-z');
        const labelZ = document.getElementById('label-z');
        const sliderPlotRange = document.getElementById('slider-plot-range');
        const labelPlotRange = document.getElementById('label-plot-range');
        const checkShowLabels = document.getElementById('check-show-labels');
        const checkShowCircle = document.getElementById('check-show-circle');
        const checkShowAxes = document.getElementById('check-show-axes');
        const checkShowGridLines = document.getElementById('check-show-grid-lines');
        const btnNewExample = document.getElementById('btn-new-example');
        const btnGoldenRatio = document.getElementById('btn-golden-ratio');
        const formulaText = document.getElementById('formula-text');
        const valuePz = document.getElementById('value-pz');
        const valueL3l2 = document.getElementById('value-l3l2');
        const valueZ = document.getElementById('value-z');

        // Calculate points
        function calculatePoints() {
            const O = { x: 0, y: 0 };
            const L1 = { x: a, y: 0 };
            const L2 = { x: a, y: b };
            const L3 = { x: a - c, y: b };
            const L1_prime = { x: a, y: -z * a };
            const L2_prime = { x: a + b * z + a * z * z, y: b };

            return { O, L1, L2, L3, L1_prime, L2_prime };
        }

        // Calculate P(z)
        function calculatePz() {
            return a * z * z + b * z + c;
        }

        // Calculate distance |L3 L2'|
        function calculateL3L2Distance(points) {
            const dx = points.L2_prime.x - points.L3.x;
            const dy = points.L2_prime.y - points.L3.y;
            return Math.sqrt(dx * dx + dy * dy);
        }

        // Generate polynomial formula string
        function generateFormulaString() {
            let formula = '';

            // a term
            if (a !== 0) {
                if (a === -1) formula += '-';
                else if (a !== 1) formula += a;
                formula += 'z^2';
            }

            // b term
            if (b !== 0) {
                if (formula && b > 0) formula += ' + ';
                else if (b < 0) formula += ' - ';

                const absB = Math.abs(b);
                if (absB !== 1) formula += absB;
                formula += 'z';
            }

            // c term
            if (c !== 0) {
                if (formula && c > 0) formula += ' + ';
                else if (c < 0) formula += ' - ';
                formula += Math.abs(c);
            }

            return formula || '0';
        }

        // Update display
        function updateDisplay() {
            const points = calculatePoints();
            const pz = calculatePz();
            const l3l2Distance = calculateL3L2Distance(points);

            // Update table values
            valuePz.textContent = pz.toFixed(3);
            valueL3l2.textContent = l3l2Distance.toFixed(3);
            valueZ.textContent = z.toFixed(2);

            // Update formula
            formulaText.textContent = generateFormulaString();

            // Re-render MathJax
            if (window.MathJax) {
                MathJax.typesetPromise([document.getElementById('formula-display')]);
            }

            // Redraw canvas
            if (p5Canvas) {
                p5Canvas.redraw();
            }
        }

        // p5.js sketch
        const sketch = (p) => {
            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, 400);
                canvas.parent('canvas-container');
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                const points = calculatePoints();
                const centerX = p.width / 2;
                const centerY = p.height / 2;
                const scale = p.width / (2 * plotRange);

                p.push();
                p.translate(centerX, centerY);
                p.scale(scale, -scale); // Flip Y-axis for Cartesian coordinates

                // Draw grid lines
                if (showGridLines) {
                    p.stroke(230);
                    p.strokeWeight(1 / scale);
                    const gridSpacing = Math.pow(10, Math.floor(Math.log10(plotRange)) - 1);

                    for (let i = -Math.ceil(plotRange / gridSpacing); i <= Math.ceil(plotRange / gridSpacing); i++) {
                        const pos = i * gridSpacing;
                        p.line(pos, -plotRange, pos, plotRange);
                        p.line(-plotRange, pos, plotRange, pos);
                    }
                }

                // Draw axes
                if (showAxes) {
                    p.stroke(0);
                    p.strokeWeight(2 / scale);
                    p.line(-plotRange, 0, plotRange, 0);
                    p.line(0, -plotRange, 0, plotRange);

                    // Draw axis labels
                    if (showLabels) {
                        p.push();
                        p.scale(1, -1);
                        p.textAlign(CENTER, CENTER);
                        p.fill(0);
                        p.textSize(12 / scale);
                        for (let i = -Math.floor(plotRange); i <= Math.floor(plotRange); i++) {
                            if (i !== 0) {
                                p.text(i.toString(), i, -0.2);
                                p.text(i.toString(), 0.2, i);
                            }
                        }
                        p.pop();
                    }
                }

                // Draw coefficient path
                p.stroke(0);
                p.strokeWeight(2 / scale);
                p.line(points.O.x, points.O.y, points.L1.x, points.L1.y);
                p.line(points.L1.x, points.L1.y, points.L2.x, points.L2.y);
                p.line(points.L2.x, points.L2.y, points.L3.x, points.L3.y);

                // Draw points
                p.fill(0);
                p.noStroke();
                p.circle(points.O.x, points.O.y, 6 / scale);
                p.circle(points.L1.x, points.L1.y, 6 / scale);
                p.circle(points.L2.x, points.L2.y, 6 / scale);
                p.circle(points.L3.x, points.L3.y, 6 / scale);

                // Draw test path
                p.stroke(0);
                p.strokeWeight(2 / scale);
                p.line(points.O.x, points.O.y, points.L1_prime.x, points.L1_prime.y);

                p.stroke(255, 0, 255);
                p.line(points.L1_prime.x, points.L1_prime.y, points.L2_prime.x, points.L2_prime.y);

                p.stroke(255, 0, 0);
                p.line(points.L2_prime.x, points.L2_prime.y, points.L3.x, points.L3.y);

                // Draw test points
                p.fill(0);
                p.noStroke();
                p.circle(points.L1_prime.x, points.L1_prime.y, 6 / scale);
                p.circle(points.L2_prime.x, points.L2_prime.y, 6 / scale);

                // Draw circle
                if (showCircle) {
                    const centerC = { x: (a - c) / 2, y: b / 2 };
                    const diameter = Math.sqrt((a - c) * (a - c) + b * b);
                    p.noFill();
                    p.stroke(0);
                    p.strokeWeight(2 / scale);
                    p.circle(centerC.x, centerC.y, diameter);
                }

                // Draw labels
                if (showLabels) {
                    p.push();
                    p.scale(1, -1);
                    p.fill(0);
                    p.noStroke();
                    p.textSize(14 / scale);
                    p.text('O', points.O.x + 0.1, points.O.y - 0.1);
                    p.text('L1', points.L1.x + 0.1, points.L1.y - 0.1);
                    p.text('L2', points.L2.x + 0.1, points.L2.y - 0.1);
                    p.text('L3', points.L3.x + 0.1, points.L3.y - 0.1);
                    p.text("L1'", points.L1_prime.x + 0.1, points.L1_prime.y - 0.1);
                    p.text("L2'", points.L2_prime.x + 0.1, points.L2_prime.y - 0.1);
                    p.pop();
                }

                p.pop();
            };

            p.windowResized = () => {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.offsetWidth, 400);
            };
        };

        // Event listeners
        sliderZ.addEventListener('input', (e) => {
            z = parseFloat(e.target.value);
            labelZ.textContent = z.toFixed(2);
            updateDisplay();
        });

        sliderPlotRange.addEventListener('input', (e) => {
            plotRange = parseFloat(e.target.value);
            labelPlotRange.textContent = plotRange.toString();
            updateDisplay();
        });

        checkShowLabels.addEventListener('change', (e) => {
            showLabels = e.target.checked;
            updateDisplay();
        });

        checkShowCircle.addEventListener('change', (e) => {
            showCircle = e.target.checked;
            updateDisplay();
        });

        checkShowAxes.addEventListener('change', (e) => {
            showAxes = e.target.checked;
            updateDisplay();
        });

        checkShowGridLines.addEventListener('change', (e) => {
            showGridLines = e.target.checked;
            updateDisplay();
        });

        btnNewExample.addEventListener('click', () => {
            // Generate new random coefficients
            const aValues = [-3, -2, -1, 1, 2, 3];
            a = aValues[Math.floor(Math.random() * aValues.length)];
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;

            updateDisplay();
        });

        btnGoldenRatio.addEventListener('click', () => {
            a = 1;
            b = -1;
            c = -1;
            updateDisplay();
        });

        // Initialize
        window.addEventListener('load', () => {
            p5Canvas = new p5(sketch);
            updateDisplay();
        });
    </script>
</body>
