<body>
    <div id="app-container">

        <div id="header-controls">
            <div id="view-switcher">
                <button id="btn-plot" class="active">T₂(α)</button>
                <button id="btn-table">solution listbutton>
                <button id="btn-turning-points">turning pointsbutton>
            </div>
            <div id="info-button-container">
                <button id="btn-info">+</button>
            </div>
        </div>

        <div id="parameter-controls">
            <label for="slider-alpha">αlabel>
            <input type="range" id="slider-alpha" min="0.00" max="0.07" step="0.0001" value="0.039">
            <span id="alpha-value-display">0.039span>
        </div>

        <div id="main-display">
            <div id="plot-view"></div>
            <div id="table-view" style="display: none;">
                <table id="solutions-table">
                    <thead>
                        <tr>
                            <th>root>
                            <th>X₁>
                            <th>X₂>
                            <th>T₁>
                            <th>T₂>
                        </tr>
                    </thead>
                    <tbody id="solutions-table-body">
                    </tbody>
                </table>
            </div>
        </div>

    </div>

    <script>
        // Sample data for the solution curve
        const solutionCurveData = [
            {alpha: 0.0000, x1: 0.0000, x2: 0.0000, t1: 0.0000, t2: 0.0000},
            {alpha: 0.0010, x1: 0.0010, x2: 0.0020, t1: 0.1000, t2: 0.2000},
            {alpha: 0.0020, x1: 0.0020, x2: 0.0040, t1: 0.2000, t2: 0.4000},
            {alpha: 0.0050, x1: 0.0050, x2: 0.0100, t1: 0.5000, t2: 1.0000},
            {alpha: 0.0100, x1: 0.0100, x2: 0.0200, t1: 1.0000, t2: 2.0000},
            {alpha: 0.0150, x1: 0.0150, x2: 0.0300, t1: 1.5000, t2: 3.0000},
            {alpha: 0.0200, x1: 0.0200, x2: 0.0400, t1: 2.0000, t2: 4.0000},
            {alpha: 0.0250, x1: 0.0250, x2: 0.0500, t1: 2.5000, t2: 5.0000},
            {alpha: 0.0300, x1: 0.0300, x2: 0.0600, t1: 3.0000, t2: 6.0000},
            {alpha: 0.0350, x1: 0.0350, x2: 0.0700, t1: 3.5000, t2: 7.0000},
            {alpha: 0.0390, x1: 0.0390, x2: 0.0780, t1: 3.9000, t2: 7.8000},
            {alpha: 0.0400, x1: 0.0400, x2: 0.0800, t1: 4.0000, t2: 8.0000},
            {alpha: 0.0450, x1: 0.0450, x2: 0.0900, t1: 4.5000, t2: 8.0000},
            {alpha: 0.0500, x1: 0.0500, x2: 0.1000, t1: 5.0000, t2: 8.0000},
            {alpha: 0.0550, x1: 0.0550, x2: 0.1100, t1: 5.5000, t2: 8.0000},
            {alpha: 0.0600, x1: 0.0600, x2: 0.1200, t1: 6.0000, t2: 8.0000},
            {alpha: 0.0650, x1: 0.0650, x2: 0.1300, t1: 6.5000, t2: 8.0000},
            {alpha: 0.0700, x1: 0.0700, x2: 0.1400, t1: 7.0000, t2: 8.0000}
        ];

        // Sample turning points data
        const turningPointsData = [
            {alpha: 0.0050, t2: 1.0000},
            {alpha: 0.0100, t2: 2.0000},
            {alpha: 0.0200, t2: 4.0000},
            {alpha: 0.0300, t2: 6.0000},
            {alpha: 0.0400, t2: 8.0000},
            {alpha: 0.0500, t2: 8.0000}
        ];

        // Global variables
        let currentView = 'plot_solutions';
        let currentAlpha = 0.039;
        let p5Instance;

        // DOM elements
        const btnPlot = document.getElementById('btn-plot');
        const btnTable = document.getElementById('btn-table');
        const btnTurningPoints = document.getElementById('btn-turning-points');
        const sliderAlpha = document.getElementById('slider-alpha');
        const alphaValueDisplay = document.getElementById('alpha-value-display');
        const plotView = document.getElementById('plot-view');
        const tableView = document.getElementById('table-view');
        const parameterControls = document.getElementById('parameter-controls');
        const solutionsTableBody = document.getElementById('solutions-table-body');

        // View switching
        function switchView(view) {
            currentView = view;

            // Update button states
            [btnPlot, btnTable, btnTurningPoints].forEach(btn => {
                btn.classList.remove('active');
            });

            // Update visibility
            if (view === 'plot_solutions') {
                btnPlot.classList.add('active');
                plotView.style.display = 'flex';
                tableView.style.display = 'none';
                parameterControls.style.display = 'flex';
            } else if (view === 'table') {
                btnTable.classList.add('active');
                plotView.style.display = 'none';
                tableView.style.display = 'block';
                parameterControls.style.display = 'flex';
                updateTable();
            } else if (view === 'plot_turning_points') {
                btnTurningPoints.classList.add('active');
                plotView.style.display = 'flex';
                tableView.style.display = 'none';
                parameterControls.style.display = 'none';
            }

            if (p5Instance) {
                p5Instance.redraw();
            }
        }

        // Event listeners for view buttons
        btnPlot.addEventListener('click', () => switchView('plot_solutions'));
        btnTable.addEventListener('click', () => switchView('table'));
        btnTurningPoints.addEventListener('click', () => switchView('plot_turning_points'));

        // Slider event listener
        sliderAlpha.addEventListener('input', (e) => {
            currentAlpha = parseFloat(e.target.value);
            alphaValueDisplay.textContent = currentAlpha.toFixed(4);

            if (currentView === 'plot_solutions' || currentView === 'table') {
                if (currentView === 'table') {
                    updateTable();
                } else if (p5Instance) {
                    p5Instance.redraw();
                }
            }
        });

        // Calculate intersection points for given alpha
        function getIntersections(alpha) {
            const intersections = [];

            for (let i = 0; i < solutionCurveData.length - 1; i++) {
                const p1 = solutionCurveData[i];
                const p2 = solutionCurveData[i + 1];

                if ((p1.alpha <= alpha && p2.alpha >= alpha) || 
                    (p1.alpha >= alpha && p2.alpha <= alpha)) {

                    // Linear interpolation
                    const t = (alpha - p1.alpha) / (p2.alpha - p1.alpha);
                    const x1 = p1.x1 + t * (p2.x1 - p1.x1);
                    const x2 = p1.x2 + t * (p2.x2 - p1.x2);
                    const t1 = p1.t1 + t * (p2.t1 - p1.t1);
                    const t2 = p1.t2 + t * (p2.t2 - p1.t2);

                    intersections.push({
                        root: intersections.length + 1,
                        x1: x1,
                        x2: x2,
                        t1: t1,
                        t2: t2
                    });
                }
            }

            return intersections;
        }

        // Update the solutions table
        function updateTable() {
            const intersections = getIntersections(currentAlpha);

            solutionsTableBody.innerHTML = '';

            intersections.forEach(intersection => {
                const row = document.createElement('tr');
                row.innerHTML = `
                    <td>${intersection.root}</td>
                    <td>${intersection.x1.toFixed(4)}</td>
                    <td>${intersection.x2.toFixed(4)}</td>
                    <td>${intersection.t1.toFixed(4)}</td>
                    <td>${intersection.t2.toFixed(4)}</td>
                `;
                solutionsTableBody.appendChild(row);
            });
        }

        // p5.js sketch
        const sketch = (p) => {
            let canvas;
            const margin = 60;
            const plotWidth = 600 - 2 * margin;
            const plotHeight = 600 - 2 * margin;

            p.setup = () => {
                canvas = p.createCanvas(600, 600);
                canvas.parent('plot-view');
                p.draw();
            };

            p.draw = () => {
                p.background(255);

                // Draw grid and axes
                drawGridAndAxes();

                // Draw solution curve
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let point of solutionCurveData) {
                    const x = p.map(point.alpha, 0, 0.07, margin, margin + plotWidth);
                    const y = p.map(point.t2, 0, 8, margin + plotHeight, margin);
                    p.vertex(x, y);
                }
                p.endShape();

                // View-specific drawings
                if (currentView === 'plot_solutions') {
                    // Draw vertical dashed line at current alpha
                    drawDashedLine();

                    // Draw intersection points
                    const intersections = getIntersections(currentAlpha);
                    p.fill(0, 0, 255);
                    p.noStroke();
                    for (let intersection of intersections) {
                        const x = p.map(currentAlpha, 0, 0.07, margin, margin + plotWidth);
                        const y = p.map(intersection.t2, 0, 8, margin + plotHeight, margin);
                        p.ellipse(x, y, 10, 10);
                    }
                } else if (currentView === 'plot_turning_points') {
                    // Draw turning points
                    p.fill(0, 255, 0);
                    p.noStroke();
                    for (let point of turningPointsData) {
                        const x = p.map(point.alpha, 0, 0.07, margin, margin + plotWidth);
                        const y = p.map(point.t2, 0, 8, margin + plotHeight, margin);
                        p.ellipse(x, y, 10, 10);
                    }
                }
            };

            function drawGridAndAxes() {
                p.stroke(200);
                p.strokeWeight(1);

                // Grid lines
                for (let i = 0; i <= 7; i++) {
                    const y = p.map(i, 0, 8, margin + plotHeight, margin);
                    p.line(margin, y, margin + plotWidth, y);
                }

                for (let i = 0; i <= 7; i++) {
                    const x = p.map(i * 0.01, 0, 0.07, margin, margin + plotWidth);
                    p.line(x, margin, x, margin + plotHeight);
                }

                // Axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(margin, margin + plotHeight, margin + plotWidth, margin + plotHeight);
                p.line(margin, margin, margin, margin + plotHeight);

                // Labels
                p.textAlign(p.CENTER, p.TOP);
                for (let i = 0; i <= 7; i++) {
                    const x = p.map(i * 0.01, 0, 0.07, margin, margin + plotWidth);
                    p.text((i * 0.01).toFixed(2), x, margin + plotHeight + 5);
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= 8; i++) {
                    const y = p.map(i, 0, 8, margin + plotHeight, margin);
                    p.text(i.toString(), margin - 10, y);
                }

                // Axis titles
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(16);
                p.text('α', width / 2, height - 20);
                p.push();
                p.translate(20, height / 2);
                p.rotate(-p.PI / 2);
                p.text('T₂(α)', 0, 0);
                p.pop();
                p.textSize(12);
            }

            function drawDashedLine() {
                p.stroke(0, 255, 0);
                p.strokeWeight(2);
                p.setLineDash([5, 5]);
                const x = p.map(currentAlpha, 0, 0.07, margin, margin + plotWidth);
                p.line(x, margin, x, margin + plotHeight);
                p.setLineDash([]);
            }
        };

        // Initialize p5.js
        p5Instance = new p5(sketch);

        // Initial setup
        alphaValueDisplay.textContent = currentAlpha.toFixed(4);
    </script>
