                    <span id="display-growth">0.1500</span>
                </div>
                <button id="btn-reset-growth">+</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        // State variables
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = { x: 0, y: 0 };
        let z_plus_loc = { x: -100, y: 0 };
        let z_minus_loc = { x: 100, y: 0 };

        // Dragging state
        let dragging = null;
        let dragOffset = { x: 0, y: 0 };

        // Fox vertices
        const fox_vertices = [
            {x: -15, y: 0}, {x: -10, y: -5}, {x: -5, y: -4},
            {x: 0, y: -5}, {x: 5, y: -5}, {x: 8, y: -2},
            {x: 10, y: -6}, {x: 12, y: -6}, {x: 15, y: 0},
            {x: 12, y: 6}, {x: 10, y: 6}, {x: 8, y: 2},
            {x: 5, y: 5}, {x: 0, y: 5}, {x: -5, y: 4},
            {x: -10, y: 5}
        ];

        // p5.js setup
        function setup() {
            let canvas = createCanvas(windowWidth * 0.9, 600);
            canvas.parent('p5-canvas');

            // Initialize button event listeners
            setupButtonListeners();
            setupSliderListeners();
        }

        function draw() {
            background(255);

            // Move origin to center
            translate(width / 2, height / 2);

            // Draw grid (optional, for reference)
            stroke(200);
            strokeWeight(1);
            for (let i = -width/2; i <= width/2; i += 50) {
                line(i, -height/2, i, height/2);
            }
            for (let i = -height/2; i <= height/2; i += 50) {
                line(-width/2, i, width/2, i);
            }

            // Draw spiral based on type
            if (spiralType === 'single') {
                drawSingleSpiral();
                if (spiralType === 'single') {
                    drawLocator(z0_loc, color(255, 255, 0, 200), 'z0');
                }
            } else {
                drawDoubleSpiral();
                // Draw locators only for double spiral
                drawLocator(z_plus_loc, color(255, 255, 0, 200), 'z+');
                drawLocator(z_minus_loc, color(0, 255, 0, 200), 'z-');
                // Draw z0Loc label at midpoint
                let midpoint = {
                    x: (z_plus_loc.x + z_minus_loc.x) / 2,
                    y: (z_plus_loc.y + z_minus_loc.y) / 2
                };
                push();
                fill(0);
                noStroke();
                textAlign(CENTER, CENTER);
                text('z0Loc', midpoint.x, midpoint.y - 30);
                pop();
            }
        }

        function drawSingleSpiral() {
            let k = spiralGrowth;
            let tMin = -8 * PI;
            let tMax = 8 * PI;
            let steps = 500;

            // Draw spiral curve
            stroke('#4A6DA8');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let i = 0; i <= steps; i++) {
                let t = tMin + (tMax - tMin) * i / steps;
                let exp_kt = exp(k * t);
                let x = z0_loc.x + exp_kt * cos(t);
                let y = z0_loc.y - exp_kt * sin(t); // Invert y for canvas
                vertex(x, y);
            }
            endShape();

            // Draw items
            if (itemType !== 'none') {
                drawItemsOnSingleSpiral(k, tMin, tMax);
            }
        }

        function drawDoubleSpiral() {
            let k = spiralGrowth;
            let p = createVector(z_plus_loc.x, z_plus_loc.y);
            let q = createVector(z_minus_loc.x, z_minus_loc.y);
            let epsilon = 0.01;
            let T = 8 * PI;
            let steps = 250;

            // Draw first branch (t from -T to -epsilon)
            stroke('#4A6DA8');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let i = 0; i <= steps; i++) {
                let t = -T + (T - epsilon) * i / steps;
                let w = calculateW(k, t);
                let z = mobiusTransform(w, p, q);
                if (z) {
                    vertex(z.x, z.y);
                }
            }
            endShape();

            // Draw second branch (t from epsilon to T)
            beginShape();
            for (let i = 0; i <= steps; i++) {
                let t = epsilon + (T - epsilon) * i / steps;
                let w = calculateW(k, t);
                let z = mobiusTransform(w, p, q);
                if (z) {
                    vertex(z.x, z.y);
                }
            }
            endShape();

            // Draw items
            if (itemType !== 'none') {
                drawItemsOnDoubleSpiral(k, epsilon, T);
            }
        }

        function calculateW(k, t) {
            let r = exp(k * t);
            let angle = t;
            return createVector(r * cos(angle), r * sin(angle));
        }

        function mobiusTransform(w, p, q) {
            let denom = w.x - 1;
            if (abs(denom) < 0.001) return null; // Avoid division by zero

            let numerator = p5.Vector.sub(p5.Vector.mult(p, w), q);
            return createVector(numerator.x / denom, numerator.y / denom);
        }

        function drawItemsOnSingleSpiral(k, tMin, tMax) {
            let numPoints = 30;
            let spacing = (tMax - tMin) / numPoints;

            for (let i = 0; i <= numPoints; i++) {
                let t = tMin + spacing * i;
                let exp_kt = exp(k * t);
                let x = z0_loc.x + exp_kt * cos(t);
                let y = z0_loc.y - exp_kt * sin(t);

                if (itemType === 'point') {
                    drawPoint(x, y);
                } else {
                    calculateAndDrawFox(createVector(x, y), t, k);
                }
            }
        }

        function drawItemsOnDoubleSpiral(k, epsilon, T) {
            let p = createVector(z_plus_loc.x, z_plus_loc.y);
            let q = createVector(z_minus_loc.x, z_minus_loc.y);
            let numPoints = 15;

            // First branch
            for (let i = 0; i <= numPoints; i++) {
                let t = -T + (T - epsilon) * i / numPoints;
                let w = calculateW(k, t);
                let z = mobiusTransform(w, p, q);
                if (z) {
                    if (itemType === 'point') {
                        drawPoint(z.x, z.y);
                    } else {
                        calculateAndDrawFox(z, t, k);
                    }
                }
            }

            // Second branch
            for (let i = 0; i <= numPoints; i++) {
                let t = epsilon + (T - epsilon) * i / numPoints;
                let w = calculateW(k, t);
                let z = mobiusTransform(w, p, q);
                if (z) {
                    if (itemType === 'point') {
                        drawPoint(z.x, z.y);
                    } else {
                        calculateAndDrawFox(z, t, k);
                    }
                }
            }
        }

        function drawPoint(x, y) {
            fill('#CB3335');
            noStroke();
            circle(x, y, 5);
        }

        function calculateAndDrawFox(position, t, k) {
            // Calculate tangent vector
            let w = calculateW(k, t);
            let derivative = calculateTangent(w, k, t);
            let angle = atan2(derivative.y, derivative.x);

            drawFox(position, angle);
        }

        function calculateTangent(w, k, t) {
            let p = createVector(z_plus_loc.x, z_plus_loc.y);
            let q = createVector(z_minus_loc.x, z_minus_loc.y);

            // Simplify tangent calculation for double spiral
            return createVector(cos(t), sin(t)).mult(exp(k * t));
        }

        function drawFox(position, angle) {
            push();
            translate(position.x, position.y);
            rotate(angle);
            scale(0.5);

            if (itemType === 'fox') {
                noFill();
                stroke('#D6292D');
                strokeWeight(1);
            } else if (itemType === 'filled-fox') {
                fill('#D6292D');
                noStroke();
            }

            beginShape();
            for (let v of fox_vertices) {
                vertex(v.x, v.y);
            }
            endShape(CLOSE);
            pop();
        }

        function drawLocator(pos, col, label) {
            push();
            translate(pos.x, pos.y);

            // Draw circle
            fill(col);
            stroke(0);
            strokeWeight(2);
            circle(0, 0, 20);

            // Draw crosshair
            stroke(0);
            strokeWeight(2);
            line(-10, 0, 10, 0);
            line(0, -10, 0, 10);

            // Draw label
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            text(label, 0, -20);
            pop();
        }

        // Mouse interaction functions
        function mousePressed() {
            let mx = mouseX - width / 2;
            let my = mouseY - height / 2;

            // Check if clicking on a locator
            if (spiralType === 'single') {
                if (dist(mx, my, z0_loc.x, z0_loc.y) < 15) {
                    dragging = 'z0';
                    dragOffset.x = mx - z0_loc.x;
                    dragOffset.y = my - z0_loc.y;
                }
            } else {
                if (dist(mx, my, z_plus_loc.x, z_plus_loc.y) < 15) {
                    dragging = 'z_plus';
                    dragOffset.x = mx - z_plus_loc.x;
                    dragOffset.y = my - z_plus_loc.y;
                } else if (dist(mx, my, z_minus_loc.x, z_minus_loc.y) < 15) {
                    dragging = 'z_minus';
                    dragOffset.x = mx - z_minus_loc.x;
                    dragOffset.y = my - z_minus_loc.y;
                }
            }
        }

        function mouseDragged() {
            if (dragging) {
                let mx = mouseX - width / 2;
                let my = mouseY - height / 2;

                if (dragging === 'z0') {
                    z0_loc.x = mx - dragOffset.x;
                    z0_loc.y = my - dragOffset.y;
                } else if (dragging === 'z_plus') {
                    z_plus_loc.x = mx - dragOffset.x;
                    z_plus_loc.y = my - dragOffset.y;
                } else if (dragging === 'z_minus') {
                    z_minus_loc.x = mx - dragOffset.x;
                    z_minus_loc.y = my - dragOffset.y;
                }
            }
        }

        function mouseReleased() {
            dragging = null;
        }

        // Button and slider setup functions
        function setupButtonListeners() {
            // Type buttons
            document.getElementById('btn-type-single').addEventListener('click', function() {
                spiralType = 'single';
                updateButtonGroup('type', this.id);
            });

            document.getElementById('btn-type-double').addEventListener('click', function() {
                spiralType = 'double';
                updateButtonGroup('type', this.id);
            });

            // Item buttons
            document.getElementById('btn-item-none').addEventListener('click', function() {
                itemType = 'none';
                updateButtonGroup('item', this.id);
            });

            document.getElementById('btn-item-point').addEventListener('click', function() {
                itemType = 'point';
                updateButtonGroup('item', this.id);
            });

            document.getElementById('btn-item-fox').addEventListener('click', function() {
                itemType = 'fox';
                updateButtonGroup('item', this.id);
            });

            document.getElementById('btn-item-filled-fox').addEventListener('click', function() {
                itemType = 'filled-fox';
                updateButtonGroup('item', this.id);
            });

            // Reset button
            document.getElementById('btn-reset-growth').addEventListener('click', function() {
                spiralGrowth = 0.25;
                document.getElementById('slider-growth').value = spiralGrowth;
                document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
            });
        }

        function setupSliderListeners() {
            let slider = document.getElementById('slider-growth');
            let display = document.getElementById('display-growth');

            slider.addEventListener('input', function() {
                spiralGrowth = parseFloat(this.value);
                display.textContent = spiralGrowth.toFixed(4);
            });
        }

        function updateButtonGroup(group, activeId) {
            let buttons = group === 'type' ? 
                ['btn-type-single', 'btn-type-double'] : 
                ['btn-item-none', 'btn-item-point', 'btn-item-fox', 'btn-item-filled-fox'];

            buttons.forEach(id => {
                document.getElementById(id).classList.remove('active');
            });
            document.getElementById(activeId).classList.add('active');
        }
    </script>
</body>
